// this project is a mess !
// it is an attempt to make raymarching work on my laptop with good fps
// it kind of works, but i think i might be interested in simpler ideas

// i used things from this shader by iq :
// https://www.shadertoy.com/view/4d2XWV
// and other things from Jamie Wong's article
// http://jamie-wong.com/2016/07/15/ray-marching-signed-distance-functions
// and also was inspired by this article
// https://learnopengl.com/Advanced-Lighting/Parallax-Mapping

const int raySteps = 8;
const int precisionSteps = 4;

const vec3 lightDir = normalize(vec3(0.5,1.0,0.5));

float iPlane( in vec3 ro, in vec3 rd ) {
    return (-1.0 - ro.y)/rd.y;
}

float dot2(vec3 p) { return dot(p,p);}

float getVal(vec3 p) {    

    //a collection of implicit surfaces.

    //the distances are not exact, therefore hasardous to draw with raymarching !

    //this is why i developed this weird
    //displacement mapping technique.


	//return dot2(p)*1.4164 - max(p.x,max(p.y,p.z)) + min(p.x,min(p.y,p.z));

    //vec3 p2 = p*p.zxy; return max(dot2(p2) - 0.05, dot(p,p)-0.9999);

    vec3 p1 = p*p.yzx; float d = dot(p,p)*0.7; return d*d*d - dot(p1,p1);

    //return .5 - cos(p.x*3.1415) - cos(p.y*3.1415) - cos(p.z*3.1415);

    //float d = dot2(p); return d*d - dot(p,vec3(p.xy,p.z*p.z)) + 0.2;

    //return dot2(p*p) - 0.333333;

    //float d = dot2(p); vec3 p2 = p*p; vec3 c = p2*p2.yzx; return d*d*d - max( c.x, max(c.y, c.z ) )*4.0 - 0.0001;

	//float d = dot2(p); return d*d - dot(abs(p.xyz*p.yzx),vec3(1));

    //float d = dot2(p); return d*d - abs(p.x*p.y*p.z)*5.1 - 0.0001;

	//float d = dot(p,vec3(0.7)); float d2 = d*d; return d2*d2*d2 - dot(p,p.yzx)  - 0.0001;

    // float d = dot(p,p); vec3 c = p*p.yzx; return d*d - max( c.x, max(c.y, c.z ) );

}

//shape + floor, used to compute AO
float scene(vec3 p) {    
    return min(getVal(p),p.y+1.0);
}

vec2 sphIntersect( in vec3 ro, in vec3 rd, in vec4 sph ) {
	vec3 oc = ro - sph.xyz;
	float b = dot( oc, rd );
	float h = b*b - dot( oc, oc ) + sph.w*sph.w;
	if( h<0.0 ) return vec2(-1.0);
    float sh = sqrt( h );
	return vec2(-sh,sh) - b;
}

const float raySteps2 = 1.0/float(raySteps);
float getDist(vec3 ro, vec3 rd, vec4 sph) {

    //do we hit the bounding sphere ?
	vec2 sphBound = sphIntersect( ro, rd, sph );

    if (sphBound.y == -1.0) { return -1.0; }

    sphBound.x = max(0.0,sphBound.x);

    //do we hit the 3D shape ?
    //(we look at the shape at regular intervals)
    vec3 ro2 = (sph.xyz - ro)/sph.w;    
    float raySteps3 = (sphBound.y-sphBound.x)*raySteps2/sph.w;

    vec2 t   = vec2(sphBound.x/sph.w,      0.0);
    vec2 val = vec2(getVal(ro2 - rd*t[0]), 0.0);
    int i = 0;

    while (i<raySteps && val[0]>0.0) {

        t   = vec2(t[0] + raySteps3, t[0]);
        val = vec2(getVal(ro2 - rd*t[0]), val[0]);

        i++;
    }

    //no surface found
    if (i == raySteps && val[0]>0.0) { return -1.0; }

    //now we try to be more precise using linear interpolation
    //between 2 points on the inside/outside of the shape

    for (int j = 0; j<precisionSteps; j++) {

        float newT = mix(t[0],t[1],val[0]/(val[0]-val[1]));

        float newVal = getVal(ro2 - rd*newT);
        int c = int(newVal>0.0);

        t[c]   = newT; 
        val[c] = newVal; 

    }
    return abs(mix(t[0],t[1],val[0]/(val[0]-val[1]))*sph.w); 
}


bool shadowRay(vec3 ro, vec3 rd, vec4 sph) {

	vec2 sphBound = sphIntersect( ro, rd, sph );

    if (sphBound.y < 0.0) { return true; }

    vec3 ro2 = (sph.xyz - ro)/sph.w;    

    sphBound.x = max(0.0,sphBound.x);
    float t1 = sphBound.x/sph.w;
    float raySteps3 = (sphBound.y-sphBound.x)*raySteps2/sph.w;

    int startInside = int(sphBound.x == 0.0);  //getVal(ro2 - rd*t1) < 0.;

    t1 += raySteps3*float(startInside);
    int i = startInside;

    while (i < raySteps && getVal(ro2 - rd*t1) > 0.0 ) {
        t1 += raySteps3;
        i++;
    }

    if (i == raySteps) { return true; }
    return false;


}

vec3 distance_field_normal(vec3 pos, float rad) {
    vec2 eps = vec2(0.0001/rad,0.0);
    pos /= rad;
    float nx = getVal(pos + eps.xyy);
    float ny = getVal(pos + eps.yxy);
    float nz = getVal(pos + eps.yyx);
    return normalize(getVal(pos)-vec3(nx, ny, nz));
}


//https://www.shadertoy.com/view/MtlBWB
const vec3 sq = 1./vec3(sqrt(2.),sqrt(3.),sqrt(4.));
const float eps = 0.125;
const vec3 eps2 = eps*vec3(2.,3.,4.);
float ao2(vec3 p, vec3 n) {

    float c1 = float(abs(n.x) < abs(n.y) && abs(n.x) < abs(n.z));
    float c2 = float(abs(n.y) < abs(n.z));
    float c3 = c2 - c2*c1;

    vec3 t = vec3(c1, c3, 1. - c1 - c3);
    vec3 u = cross(n, t);
    vec3 v = cross(n, u);    

    vec3 epn = eps2[2]*n + p;

    float occ  = max(scene(p + eps*n),0.0);
          occ += max(scene(p + eps*u),0.0);
          occ += max(scene(p - eps*u),0.0);
          occ += max(scene(p + eps*v),0.0);
          occ += max(scene(p - eps*v),0.0);

    occ += (max(scene(epn + eps2.z*u),0.0)
        +   max(scene(epn - eps2.z*u),0.0)
        +   max(scene(epn + eps2.z*v),0.0)
        +   max(scene(epn - eps2.z*v),0.0))*0.5;

    occ += max(scene(p + eps2.x*n)*sq.x,0.0);
    occ += max(scene(p + eps2.y*n)*sq.y,0.0);
    occ += max(scene(epn         )*sq.z,0.0);

    return max(1.0 - 1.0/(1.0+2.0*occ), 0.0);
}



