// 
// You can use this shader as a template for ray marching shaders

#define MAX_STEPS 200
#define MAX_DIST 5.
#define SURF_DIST .0001

#define S smoothstep

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float Hash21(vec2 p) {
    p = fract(p*vec2(123.34,233.53));
    p += dot(p, p+23.234);
    return fract(p.x*p.y);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}






float GetDist(vec3 p) {

    p.xz *= Rot(sin(iTime));

    p.x = abs(p.x);
    p.x += 0.1 * sin(iTime / 1.7);
    //p.x -= 0.1 * sin(iTime);


    p.z = abs(p.z);
    p.z += 0.1 * sin(iTime / 1.4);
    //p.z -= 0.1 * sin(iTime);

    float box = sdBox(p, vec3(iTime));
    //p.y += iTime / 10.;
    p.y += iTime * 1.;


    float hole = 0.;

    float holiness = 3.;//2. + 2. * (0.5 + 0.5 * sin(p.y + iTime));
    float iterations = 3.;
    for (float axes = 0.; axes <= 2.; axes++) {
        p = p.zxy;
	    vec3 pm = p;
   		for (float i = 1.; i <= iterations; i++) {
        	pm.xy = fract((pm.xy * 1. * i + 0.5)) - 0.5;
		    hole = min(hole, sdBox(pm, vec3(1./holiness/i, 1./holiness/i, iTime * 2.)));
	    }
    }

    //hole /= 2. * iterations;
    hole *= 3.9;

    return max(0., -hole);
}






float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}



