    for (float i=0.0;i<divider;i+=1.0) {
        if (i>=hiddenLine && i<hiddenLine+0.5) {
            float startX = pow(start.x+mouseRatio.x*(i-1.0),powValue);
            float colorX1 = startX;
            float colorX2 = pow(start.x+mouseRatio.x*(i+1.0),powValue);
            if (i>0.0) {
                colorX1 += lineW;
            }
            if (i>divider-1.5) {
                colorX2 = 1.0;
            }
            if (st.x>=colorX1 && st.x<=colorX2 && st.y<pow(start.y, powValue)) {
                color=blue;
            }
        }
    }

	fragColor = vec4(color,1.0);
}

// Razor Blossom by Kristian Sivonen (ruojake)
// CC BY-SA 4.0 (https://creativecommons.org/licenses/by-sa/4.0/)

#define sat(x) clamp(x, 0., 1.)

float hash12(vec2 p)
{
	return fract(sin(dot(p, vec2(3.5341, 2.9413))) * 4321.123);
}

vec2 hash21(float v)
{
    vec2 p = vec2(v * 12.3 + 2., v + 11.31);
	return fract(sin(p + dot(p, vec2(2.5341, 1.9413))) * 41321.123);
}

mat2 rot(float a)
{
	float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

float scene(vec3 p)
{
    p.y *= 2.;
    vec2 h2 = hash21(floor(p.y - .5));
    p.xz += h2 * 3.;
    float s = fract(dot(floor(p.xz - .5), vec2(1)) * .5) * 4. - 1.;
    float h = hash12(floor(p.xz) + h2.x);
    p.y -= .5;
    p = abs(fract(p) * 2. - 1.);
    float a = atan(p.x, p.z) * s;
    float x = length(p) - .75;
    vec2 cp = vec2(x, p.y);
    cp *= rot(a * 2. + iTime * (6. + h * 7.));
    cp.y = abs(abs(cp.y) - .0625) - .025;

	return (max(abs(cp.x), abs(cp.y)) - .025) * .3;
}

vec3 normal(vec3 p)
{
	float d = scene(p);
    vec2 e = vec2(.001, .0);
    return normalize(d - vec3(
        scene(p - e.xyy),
        scene(p - e.yxy),
        scene(p - e.yyx)));
}

vec3 ray(vec3 ro, vec3 lookAt, vec2 uv, float zoom)
{
	vec3 f = normalize(lookAt - ro);
    vec3 r = cross(vec3(0., 1., 0.), f);
    vec3 u = cross(f, r);

    return normalize(uv.x * r + uv.y * u + f * zoom);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;

    vec3 v = vec3(.2, 0., .1) * iTime;
    vec3 ro = vec3(0, .25, -2) + v;
    vec3 tgt = vec3(-1) + v;
    vec3 rd = ray(ro, tgt, uv, .7);
    vec3 p;
    float t = 0.;

    for(int i = 0; i < 200; i++)
    {
    	p = ro + rd * t;
        float d = scene(p) * (1. + rd.y * .5);
        t += d;
        if (abs(d) < .0001 || t > 10.) break;
    }

    vec3 n = normal(p);
    vec3 lDir = normalize(ro - p);
    float l = dot(n, lDir) * .5 + .5;
    l *= sat(2. - length(ro - p) * .4);
    l *= sat(scene(p + n * .01) * 200.) * .5 + .5;

    vec3 col = mix(vec3(.05,.05,.3), vec3(.9, .4, .8), l);
    col += l * l * l;
    col *= sat(2. - t * .3);

    col *= vec3(1., .97, .92);

    col = pow(col, vec3(1./2.2));
    col *= 1. - smoothstep(.45, .7, length((uv * iResolution.y / iResolution.xy))) * .5;

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 smoothed = texture(iChannel0, fragCoord / iResolution.xy).xyz;
	vec3 raw = texture(iChannel1, fragCoord / iResolution.xy).xyz;

    vec3 diff = hsv_sub(raw, smoothed);
    vec3 gain = GAIN;

    fragColor = vec4(1.);
    fragColor.xyz = hsv2rgb(hsv_add(raw, diff * gain));
    //fragColor.xyz = vec3(.5) + diff * gain;
    //fragColor.xyz = hsv2rgb(raw);
}

