// Smoothing (slow)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 previous = texture(iChannel1, fragCoord / iResolution.xy).xyz;
    vec3 next = texture(iChannel0, fragCoord / iResolution.xy).xyz;

    // a = previous, b = next
    // r = a * (1 - m) + b * m 
    //   = a + b * m - a * m 
    //   = a + (b - a) * m
    // d = (b - a)
    // r = a + d * m

    vec3 d = hsv_sub(next, previous);
    fragColor.xyz = hsv_add(previous, d * vec3(SMOOTH_BASELINE));
}

