void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float n = 0.;
    vec3 rgb = vec3(0.);

    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;
    float taspect = iChannelResolution[0].y / iChannelResolution[0].x;

    for (int x = -BLUR_INPUT_RADIUS; x < BLUR_INPUT_RADIUS + 1; x++) {
        for (int y = -BLUR_INPUT_RADIUS; y < BLUR_INPUT_RADIUS + 1; y++) {
            float f = 1. / (1. + length(vec2(x, y)) / 2.);
    		vec2 chOfs = vec2(x, y);
            rgb += texture(
                iChannel0, 
                vec2(
                    .5 + uv.x * taspect + chOfs.x / iChannelResolution[0].x,
                    .5 + uv.y +           chOfs.y / iChannelResolution[0].y
                )
            ).xyz * f;
            n += f;
        }
    }

    fragColor.xyz = rgb2hsv(rgb / n);
}

