#define BLUR_INPUT_RADIUS 3         // Blurring of input to reduce noise
#define SMOOTH_RAW .2				// Exponential color smoothing constant for raw footage to reduce color noise. 1 -> no smoothing
#define SMOOTH_BASELINE .1			// Exponential color smoothing constant for baseline image. 1 -> no smoothing
#define GAIN vec3(30., 20., 10.)	// Hue, Saturation, Value amplification

vec3 hsv_sub(vec3 a, vec3 b)
{
    vec3 res = vec3(0.);
    res.yz = a.yz - b.yz;

    // h1 - h0 = 0
    // h0.5 - h0.4 = 0.1
  	// h0.9 - h0.1 = -0.2
  	// h0.1 - h0.9 = 0.2
    // h0.4 - h0.5 = -0.1

    float d1 = a.x - b.x;

    if (d1 < -0.5) {
        res.x = d1 + 1.;
    } else if (d1 > 0.5) {
        res.x = d1 - 1.;
    } else {
        res.x = d1;
    }

    return res;
}


vec3 hsv_add(vec3 a, vec3 b)
{
    vec3 res = a + b;
    res.x = mod(res.x, 1.0);
    return res;
}

// Taken from https://stackoverflow.com/a/17897228
vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

