// TIME UTILS

// 1.0 between t=0 and t1 then 0.0 until t2 -> t=0
float timed_act(float it, float t1, float t2, float spd)
{
    return (1.0 - step(t1 * spd, mod(it * spd, t2 * spd)));
}

// 0.0-> 1.0 (smooth) between t=0 and t1 then 0.0 until t2 -> t=0
float timed_step(float it, float t1, float t2, float spd)
{
    return timed_act(it, t1, t2, spd) * smoothstep(0.0, 1.0, mod(it * spd, t1 * spd));
}

// 0.0 -> 1.0 -> 0.0 (smooth) between t=0 and t1 until t2 -> t=0
float timed_bump(float it, float t1, float t2, float spd)
{
    return timed_act(it, t1, t2, spd) 
        * smoothstep(0.0, 1.0, mod(it * spd * 2.0, t1 * spd * 2.0)) // ascend
        * smoothstep(1.0, 0.0, mod(it * spd * 2.0, t1 * spd * 2.0)  - t1 * spd); // descend
}

// SHAPES UTILS

float filled_rect(vec2 uv, vec2 p0, vec2 p1)
{
    float c = 1.0;
    c *= step(min(p0.x, p1.x), uv.x);
    c *= (1.0 - step(max(p0.x, p1.x), uv.x));
   	c *= step(min(p0.y, p1.y), uv.y);
    c *= (1.0 - step(max(p0.y, p1.y), uv.y));
    return clamp(c, 0.0, 1.0);
}

float rect(vec2 uv, vec2 p0, vec2 p1, float t)
{
    float c = 0.0;
    vec2 thick = vec2(t * 0.5);
    c += filled_rect(uv, p0 - thick, p1 + thick);
    c -= filled_rect(uv, p0 + thick, p1 - thick);
    return clamp(c, 0.0, 1.0);
}

// VECTOR SPACE UTILS

vec2 distortion(vec2 uv, float it, vec2 d, float spd, float cycles)
{
    float v = 1.0;
    float cycle_time = 1.0 / spd;
    v *= timed_act(it, cycle_time, cycle_time * cycles, spd);
    v *= smoothstep(mod(it * spd, 1.0) - 0.5, mod(it * spd, 1.0) - 0.5 + d.y, uv.y);
    uv.x += d.x * v * (1.0 - v);
    return uv;
}

// LOGO DEFINITION

float logo_border(vec2 uv, float it)
{
    float c = 0.0;

    float t0 = spin_on * timed_step(it - spin_st, spin_ln, spin_cyc, spin_spd);
    vec2 spin1 = t0 * vec2(0.04, -0.035);
    vec2 spin2 = t0 * vec2(0.04, -0.04);
    float spin = t0 * 0.04;
    float spiny1 = t0 * 0.035;

    // slightly asymetric on purpose
    c += rect(uv, vec2(-0.40, -0.37) + spin1, vec2(0.36, 0.41) + spin2, 0.018);
    c += rect(uv, vec2(-0.36, -0.41) - spin1, vec2(0.40, 0.37) - spin2, 0.018);
    return clamp(c, 0.0, 1.0);
}

float logo_bars(vec2 uv, float it)
{
    float c = 0.0;


    vec2 p0u = vec2(-4.0 * bar_w, 0.0);
    vec2 p0d = vec2(-3.0 * bar_w, -bar_h);
    vec2 dx = vec2(bar_w * 2.0, 0.0);
    vec2 dy = vec2(0.0, bar_h * 2.0);
    vec2 bar = vec2(bar_w, bar_h);

    float t0 = drift_on * timed_step(it - drift_st, drift_ln, drift_cyc, drift_spd);
    vec2 drift = vec2(0.0, -t0 * bar_h * 2.0);

    float t1 = wave_on * timed_bump(it - wave_st, wave_ln, wave_cyc, wave_spd);

    for (int i = 0; i < 4; i++) {
        float fi = float(i);
        vec2 wave_up = vec2(0.0, t1 * bar_h * (sin(fi * 0.5 + mod(it * wave_spd - wave_st, wave_ln * wave_spd) * wave_spd2) * 0.5 - 0.5));
        vec2 wave_down = vec2(0.0, t1 * bar_h * (sin((fi + 0.5) * 0.5 + mod(it * wave_spd - wave_st, wave_ln * wave_spd) * wave_spd2) * 0.5 - 0.5));

        // upper bars
        c += filled_rect(uv, p0u + dx * fi + drift, p0u + dx * fi + bar + drift + wave_up);

        // lower bars
        c += filled_rect(uv, p0d + dx * fi + drift - wave_down, p0d + dx * fi + bar + drift);

        if (abs(t0) > EPS)
        {
            // upper bars clone when drift
            c += filled_rect(uv, p0u + dx * fi + drift + dy, p0u + dx * fi + bar + drift + dy);
            // lower bars clone when drift
			c += filled_rect(uv, p0d + dx * fi + drift + dy, p0d + dx * fi + bar + drift + dy);
        }
    }
    // mask to crop out the outside
    c *= filled_rect(uv, vec2(-4.0 * bar_w, -bar_h), vec2(4.0 * bar_w, bar_h));
    return clamp(c, 0.0, 1.0);
}


float logo(vec2 uv, float it)
{
    float c = 0.0;
    c += logo_border(uv, it);
    c += logo_bars(uv, it);
    return clamp(c, 0.0, 1.0);
}

// MAIN

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord  - .5 * iResolution.xy)/min(iResolution.x, iResolution.y);

	uv = distortion(uv, iTime - dst_st, vec2(dst_dst * dst_on), dst_spd, dst_cnt);

    vec3 c3 = vec3(logo(uv, iTime));

    float color_dist = col_on * 0.01 * timed_bump(iTime - col_st, col_ln, col_cyc, col_spd);
    if (abs(color_dist) > EPS)
    { 
        c3 = vec3(
        	c3.x,
        	logo(uv + color_dist * vec2(1.0, 0.0), iTime),
        	logo(uv + color_dist * vec2(0.0, 1.0), iTime)
        );
    }

    // click to add color
    c3 += step(EPS, iMouse.z) * (0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)));

    fragColor = vec4(c3,1.0);
}

#define S( a , b , t ) smoothstep( a , b , t )

struct Ray {
	vec3 ro;		// Ray Origin
    vec3 rd;		// Ray Direction
};



// Noise
float Noise(float t) {
    float n = fract( sin( t * 3456.) * 4547. ) ;
    return n;
}

// Noise 1 Input and 4 Output
vec4 Noise14(float t ) {
    vec4 n = fract( sin( t * vec4(123. , 1024. , 3456. , 9564. ) ) * vec4( 6547., 345., 8799., 1564. ) );    
    return n;
}


// RAY
Ray GetRay( vec2 uv , vec3 camPos , float camZoom , vec3 lookAt ) {

    Ray ray;

    ray.ro = camPos;

    vec3 F = normalize( lookAt - camPos );
    vec3 R = normalize( cross( vec3( 0 , 1 , 0 ) , F ) );
    vec3 U = cross( F , R );

    vec3 center  = camPos + F * camZoom;
    vec3 intersectionPoint = center + uv.x * R  + uv.y * U;

    // rd have to do normalized.
    // because where GetClosestPoint() , dot ( target - ro , rd ) * rd.
    ray.rd = normalize( intersectionPoint - camPos );

    return ray;
}


// 
vec3 GetClosestPoint( Ray ray , vec3 targetPos ) {

    // 이때 반드시, rd가 노멀라이즈 되어 있어야,
    // dot( targetPos - ray.ro , ray.rd )가 정확하게 계산이 된다.
    return ray.ro + max( 0.0f , dot( targetPos - ray.ro , ray.rd ) ) * ray.rd;
}


// 
float GetDistance( Ray ray , vec3 targetPos ) {
	float d = length( targetPos - GetClosestPoint( ray , targetPos ) );
    return d;
}


// BOKE
float Boke(  Ray ray , vec3 targetPos ,  float lightDiameter , float blur ) {

    //vec3 targetPos = vec3( -1.0f , 0.15f , z );
    float d = GetDistance( ray , targetPos );
    float expandLight = lightDiameter * length( targetPos );
    //c += Boke( d , expandLight , bokeBlur ) * fade;

	float c = S( expandLight , expandLight * ( 1.0f - blur ) , d );
    c *= mix( 0.7f , 1.0f , S( expandLight * 0.8f , expandLight , d ) );
    return c;
}




// Street Light
vec3 StreetLight( Ray ray , float lightDiameter, float bokeBlur , float time ) {
    float c = 0.0f;

    float t = time * 0.1f;
    float s = 1.0f / 10.0f; // in 1 sec, 100 count.
    float side = step( ray.rd.x , 0.0f );
    ray.rd.x = abs( ray.rd.x ); // reflect ray x
    for ( float i = 0.0f ; i < 1.0f ; i+=s ) {    

        float ti = fract(t + i + side * s * 0.5f );
        vec3 targetPos = vec3( 2.0f , 2.0f , 100.0f - ti * 100.0f );
        c += Boke( ray , targetPos , lightDiameter , bokeBlur ) * ti * ti * ti;
    }

    vec3 col = vec3( 1.0f , 0.7f , 0.3f) * c;
    return col;
}


// Head Light
vec3 HeadLights( Ray ray , float lightDiameter, float bokeBlur , float time ) {

    float headLightDelta = -0.25f;
    float headLightDelta2 = headLightDelta* 1.2f;


    time = time * 2.0f;

    float c = 0.0f;
    float t = time * 0.1f;
    float s = 1.0f / 15.0f; // in 1 sec, 100 count.
    //float side = step( ray.rd.x , 0.0f );
    //ray.rd.x = abs( ray.rd.x ); // reflect ray x
    for ( float i = 0.0f ; i < 1.0f ; i+=s ) {    

        float n = Noise( i );
		if ( n > 0.1f ) continue;

        float ti = fract(t + i);
        float z = 100.0f - ti * 100.0f;
        float fade = ti * ti * ti * ti * ti;
        float focus = S( 0.8f , 1.0f , ti );
        float size = mix( lightDiameter , lightDiameter * 0.5f , focus );

        c += Boke( ray , vec3( -headLightDelta + -1.0f , 0.15f , z ) , size , bokeBlur ) * fade;
        c += Boke( ray , vec3( +headLightDelta + -1.0f , 0.15f , z ) , size , bokeBlur ) * fade;

        c += Boke( ray , vec3( -headLightDelta2 + -1.0f , 0.15f , z ) , size , bokeBlur ) * fade;
        c += Boke( ray , vec3( +headLightDelta2 + -1.0f , 0.15f , z ) , size , bokeBlur ) * fade;


    	// refection
        float reflection = 0.0f;

        reflection += Boke( ray , vec3( -headLightDelta2 + -1.0f , -0.15f , z ) , size * 3.0f , 1.0f ) * fade;
        reflection += Boke( ray , vec3( +headLightDelta2 + -1.0f , -0.15f , z ) , size * 3.0f , 1.0f ) * fade;

        c += reflection * focus;
    }

    //vec3 col = vec3( 1.0f , 1.7f , 0.3f) * c;
    vec3 col = vec3( 0.9f , 0.9f , 1.0f) * c;

    return col;
}


// Tail Light
vec3 TailLights( Ray ray , float lightDiameter, float bokeBlur , float time ) {

    float headLightDelta = -0.25f;
    float headLightDelta2 = headLightDelta* 1.2f;


    time = time * 0.1f;

    float c = 0.0f;
    //float t = time * 0.1f;
    float t = time;

    float s = 1.0f / 15.0f; // in 1 sec, 100 count.
    //float side = step( ray.rd.x , 0.0f );
    //ray.rd.x = abs( ray.rd.x ); // reflect ray x
    for ( float i = 0.0f ; i < 1.0f ; i+=s ) {    

        float n = Noise( i );			// 0 ~ 1
		if ( n > 0.5f ) continue;		// 0 ~ 0.5



        float ti = fract(t + i);
        float z = 100.0f - ti * 100.0f;
        float fade = ti * ti * ti * ti * ti;
        float focus = S( 0.8f , 1.0f , ti );
        float size = mix( lightDiameter , lightDiameter * 0.5f , focus );

        float lane = step( 0.25f , n );	// 0 , 1
        float laneShift = S(0.99f, 0.96f , ti );
        float carPos = 1.5f - lane * laneShift;

        float blink = step( 0.0 , sin( t * 10000.0)) * 7.0f * lane * step( 0.9f , ti );

        c += Boke( ray , vec3( carPos -headLightDelta , 0.15f , z ) , size , bokeBlur ) * fade;
        c += Boke( ray , vec3( carPos +headLightDelta , 0.15f , z ) , size , bokeBlur ) * fade;

        c += Boke( ray , vec3( carPos -headLightDelta2 , 0.15f , z ) , size , bokeBlur ) * fade;
        c += Boke( ray , vec3( carPos +headLightDelta2 , 0.15f , z ) , size , bokeBlur ) * fade * (1.0f + blink);


    	// refection
        float reflection = 0.0f;

        reflection += Boke( ray , vec3( carPos -headLightDelta2 , -0.15f , z ) , size * 3.0f , 1.0f ) * fade;
        reflection += Boke( ray , vec3( carPos +headLightDelta2 , -0.15f , z ) , size * 3.0f , 1.0f ) * fade;

        c += reflection * focus;
    }


    vec3 col = vec3( 1.0f , 0.1f , 0.01f) * c;

    return col;
}



// Environment Light
vec3 EnvironmentLight( Ray ray , float lightDiameter, float bokeBlur , float time ) {
    vec3 c = vec3(0);

    float t = time * 0.1f;
    float s = 1.0f / 10.0f; // in 1 sec, 100 count.
    float side = step( ray.rd.x , 0.0f );
    ray.rd.x = abs( ray.rd.x ); // reflect ray x

    vec3 col = vec3(0.);

    for ( float i = 0.0f ; i < 1.0f ; i+=s ) {    

        float ti = fract(t + i + side * s * 0.5f );

        vec4 n = Noise14( i + side * 100. );
        float x = mix( 2.5f , 10.f , n.x );
        float y = mix( 0.1f , 1.5f , n.y );

        float occulution = sin( ti * 6.28 * 10.0 ) * 0.5f + 0.5f; // 2 pie
        float fade = ti * ti * ti;
        fade = occulution;


        vec3 targetPos = vec3( x , y , 50.0f - ti * 50.0f );

        col = n.wzy;
        c += Boke( ray , targetPos , lightDiameter , bokeBlur ) * fade * col * 0.5f;
    }

    //vec3 col = vec3( 1.0f , 0.7f , 0.3f) * c;
    //return col;
    return c;
}


// MAIN
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord - 0.5f * iResolution.xy) / min( iResolution.x, iResolution.y);

    vec2 mouseUv = iMouse.xy / iResolution.xy;
    //float m = length( mouseUv );


	vec3 camPos = vec3( 0.5f , 0.2f , 0.0f );
    vec3 lookAt = vec3( 0.5f , 0.2f , 1.0f );
    float camZoom = 2.0f;



    float streetLightDiameter = 0.05f;
    float headLightDiameter = 0.05f;
    float bokeBlur = 0.1f;


    Ray ray = GetRay( uv , camPos , camZoom , lookAt );


	vec3 col = StreetLight( ray , streetLightDiameter , bokeBlur, iTime + mouseUv.x );
    col+= HeadLights(ray , headLightDiameter , bokeBlur, iTime + mouseUv.x );
    col+= TailLights(ray , headLightDiameter , bokeBlur, iTime + mouseUv.x );
    col+= EnvironmentLight( ray , streetLightDiameter , bokeBlur, iTime + mouseUv.x );


    // Back ground Gradient color
    col+= ( ray.rd.y + 0.25f ) * vec3( 0.2f , 0.1f, 0.5f );

    fragColor = vec4( col ,1.0);
}

// =========
// CONSTANTS
// =========

#define QP 0.785398163397448
#define TRP 1.047197551196598
#define HP 1.570796326794897
#define P 3.141592653589793
#define TP 6.283185307179586

#define t iTime
#define ht 0.5 * t
#define tt 0.1 * t

#define EPS 0.001
#define STEPS 64.0
#define DIST 0.01

#define S(a, b, t) smoothstep(a, b, t)

// ========
// UTILS FN
// ========

/* map a value `n` from a range `start1` -> `stop1` to a range `start2` -> `stop2` */
float map(float n, float start1, float stop1, float start2, float stop2) {
  return (n - start1) / (stop1 - start1) * (stop2 - start2) + start2;
}

/* map a value `n` from the range -1.0 -> 1.0 to a range `start2` -> `stop2`
  to be used with cos/sin
  e.g.: `map01(sin(x), 0.0, 1.0)` */
float map01(float n, float start2, float stop2) {
  return (0.5 * n + 0.5) * (stop2 - start2) + start2;
}

// classical 2d rotation
vec2 rotate2d(vec2 uv, float a) {
  return mat2(cos(a), -sin(a), sin(a), cos(a)) * uv;
}

// =====
// SCENE
// =====

/* SDF of a torus from Inigo Quilez
  https://iquilezles.org/articles/distfunctions
*/
float torusSDF(vec3 p, vec2 t) {
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

/* scene definition
  mainly defining a torus and deform radius and thickness of it */
float sceneSDF(vec3 p) {
  vec3 _p = p; // save value of `p` before rotating it

  // some rotations
  p.xy = rotate2d(p.xy, t);
  float angle = P * S(0.25, 0.75, abs(2.0 * fract(t / P) - 1.0));
  p.yz = rotate2d(p.yz, angle - QP);

  // combine cos/sin and `p` to deform the radius
  float r = map01(sin(t
    + 2.0 * (_p.x + _p.z)
    + HP * cos(-t + p.y)
    ), 1.25, 1.5); // radius

  // switch between `p.x` and `p.y` to deform thickness
  float mixer = S(0.125, 0.875, abs(2.0 * fract(t / TP) - 1.0));
  // combine cos/sin and `p` to deform the thickness
  float t = map01(cos(-t
    + 2.5 * mix(_p.x, _p.y, mixer)
    + P * sin(-t
      + 2.0 * _p.z
      + TP * sin(t + 0.25 * (_p.x + _p.y + _p.z))
    )), 0.55, 0.65); // thickness

  float torus = torusSDF(p, vec2(r, t));

  return torus;
}

// ======
// RENDER
// ======

/* compute normales by an estimation of them, offseting the scene computation
  more details reading here:
  http://jamie-wong.com/2016/07/15/ray-marching-signed-distance-functions/#surface-normals-and-lighting */
vec3 computeNormal(vec3 p) {
  float center = sceneSDF(p);
  vec3 offset = vec3(0.0, EPS, 0.0);

  return normalize(vec3(
    center - sceneSDF(p + offset.yxx),
    center - sceneSDF(p + offset.xyx),
    center - sceneSDF(p + offset.xxy)
  ));
}

// =====
// MAIN
// =====

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;


  vec3 color = vec3(0.07, 0.05, 0.08);

  vec3 rd = normalize((vec3(uv, 1.0)));
  vec3 ro = vec3(0.0, 0.0, -5.0);

  // raymarching loop
  float f = 10.0 / STEPS;
  for (float i = 0.0; i < STEPS; i++) {
    vec3 pos = ro + f * rd;
    float scene = sceneSDF(pos);
    if (scene < DIST) {
      // use normales as color and switch the channels, instead of classical `.rgb` use `.brg` for a fresher render
      color = 0.5 * computeNormal(pos).brg + 0.5;
      break;
    }
    f += scene;
  }

  fragColor = vec4(color, 1.0);
}

// Apollonian fractal formula by Matej Vanco, 2020

struct ray
{
	vec3 o,d;
	float l;
};

vec4 fractal(vec3 p, vec2 par, vec3 color)
{
	float s = par.x;
	float g = 45.;
	p.xy*=mat2(sin(g), -cos(g), cos(g), sin(g));
	float c = 1.5;

	float scale = 1.;
	vec3 cStorage = color;

	for (int i = 0; i < 13; i++)
	{
		p = -1. + 2.0 * fract(0.5 * p + 0.5);

		float r2 = dot(p, p);

		color += mix(0.8 - color, vec3(0.09,0.0,0.8) * pow(r2, 0.8), 0.8);

		float k = s / r2;
		p *= k;
		p.x *= par.y;
		scale *= k;
	}
	return vec4(color * cStorage, 0.25 * abs(p.y) / scale);
}

vec4 geo(vec3 p)
{
	return fractal(p-vec3(0.22+sin(iTime * 0.2)*0.1,0.9,0.2),vec2(1.24,0.75),vec3(0.3,0.29,0.095));	
}

vec4 march(ray r)
{
	vec3 c = vec3(0.);
	for(int i = 0; i < 64; i++)
	{
		vec3 p = r.o + r.d * r.l;
		vec4 g = geo(p);
		c = g.rgb;
		r.l += g.w;
		if(r.l>8.)
			break;
	}
	return vec4(c, r.l);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ( fragCoord.xy -0.5 * iResolution.xy ) / iResolution.y;

    vec3 c = vec3(0.);

	ray r = ray(vec3(0.), vec3(uv,1.), 0.);
	vec4 mm = march(r);
	vec3 p = r.o + r.d * mm.w;
	c = mm.rgb * mm.w;

    fragColor = vec4(c,1.0);
}

// "RayMarching starting point" 
// by Martijn Steinrucken aka BigWings/CountFrolic - 2020
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// 
// You can use this shader as a template for ray marching shaders

#define MAX_STEPS 100
#define MAX_DIST 20.
#define SURF_DIST .001

#define T iTime * 0.2

#define S smoothstep

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float Hash21(vec2 p) {
    p = fract(p*vec2(123.34,233.53));
    p += dot(p, p+23.234);
    return fract(p.x*p.y);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

vec3 hash( vec3 p ) // replace this by something better. really. do
{
	p = vec3( dot(p,vec3(127.1,311.7, 74.7)),
			  dot(p,vec3(269.5,183.3,246.1)),
			  dot(p,vec3(113.5,271.9,124.6)));

	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec3 x )
{
    // grid
    vec3 p = floor(x);
    vec3 w = fract(x);

    // quintic interpolant
    vec3 u = w*w*w*(w*(w*6.0-15.0)+10.0);


    // gradients
    vec3 ga = hash( p+vec3(0.0,0.0,0.0) );
    vec3 gb = hash( p+vec3(1.0,0.0,0.0) );
    vec3 gc = hash( p+vec3(0.0,1.0,0.0) );
    vec3 gd = hash( p+vec3(1.0,1.0,0.0) );
    vec3 ge = hash( p+vec3(0.0,0.0,1.0) );
    vec3 gf = hash( p+vec3(1.0,0.0,1.0) );
    vec3 gg = hash( p+vec3(0.0,1.0,1.0) );
    vec3 gh = hash( p+vec3(1.0,1.0,1.0) );

    // projections
    float va = dot( ga, w-vec3(0.0,0.0,0.0) );
    float vb = dot( gb, w-vec3(1.0,0.0,0.0) );
    float vc = dot( gc, w-vec3(0.0,1.0,0.0) );
    float vd = dot( gd, w-vec3(1.0,1.0,0.0) );
    float ve = dot( ge, w-vec3(0.0,0.0,1.0) );
    float vf = dot( gf, w-vec3(1.0,0.0,1.0) );
    float vg = dot( gg, w-vec3(0.0,1.0,1.0) );
    float vh = dot( gh, w-vec3(1.0,1.0,1.0) );

    // interpolation
    return va + 
           u.x*(vb-va) + 
           u.y*(vc-va) + 
           u.z*(ve-va) + 
           u.x*u.y*(va-vb-vc+vd) + 
           u.y*u.z*(va-vc-ve+vg) + 
           u.z*u.x*(va-vb-ve+vf) + 
           u.x*u.y*u.z*(-va+vb+vc-vd+ve-vf-vg+vh);
}



vec3 colorInterpolation(vec3 col1, vec3 col2, vec3 col3, vec3 col4, float x) {
	vec3 color;
    float N = 3.;
    if (x < 1./N) {
    	color = mix(col1, col2, x * N);
    }
    else {
        if (x < 2./N) {
            color = mix(col2, col3, (x - 1./N) * N);
        }
        else {
            color = mix(col3, col4, (x - 2./N) * N);
        }
    }
    color = max(vec3(0), color);
    return color;

}



float GetDist(vec3 p) {
    p.x += T * 10.;
    p.z *= 0.6;
    p.x *= 1.;
    p.y *= .5;
    float plane = dot(p, normalize(vec3(0.,1.,0.)));
    float n = noise(p);
    float n2 = noise(p * 2.)  * 0.5;
    float n3 = noise(p * 8.)  * 0.05;
    float n4 = noise(p * 16.)  * 0.005;
    //float n5 = noise(p * 8.) * 0.0375;
    n += n2 + n3 + n4;// + n5;

    return (plane + n) * 0.6;
}









float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST*(1.0+dO)) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}



