#define AA 6.0 / iResolution.y

float hash21(vec2 v) {
    return fract(sin(dot(v, vec2(12.9898, 78.233))) * 43758.5453123);
}

float line(vec2 uv, vec2 a, vec2 b, float width)
{
    vec2 pa = uv - a;
    vec2 ba = b - a;
	float h = clamp(dot(pa,ba) / dot(ba,ba), 0.0, 1.0);	
	return 1.0 - smoothstep(-AA, AA, length(pa - ba * h) - width);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float ar = iResolution.y / iResolution.x;
    uv = uv * 2.0 - 1.0;
    uv *= 1.0 + sin(iTime * 0.5) * 0.35;

    uv.y *= ar;


    vec3 comp = vec3(0.0);
    float lines = 0.0;
    for(int i = 0; i < 100; i++)
    {
       	float ifloat = float(i);
        vec2 seed = vec2(ifloat * 1.0, ifloat * 3.0);
        vec2 pos = (vec2((hash21(seed) * 2.0 - 1.0) * 2.0,
                         (hash21(seed*2.0) * 2.0 - 1.0)) * 0.5);

        float soundsample = texture( iChannel0, vec2(floor(abs(pos.x*0.5) * 32.0) / 32.0,0.0) ).x * 1.10;
        if(soundsample < 0.55)
            continue;

        float burst = pow(soundsample, 5.0) * 20.0;

        pos.x += sin(iTime + ifloat) * 0.25;
        pos.y += cos(iTime * 2.0 + ifloat) * 0.15;
        float value = length(uv + pos) * 4.25;

        // circles
        vec3 col = normalize(vec3(sin(pos.x*5.0) + 0.25, 0.1, sin(pos.y*10.0))) * 3.0;
    	comp += clamp(col * smoothstep(value - AA, value + AA, 0.01 + burst * 0.02) * 0.155, vec3(0.0), vec3(1.0));
        comp += clamp(col * smoothstep(value - 0.5, value + 0.5, 0.01 + burst * 0.075) * 0.01, vec3(0.0), vec3(1.0)) * 2.0;

        // Lines
        float L = 0.0;
        for(int j = 0; j < int(pow(soundsample, 3.0) * 15.8); j++)
        {
            float jfloat = float(j);
            vec2 Lseed = vec2(jfloat, jfloat);
            vec2 Lpos = (vec2((hash21(Lseed) * 2.0 - 1.0) * 2.0,
                         (hash21(Lseed*2.0) * 2.0 - 1.0)) * 0.5);
            Lpos.x += sin(iTime + jfloat) * 0.25;
        	Lpos.y += cos(iTime * 2.0 + jfloat) * 0.15;
            L = max(L, line(uv, -pos, -Lpos, 0.001));
        }
        lines += L;

    }
    comp *= 0.25;
    comp += lines * 0.01;
    vec3 bg = vec3(0.35*(uv.y * 0.5 + 0.5), 0.0, 0.15);
    fragColor = vec4(bg + vec3(comp)*12.0, 1.0);
}

// =========
// CONSTANTS
// =========

#define QP 0.785398163397448
#define TRP 1.047197551196598
#define HP 1.570796326794897
#define P 3.141592653589793
#define TP 6.283185307179586

#define t iTime
#define ht 0.5 * t
#define tt 0.1 * t

#define EPS 0.001
#define STEPS 64.0
#define DIST 0.05

#define S(a, b, t) smoothstep(a, b, t)

// ========
// UTILS FN
// ========

/* map a value `n` from a range `start1` -> `stop1` to a range `start2` -> `stop2` */
float map(float n, float start1, float stop1, float start2, float stop2) {
  return (n - start1) / (stop1 - start1) * (stop2 - start2) + start2;
}

/* map a value `n` from the range -1.0 -> 1.0 to a range `start2` -> `stop2`
  to be used with cos/sin
  e.g.: `map01(sin(x), 0.0, 1.0)` */
float map01(float n, float start2, float stop2) {
  return (0.5 * n + 0.5) * (stop2 - start2) + start2;
}

// classical 2d rotation
vec2 rotate2d(vec2 uv, float a) {
  return mat2(cos(a), -sin(a), sin(a), cos(a)) * uv;
}

// =====
// SCENE
// =====

/* scene definition
  mainly defining a plane deformed to be a wave */
float sceneSDF(vec3 p) {
  // distance from the point `p` to the center of the XZ plane
  float d = length(p.xz);

  // amplitude of the wave (height), smoothly decreased relatively to the center of the plane
  float amp = S(25.0, 0.0, d);

  // offset of the frequency, based on the `z` coord of the plane
  float offset = sin(-t + 0.1 * (p.z));
  // frequency of the wave
  float freq = map(
    S(0.125, 0.875, abs(2.0 * fract((t + offset) / TP) - 1.0)),
    0.0, 1.0, 0.4, 1.0
  );

  /* main equation of the render
	combination of some cos/sin based of `d`, `p.x` and `p.z`
  */
  float wave = amp * cos(-t
    + freq * d
    + sin(2.0 * t)
    - 0.5 * cos(-2.0 * t + 0.25 * d)
    + 0.5 * cos(t + 0.1 * TP * p.x)
    + 0.25 * sin(-t + 0.125 * TP * p.z)
  );
  // sdf of a horizontal plane deformed by a wave
  float plane = p.y + wave;

  return plane;
}

// ======
// RENDER
// ======

/* compute normales by an estimation of them, offseting the scene computation
  more details reading here:
  http://jamie-wong.com/2016/07/15/ray-marching-signed-distance-functions/#surface-normals-and-lighting */
vec3 computeNormal(vec3 p) {
  float center = sceneSDF(p);
  vec3 offset = vec3(0.0, EPS, 0.0);

  return normalize(vec3(
    center - sceneSDF(p + offset.yxx),
    center - sceneSDF(p + offset.xyx),
    center - sceneSDF(p + offset.xxy)
  ));
}

/* compute surface of the object, combining normales, light, ambient etc. */
vec3 computeSurface(vec3 pos, float f) {
  vec3 normal = computeNormal(pos);

  vec3 lightPos = vec3(2.5, -25.0, 25.0 * sin(t + 0.05 * (pos.x + pos.z)));
  float light = max(0.0, dot(normal, lightPos));

  vec3 ambient = vec3(0.2078, 0.2, 0.7725);

  float fog = 5.0 / pow(f, 2.0);

  return vec3(fog * light) + ambient;
}

