// Original source code is available here:
// https://squaresrng.wixsite.com/rand
//
// I'm looking to use it as my go to noise function
// It is simple and only uses mult/adds, bitshifts,
// and bitwise ORs. With no state, it was trivially
// parallelizable to the GPU.

precision highp float;
precision highp int;

// provides 16 psuedo-random bits
// conviently packaged in a float
// in the [0,1] range.
float squares16(uint ctr) {
    const uint key = uint(0x7a1a912f);
    const float two16 = 65536.0;

    uint x, y, z;

    // initialize
    // ==================================
    // Weyl sequence vars, y and z
    y = ctr * key;
    z = (ctr + uint(1)) * key;

    // init the mixing var, x
    x = y;

    // begin the mixing rounds
    // ===================================

    // round 1
    x = x*x + y; x = (x>>16) | (x<<16);

    // round 2
    x = x*x + z; x = (x>>16) | (x<<16);

    // round 3
    x = (x*x + y) >> 16;

    return float(x)/two16;
}

float pixel_id(vec2 fragCoord) {
    return dot(fragCoord.xy,
               vec2(1, iResolution.x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float id = pixel_id(fragCoord);
    int cnt_pixels = int(iResolution.x * iResolution.y);

    vec3 col = vec3(squares16(uint(id)+uint(iFrame*cnt_pixels)));

    // Output to screen
    fragColor = vec4(col,1.0);
}

