    float a = dot(rayDir, rayDir);
    float b = 2.0 * dot(rayDir, -spherePos);
    float c = dot(-spherePos, -spherePos) - r*r;
    float d = b*b - 4.0*a*c;
    float e;
    if (d < 0.0) e = -1.0;
    else e = (-b - sqrt(d)) / 2.0*a;
    return rayDir * e;
}

