vec3 random(vec3 p, int n)
{
    float u = random(vec4(p, n));
    float v = random(vec4(n, p));
    float yaw = tau * u;
    float pitch = acos(2.0*v - 1.0);
    return vec3(sin(pitch)*cos(yaw), cos(pitch), sin(pitch)*sin(yaw));
}

float phong(float cosangle, float alpha)
{
    //float norm = 0.0397436 * alpha + 0.0856832;
    float norm = (alpha + 2.0) / tau;
    return pow(cosangle, alpha) * norm;
}

float gauss(float cosangle, float sigma)
{
    float sqrt2pi = 2.50662827463;
    float angle = acos(cosangle);
    float a = angle/sigma;
    float x = 1.0/(sigma*sqrt2pi);
    return ( x * exp(-0.5 * (a*a)) );// / (x > 1.0 ? x : 1.0);
}

float snell(float angle, float eta)
{
    return sin(eta * sin(angle));
}

float fresnel(float IOR, float cosangle_i)
{
    float n1 = 1.0003; float n2 = IOR;
    float Rs, Rp, cosangle_t;
    cosangle_t = snell(acos(cosangle_i), n1/n2);
    cosangle_t = cos(cosangle_t);
    Rs = (n1*cosangle_i - n2*cosangle_t) / (n1*cosangle_i + n2*cosangle_t);
    Rp = (n1*cosangle_t - n2*cosangle_i) / (n1*cosangle_t + n2*cosangle_i);
    Rs = abs(Rs); Rp = abs(Rp);
    return (Rs*Rs + Rp*Rp) / 2.0;
}

float schlick(float F0, float cosangle)
{
    float a = 1.0 - cosangle;
    return F0 + (1.0 - F0) * (a*a*a*a*a);
        //exp2((-5.55473 * cosangle - 6.98316) * cosangle);
}

