#define PI 3.1415926535897932384626433832795

#define change (sin(iTime) * .5 + .5)

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float v = 0.0;
    vec2 u_k = vec2(8.0);
    float time = iTime;

    vec2 c = (fragCoord.xy / iResolution.xy) * u_k - u_k/2.0;

    float off = mix(1.0, -1.0, change);

    v += sin((c.x * off + time));
    v += sin((c.y * off + time)/2.0);
    v += sin((c.x * -off + c.y * off + time)/2.0);
    c += u_k / 2.0 * vec2(sin(time/3.0), cos(time/2.0));
    v += sin(sqrt(c.x * c.x + c.y * c.y + 1.0) + time);
    v = v / 2.0;

    float a = sin(v * mix(2.0, 2.4, change) * PI) * .5 + .5;
    float b = cos(v * mix(2.0, 2.4, change) * PI) * .5 + .5;

    vec3 col1 = vec3(a - b - .2, a, a - b - .3);
    vec3 col2 = vec3(b - a - .2, b - a - .3, b);

    fragColor = vec4(mix(col1, col2, change), 1);
}

// "Aperiodic Penrose Tiling" by dr2 - 2020
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

/*
 Based on an old program that used OpenGL to draw quads; that was much more 
 efficient than the present pixel-based version and allowed much larger grids.
 Using de Bruijn's projection method [Math. Proc. A 84 (1981) 39,53]

 Projection method also used by knighty (https://www.shadertoy.com/view/XdtBzH) to
 produce a symmetric pattern, but not a full aperiodic tiling.
 An alternative (and faster) substitution method was used by tomkh
 (https://www.shadertoy.com/view/4t2XWG).
 There are other examples (search: penrose) that consider the related kite-and-dart tiles.
*/

#define BIG  0  // set =1 for larger grid - more work!!!

#define VAR_ZERO min (nFrame, 0)

mat3 VToRMat (vec3 v, float a);
float Maxv3 (vec3 p);
vec2 Rot2D (vec2 q, float a);
vec3 HsvToRgb (vec3 c);

float tCur;
int nFrame;
bool showPoly, rhomCol;
const float pi = 3.1415927;

bool InTri (vec2 p, vec2 v0, vec2 v1, vec2 v2)
{
  vec3 s;
  vec2 v10, v20, p0;
  float d;
  v10 = v1 - v0;
  v20 = v2 - v0;
  d = v20.y * v10.x - v20.x * v10.y;
  p0 = p - v0;
  s.xy = vec2 (v20.y * p0.x - v20.x * p0.y, - v10.y * p0.x + v10.x * p0.y);
  s = abs (vec3 (s.xy, d - s.x - s.y) - 0.5 * d);
  return (Maxv3 (s) < 0.5 * abs (d));
}

vec3 ShowScene (vec2 uv)
{
  vec3 col, vn, ltDir;
  vec2 vp[5], csn[5], g, gg, mp, offset, up, vm, v[3];
  float gam[5], gamSum, kCur[5], d, dm, di, rr, pScale, r1, r2, ic, f;
  bool fatRom, done;
#if BIG
  const float kLimit = 7.;
  pScale = 0.09;
#else
  const float kLimit = 4.;
  pScale = 0.15;
#endif
  offset = Rot2D (vec2 (1.7, 0.), 0.05 * pi * tCur);
  uv /= pScale;
  uv -= offset;
  r1 = 0.5 * sin (pi / 5.);
  r2 = 0.5 * sin (2. * pi / 5.);
  for (int k = 0; k < 5; k ++) csn[k] = sin (2. * pi * float (k) / 5. + vec2 (0.5 * pi, 0.));
  gamSum = 0.;
  for (int k = 0; k < 4; k ++) {
    gam[k] = 0.1 * float (k + 1);
    gamSum += gam[k];
  }
  gam[4] = - gamSum;
  done = false;
  ic = -1.;
  for (int k1 = VAR_ZERO; k1 <= 3; k1 ++) {
    for (int k2 = VAR_ZERO + 1; k2 <= 4; k2 ++) {
      if (k2 >= k1 + 1) {
        fatRom = (k2 - k1 == 1 || k2 - k1 == 4);
        rr = fatRom ? r2 * r2 : r1 * r1;
        ++ ic;
        gg = - gam[k1] * csn[k2] + gam[k2] * csn[k1];
        di = 1. / (csn[k1].x * csn[k2].y - csn[k2].x * csn[k1].y);
        for (float kV1 = - kLimit + float (VAR_ZERO); kV1 <= kLimit; kV1 ++) {
          kCur[k1] = kV1;
          for (float kV2 = - kLimit + float (VAR_ZERO); kV2 <= kLimit; kV2 ++) {
            kCur[k2] = kV2;
            g = (gg + kV1 * csn[k2] - kV2 * csn[k1]).yx * vec2 (1., -1.);
            for (int k = 0; k < 5; k ++) {
              if (k != k1 && k != k2) {
                d = dot (csn[k], g) * di + gam[k];
                d += step (0., d);
                kCur[k] = floor (abs (d)) * sign (d);
              }
            }
            vp[0] = vec2 (0.);
            for (int k = 0; k < 5; k ++) vp[0] += kCur[k] * csn[k];
            vp[1] = vp[0] + csn[k1];
            vp[2] = vp[0] + csn[k1] + csn[k2];
            vp[3] = vp[0] + csn[k2];
            mp = 0.25 * (vp[0] + vp[1] + vp[2] + vp[3]) - uv;
            if (showPoly) done = (InTri (uv, vp[0], vp[1], vp[2]) ||
               InTri (uv, vp[0], vp[3], vp[2]));
            else done = (dot (mp, mp) < rr);
            if (done) break;
          }
          if (done) break;
        }
      }
      if (done) break;
    }
    if (done) break;
  }
  if (done) {
    if (showPoly) {
      dm = 99.;
      vp[4] = vp[0];
      for (int k = 0; k < 4; k ++) {
        up = uv - vp[k];
        v[0] = normalize (vp[k + 1] - vp[k]);
        if (k == 0) v[1] = v[0];
        else if (k == 1) v[2] = v[0];
        f = dot (v[0], up);
        d = dot (up, up) - f * f;
        if (d < dm) {
          dm = d;
          vm = v[0];
        }
      }
      dm = sqrt (dm);
      if (rhomCol) col = HsvToRgb (vec3 (0.1 * ic, (fatRom ? 1. : 0.5), 1.));         
      else col = fatRom ? vec3 (0., 1., 0.) : vec3 (1., 0., 0.);
      col *= 0.4 + 0.6 * smoothstep (0.01, 0.03, dm);
      vn = VToRMat (vec3 (vm, 0.), 0.12 * pi * sign (v[1].x * v[2].y - v[2].x * v[1].y) *
         (1. - smoothstep (0., 0.15, dm))) * vec3 (0., 0., -1.);
    } else {
      col = vec3 (1., 1., (fatRom ? 0. : 0.5)) * (1. - smoothstep (0.9, 0.99, dot (mp, mp) / rr));
      vn = - normalize (vec3 (mp, 0.7));
    }
    ltDir = normalize (vec3 (0.4, 0.6, -1.));
    f = max (dot (vn, ltDir), 0.);
    col = col * (0.2 + 0.8 * f * f + 0.5 * pow (max (0., dot (ltDir,
       reflect (vec3 (0., 0., 1.), vn))), 32.));
    col *= 0.1 + 0.9 * smoothstep (0.1, 0.9, Maxv3 (col));
  } else col = vec3 (0.1, 0.1, 0.1);
  return col;
}

#define AA   0 

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr;
  vec3 col;
  vec2 canvas, uv, uvv;
  float asp, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  nFrame = iFrame;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  rhomCol = true;
  showPoly = true;
  if (mPtr.z > 0.) {
    if (mPtr.y > 0.) showPoly = false;
    else rhomCol = false;
  }
  if (abs (uv.x) < 1.) {
#if ! AA
    const float naa = 1.;
#else
    const float naa = 3.;
#endif  
    col = vec3 (0.);
    sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
    for (float a = float (VAR_ZERO); a < naa; a ++) {
      uvv = uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.), sr * (0.667 * a + 0.5) * pi);
      col += (1. / naa) * ShowScene (uvv);
    }
  } else col = vec3 (0.82);
  fragColor = vec4 (col, 1.);
}

mat3 VToRMat (vec3 v, float a)
{
  mat3 m;
  vec3 w, b1, b2;
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  w = (1. - cs.x) * v * v + cs.x;
  b1 = (1. - cs.x) * v.zyx * v.xzy;
  b2 = - cs.y * v;
  m[0][0] = w.x;  m[1][1] = w.y;  m[2][2] = w.z;
  m[1][2] = b1.x + b2.x;  m[2][1] = b1.x - b2.x;
  m[2][0] = b1.y + b2.y;  m[0][2] = b1.y - b2.y;
  m[0][1] = b1.z + b2.z;  m[1][0] = b1.z - b2.z;
  return m;
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

/**
 * Part 6 Challenges:
 * - Make a scene of your own! Try to use the rotation transforms, the CSG primitives,
 *   and the geometric primitives. Remember you can use vector subtraction for translation,
 *   and component-wise vector multiplication for scaling.
 */

const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float EPSILON = 0.0001;

/**
 * Rotation matrix around the X axis.
 */
mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

/**
 * Rotation matrix around the Y axis.
 */
mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

/**
 * Rotation matrix around the Z axis.
 */
mat3 rotateZ(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, -s, 0),
        vec3(s, c, 0),
        vec3(0, 0, 1)
    );
}

/**
 * Constructive solid geometry intersection operation on SDF-calculated distances.
 */
float intersectSDF(float distA, float distB) {
    return max(distA, distB);
}

/**
 * Constructive solid geometry union operation on SDF-calculated distances.
 */
float unionSDF(float distA, float distB) {
    return min(distA, distB);
}

/**
 * Constructive solid geometry difference operation on SDF-calculated distances.
 */
float differenceSDF(float distA, float distB) {
    return max(distA, -distB);
}

/**
 * Signed distance function for a cube centered at the origin
 * with dimensions specified by size.
 */
float boxSDF(vec3 p, vec3 size) {
    vec3 d = abs(p) - (size / 2.0);

    // Assuming p is inside the cube, how far is it from the surface?
    // Result will be negative or zero.
    float insideDistance = min(max(d.x, max(d.y, d.z)), 0.0);

    // Assuming p is outside the cube, how far is it from the surface?
    // Result will be positive or zero.
    float outsideDistance = length(max(d, 0.0));

    return insideDistance + outsideDistance;
}

/**
 * Signed distance function for a sphere centered at the origin with radius r.
 */
float sphereSDF(vec3 p, float r) {
    return length(p) - r;
}

/**
 * Signed distance function for an XY aligned cylinder centered at the origin with
 * height h and radius r.
 */
float cylinderSDF(vec3 p, float h, float r) {
    // How far inside or outside the cylinder the point is, radially
    float inOutRadius = length(p.xy) - r;

    // How far inside or outside the cylinder is, axially aligned with the cylinder
    float inOutHeight = abs(p.z) - h/2.0;

    // Assuming p is inside the cylinder, how far is it from the surface?
    // Result will be negative or zero.
    float insideDistance = min(max(inOutRadius, inOutHeight), 0.0);

    // Assuming p is outside the cylinder, how far is it from the surface?
    // Result will be positive or zero.
    float outsideDistance = length(max(vec2(inOutRadius, inOutHeight), 0.0));

    return insideDistance + outsideDistance;
}



/**
 * Return the normalized direction to march in from the eye point for a single pixel.
 * 
 * fieldOfView: vertical field of view in degrees
 * size: resolution of the output image
 * fragCoord: the x,y coordinate of the pixel in the output image
 */
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}




/**
 * Return a transform matrix that will transform a ray from view space
 * to world coordinates, given the eye point, the camera target, and an up vector.
 *
 * This assumes that the center of the camera is aligned with the negative z axis in
 * view space when calculating the ray marching direction. See rayDirection.
 */
mat3 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat3(s, u, -f);
}

/**
 * Signed distance function describing the scene.
 * 
 * Absolute value of the return value indicates the distance to the surface.
 * Sign indicates whether the point is inside or outside the surface,
 * negative indicating inside.
 */
float sceneSDF(vec3 p) {

    for (int i = 0; i < 6; i++) {
        p = abs(p);
        p += vec3(-0.8);
        p = p * rotateY(iTime*.3);
        p = p * rotateX(iTime*.7);
        p = p * rotateZ(iTime*1.1);
    }
    return boxSDF(p, vec3(3.1 + .9 * sin(iTime * 3.)));
}


/**
 * Using the gradient of the SDF, estimate the normal on the surface at point p.
 */
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

/**
 * Lighting contribution of a single point light source via Phong illumination.
 * 
 * The vec3 returned is the RGB color of the light's contribution.
 *
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 * lightPos: the position of the light
 * lightIntensity: color/intensity of the light
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
                          vec3 lightPos, vec3 lightIntensity) {
    vec3 N = estimateNormal(p);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {
        // Light not visible from this point on the surface
        return vec3(0.0, 0.0, 0.0);
    } 

    if (dotRV < 0.0) {
        // Light reflection in opposite direction as viewer, apply only diffuse
        // component
        return lightIntensity * (k_d * dotLN);
    }
    return lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));
}


/**
 * Lighting via Phong illumination.
 * 
 * The vec3 returned is the RGB color of that point after lighting is applied.
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye) {
    const vec3 ambientLight = 0.5 * vec3(1.0, 1.0, 1.0);
    vec3 color = ambientLight * k_a;

    vec3 light1Pos = vec3(4.0 * sin(iTime),
                          2.0,
                          4.0 * cos(iTime));
    vec3 light1Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light1Pos,
                                  light1Intensity);

    vec3 light2Pos = vec3(2.0 * sin(0.37 * iTime),
                          2.0 * cos(0.37 * iTime),
                          2.0);
    vec3 light2Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light2Pos,
                                  light2Intensity);    
    return color;
}



/**
 * Return the shortest distance from the eyepoint to the scene surface along
 * the marching direction. If no part of the surface is found between start and end,
 * return end.
 * 
 * eye: the eye point, acting as the origin of the ray
 * marchingDirection: the normalized direction to march in
 * start: the starting distance away from the eye
 * end: the max distance away from the ey to march before giving up
 */
float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sceneSDF(eye + depth * marchingDirection);
        if (dist < EPSILON) {
			return depth;
        }
        depth += dist;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec2 mouse = iMouse.xy / 100.;
    vec3 eye = vec3( 50. * sin(iTime), 50. * cos(iTime), 0.);

    mat3 viewToWorld = viewMatrix(eye, vec3(0.0, 0.0, 0.0), vec3(0.0, 0.0, 1.0));

    vec3 worldDir = viewToWorld * viewDir;

    float dist = shortestDistanceToSurface(eye, worldDir, MIN_DIST, MAX_DIST);

    if (dist > MAX_DIST - EPSILON) {
        // Didn't hit anything
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
		return;
    }

    // The closest point on the surface to the eyepoint along the view ray
    vec3 p = eye + dist * worldDir;

    // Use the surface normal as the ambient color of the material
    vec3 K_a = (estimateNormal(p) + vec3(1.0));
    vec3 K_d = K_a;
    vec3 K_s = vec3(1.0, 1.0, 1.0);
    float shininess = 10.0;

    vec3 color = phongIllumination(K_a, K_d, K_s, shininess, p, eye);

    fragColor = vec4(color, 1.0);
}

float sceneSDF(vec3 a){
    return sceneSDF(a,iTime);
}

/**
 * Using the gradient of the SDF, estimate the normal on the surface at point p.
 */
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

/**
 * Lighting contribution of a single point light source via Phong illumination.
 * 
 * The vec3 returned is the RGB color of the light's contribution.
 *
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 * lightPos: the position of the light
 * lightIntensity: color/intensity of the light
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
                          vec3 lightPos, vec3 lightIntensity) {
    lightPos = eye;
    vec3 N = estimateNormal(p);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {
        // Light not visible from this point on the surface
        return vec3(0.0, 0.0, 0.0);
    } 

    if (dotRV < 0.0) {
        // Light reflection in opposite direction as viewer, apply only diffuse
        // component
        return lightIntensity * (k_d * dotLN);
    }
    return lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));
}

/**
 * Lighting via Phong illumination.
 * 
 * The vec3 returned is the RGB color of that point after lighting is applied.
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye) {
    const vec3 ambientLight = 0.5 * vec3(1.0, 1.0, 1.0);
    vec3 color = ambientLight * k_a;

    vec3 light1Pos = eye;
    vec3 light1Intensity = vec3(0.8);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light1Pos,
                                  light1Intensity);   
    return color;
}

