// Lincense: CC0 (https://creativecommons.org/publicdomain/zero/1.0/)

/*
The reconstruction kernels used for per-cell cubic interpolation on regular grids of values and partial derivatives:
https://www.shadertoy.com/view/3tfBzX

red:  kern_v
blue: kern_d

light red:  kern_vD1
light blue: kern_dD1
*/

float kern_v(float x) { return 1.0-x*x*(3.0-2.0*abs(x)); }
float kern_d(float x) { float o = abs(x)-1.0; return x*(o*o); }

float kern_vD1(float x) { return x*(abs(x)*6.0-6.0); }
float kern_dD1(float x) { return (abs(x)-1.0)*(abs(x)*3.0-1.0); }

void mainImage( out vec4 outCol, in vec2 uv0 )
{
	vec2 uv = uv0 - 0.5;
    uv0 += 0.0;

    vec2 tex = uv0;
    tex -= iResolution.xy * vec2(0.5, 0.3);
    tex /= iResolution.xx * 0.125;

    tex *= 0.5;


    vec3 col = vec3(0.0);

	col = mix(vec3(1.0), vec3(0.9), Grid(tex.xy * 4.0, 0.0));        
	col = mix(col, vec3(0.5), Grid(tex.xy * 1.0, 0.0));        
	col = mix(col, vec3(0.125), Cross(tex.xy * 1.0, 0.)); 	


	if(tex.x > -1.0 && tex.x < 1.0)
    {
        col = mix(col, vec3(1.0, 0.0, 0.0), Graph(kern_vD1(tex.x) - tex.y, 0.5)*0.25); 
        col = mix(col, vec3(0.0, 0.5, 1.0), Graph(kern_dD1(tex.x) - tex.y, 0.5)*0.25); 

        col = mix(col, vec3(0.0, 0.5, 1.0), Graph(kern_d(tex.x) - tex.y, 0.5));       
        col = mix(col, vec3(1.0, 0.0, 0.0), Graph(kern_v(tex.x) - tex.y, 0.5));       
    }

    #if 1
    vec2 s = (uv0/iResolution.xy*2.0-1.0);
    s.x = 1.0-Pow2(s.x);    s.y = 1.0-Pow2(s.y);
    col *= mix(1.0, 0.4, Pow2(1.0-sqrt(s.x*s.y)));
    #endif

    outCol = vec4(GammaEncode(clamp01(col)), 1.0);    

}

