float SampleBicubic(vec2 p)
{
    vec2 p0 = floor(p);
    vec2 l  = p - p0;

    float f = 0.0;
	for (float y = 0.0; y < 2.0; ++y)
	for (float x = 0.0; x < 2.0; ++x)
    {
        vec4 n = Map2(p0 + vec2(x, y));

        f += dot(kern(l - vec2(x, y)), n);
    }

    return f;
}

vec4 SampleBicubic2(vec2 p)
{
    vec2 p0 = floor(p);
    vec2 l  = p - p0;

    vec4 r = vec4(0.0);
	for (float y = 0.0; y < 2.0; ++y)
	for (float x = 0.0; x < 2.0; ++x)
    {
        vec4 n = Map2(p0 + vec2(x, y));

        r += kern4x4(l - vec2(x, y)) * n;
    }

    return r;
}

vec4 SampleBicubic3(vec2 p, out vec4 d2)
{
    vec2 p0 = floor(p);
    vec2 l  = p - p0;

    vec4 r = vec4(0.0);
	for (float y = 0.0; y < 2.0; ++y)
	for (float x = 0.0; x < 2.0; ++x)
    {
        vec4 n = Map2(p0 + vec2(x, y));

        mat4 mA, mB;
        kern4x4(l - vec2(x, y), /*out*/ mA, mB);

        r  += mA * n;
        d2 += mB * n;
    }

    return r;
}

void mainImage( out vec4 outCol, in vec2 uv0 )
{
    vec2 uv = uv0 - 0.5;
	vec2 tex = (uv0 - vec2((iResolution.x-iResolution.y) * 0.5, 0.0)) / iResolution.yy;

    vec3 col = vec3(0.0);

    //vec2 p = uv0.xy*0.02;
    float s = iResolution.x / 16.0;
    vec2 p = uv0 / s;

#if DISPLAY == 0

    float base = 0.012;

   #if 1
    vec2 grid0 = quintic(clamp01((abs(fract(p.xy + 0.5) - 0.5)-0.01)*0.75*s));

    base *= mix(0.5, 1.0, min(grid0.x, grid0.y));
   #endif

    vec4 approx = SampleBicubic2(p);
    {
    	vec4 c = approx;
    	float line = 1.0 - quintic(clamp01((abs(c.w / length(c.xy)) - 0.01)*0.5*s));
    	col.gb = vec2(mix(c.w < 0.0 ? 0.0 : clamp01(1.0-c.w*0.25) * 0.25 + base, 1.0, line));
    }

    vec4 ref = Map2(p);
    {
    	vec4 c = ref;
    	float line = 1.0 - quintic(clamp01((abs(c.w / length(c.xy)) - 0.01)*0.5*s));
    	col.r = mix(c.w < 0.0 ? 0.0 : clamp01(1.0-c.w*0.25) * 0.25 + base, 1.0, line);
    }

    col = sRGB_EOTF(clamp01(col));

#else

    vec4 d2;
    vec4 approx = SampleBicubic3(p, /*out*/ d2);

    if(uv0.x < iResolution.x*0.5)
    {
        col = normalize(vec3(-approx.xy*0.1, 1.0))*0.5+0.5;
    }
    else
    {
    	col = vec3(abs(d2.x + d2.y)*0.005);
      //col = vec3(abs(d2.x + d2.y + d2.z + d2.w)*0.005);
    }

#endif

	outCol = vec4(col, 1.0);
}

