float map(vec3 z0)
{
    float T = cos(iTime*0.002)+0.33;
    float T2 = cos(iTime*0.015)+0.25;
    vec4 z = vec4(z0,0.05);

    for (int n = 0; n < 8; n++) {
        z.xyz=clamp(z.xyz, -T*T2, T*T2)*2.0-z.xyz;
        z*=(T-0.25)/max(dot(z.xyz, z.xyz), 0.02*(T+T2));
    }
    return length(max(abs(z.xyz)-vec3(0.0,1.0,0.0),0.0))/z.w;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float T = sin(iTime*0.05)-1.0;
    vec2 uv = fragCoord/iResolution.xy;
	vec3 rayOri = vec3((uv - 0.5) * vec2(iResolution.x/iResolution.y, 1.0)*(1.0-T)*1024.0, 0.0);
	vec3 rayDir = vec3(0.0, 0.0, 0.001);

	float depth = 0.0;
	vec3 p;

	for(int i = 0; i < 10; i++) {
		p = rayOri + rayDir * depth;
		float dist = map(p);
        depth += dist;
		if (dist < 1e-7) {
			break;
		}
	}

    float c = 1.0-pow(depth*0.0005, 0.75);
    fragColor = vec4(c*2.0,c*0.125,c*0.125,1.0-depth*0.00001);
}

