#define PI 3.14159265359

float circle_outline(in vec2 _st, in float _radius, in float _thk, in float _blur){
    vec2 dist = _st-vec2(0.5);
    float pct = smoothstep(_radius-_thk/2.-(_blur),
                         _radius-_thk/2.,
                         dot(dist,dist)*5.128) - 
        		smoothstep(_radius+_thk/2.,
                         _radius+_thk/2.+(_blur),
                         dot(dist,dist)*5.128);
	return pct;
}

