// const
const float PI = 3.14159265359,
    PI_2 = PI/2., PI_3 = PI/3., PI_4 = PI/4., PI_6 = PI/6.,
    SQRT2 = sqrt(2.), SQRT_2 = 1./SQRT2,  
    SQRT3 = sqrt(3.), SQRT_3 = 1./SQRT3;

// 1D-transformations: float => float
float csteps(float x,float b){
    //centered step, step width b
    return b*floor((x/b+0.5));
    }
float signeveodd(float x){
    return sign(mod(x-0.5,2.)-1.);
    }
float smoothpuls(float x,float b){
    return smoothstep(abs(b),0.,abs(x));
    }

// 2D-transformations: vec2 => vec2
vec2 rot2(vec2 x,float a){
    float si = sin(a), co = cos(a);
    return mat2(co,-si,si,co)*x;
    }

vec2 recgrid2rec(in vec2 Z, out vec2 Z_id, out float  z_sr){  
    // maps a grid of unit-squares to one unit-square
    // The unit-square is inside of the unit-circle
    Z /= SQRT_2;	// F:scale grid
    Z_id = floor(0.5*(Z+1.));// global cell offset id (...,-2,-1,0,+1,+2,...)
    Z = Z-2.*Z_id;// local cell coord's
    Z *= SQRT_2; // B:scale cell    
    // symmetries
    z_sr = signeveodd((Z_id.x+Z_id.y));// sense of rotation of local cell
    Z.x *= signeveodd(Z_id.x);//mirror x
    Z.y *= signeveodd(Z_id.y);//mirror y
    return Z;
    }
vec2 hexgrid2hex(in vec2 Z, out vec3 U_id, out vec3 U_lo){
    // maps a hexagonalgrid of unit-hexagons to one unit-hexagon
    // the unit-hexagon is inside of the unit-circle   
    // with edge orientation pointing up
    // using cubic hexagonal coordinates U_id, U_lo
    // ==> https://www.redblobgames.com/grids/hexagons/
	// ==> https://bl.ocks.org/patricksurry/0603b407fa0a0071b59366219c67abca
    // matrix: unitvectors eu,ev -> ex(red),ez(blue)
    const mat2 iM = mat2(SQRT3, -SQRT3, -1., -1.)*2./3., M = inverse(iM);
    Z = iM*Z; // F: cartesian -> 2d-cubic-hex grid-coord's
    vec3 U = vec3(Z.x,-Z.x-Z.y,Z.y); // 3d-cubic-hex grid-coord's, plane: x+y+z = 0    
    U_id = floor(0.5*(U+1.)); // global 3d-cubic-hex grid-cell offset id 
    U_lo = U-2.*U_id; // local 3d-cubic-hex cell coord's
    // correction triangles
    vec3 aU_lo = abs(U_lo);
    if (aU_lo.x > aU_lo.y && aU_lo.x > aU_lo.z) {
        U_lo.x = -U_lo.y-U_lo.z;
        U_id.x = -U_id.y-U_id.z;}
    if (aU_lo.y > aU_lo.z) {
        U_lo.y = -U_lo.x-U_lo.z;
        U_id.y = -U_id.x-U_id.z;}
    else {
        U_lo.z = -U_lo.x-U_lo.y;
        U_id.z = -U_id.x-U_id.y;}
    return M*U_lo.rb ; // B: local 2d-cubic-hex cell -> local-cartesian cell coord's
    }
vec2 hex2tri(in vec2 Z, out float a_id, out float a_cy){
    // maps 6 triangles inside a unit-hexagon to one unit-triangle
    // The unit-hexagon is inside of the unit-circle
    // with edge orientation pointing in x plus direction    
    Z = rot2(Z,-PI_6); 		// orientation adaption  tri2hex ==> hexgrid2hex 
    Z = rot2(Z,PI_3);		// F:orientation-offset of simplex  
    float a = atan(Z.y,Z.x);   
    a_id = floor(a/PI_3+0.5);// triangle offset id ((-3),-2,-1,0,1,2,(3))
    float a_lo = a-PI_3*a_id;			// local coord's angle
    a_cy = signeveodd(a/PI_3);			// cycle even +1, odd -1
    Z = rot2(Z,PI_3*a_id);				// mape grid-zell to unit-triangle
    Z = (Z-vec2(SQRT_3,0.))*SQRT3; 		// translation,scale unit-triangle
    Z.y *= a_cy;						// y-mirror cyclic
    return rot2(Z,-PI_3);				// B:orientation-offset of unit-triangle
    }

// 2D-transformation-matrix: vec2 => mat2
mat2 mat2rot(float a){
    float si = sin(a), co = cos(a);
    return mat2(co,-si,si,co);
    }

// inface: vec2 => bool
bool inudisk(vec2 z){
    // in unit disk
	return (length(z)<1.);
    }
bool inuarrow(vec2 z){
    // in unit arrow
    // line
    bool br = (z.x>-1.) && (z.x<0.9) && (abs(z.y)<0.01); 
    // arrow
    float lr = length(z-vec2(1.,0.));
    bool ba	= (lr<0.2) && (z.x-1.<0.0) && (abs(z.y)<0.3*lr);
    return br || ba;
    }
bool inupoly(vec2 z,int k){
    const float PI = 3.14159265359;
    // in unit polygon
    k = abs(k);			//poly-num-edges
    bool m = true;		//bit-mask    
    if (k<3) m = false;
    else{   
    	float a = PI/float(k);
    	float h = cos(a);//
    	for (int i=0; i<k; i++){
        	float a1 = 2.*a*float(i);
        	vec2 w = vec2(cos(a1),sin(a1));
        	if(dot(w,z)>h) m = false;
        }
    }
    return m;
    }

// texture: vec2 => vec4
vec4 texCh2(vec2 z){
