vec4 texUnitGrid(vec2 z){
    const float b = 0.02;
    vec3 col = vec3(0.5);
    if (abs(z.y) < b)  col = 0.8*vec3(1.,0.,0.);
    if (abs(z.x) < b)  col = 0.8*vec3(0.,1.,0.);
    return vec4(col,smoothpuls(fract(z.x),b) + smoothpuls(fract(z.y),b));
    }
vec4 texUnitCircle(vec2 z){
    const float b = 0.02; float dr = length(z)-1.;
    return vec4(vec3(0.9), smoothpuls(dr,b));
