vec4 texCartesianBase(vec2 z){
    // pixel_color
    bool m1 = false, m2 = false;
    vec3 col = vec3(0.,0.,0.);
    //color replace condition			update alpha-bit   color
    m1 = inuarrow(z);					if(m1){m2 = m2||m1;col=vec3(1.0,0.,0.);}//x
    m1 = inuarrow(mat2(0.,-1.,1.,0.)*z);if(m1){m2 = m2||m1;col=vec3(0.,1.0,0.);}//y
    m1 = inudisk(17.*z);				if(m1){m2 = m2||m1;col=vec3(0.,0.0,1.0);}//z
    return vec4(col,float(m2));
