vec4 texHexCubicBase(vec2 z){
    const float A = sqrt(3.);
    bool m1 = false, m2 = false;
    vec3 col = vec3(0.,0.,0.);
    m1 = inuarrow(.5*mat2(A,1.,-1.,A)*z);	if(m1){m2 = m2||m1;col=vec3(1.,0.,0.);}//r
    m1 = inuarrow(mat2(0.,-1.,1.,0.)*z);	if(m1){m2 = m2||m1;col=vec3(0.,1.,0.);}//g
    m1 = inuarrow(.5*mat2(-A,1.,-1.,-A)*z);	if(m1){m2 = m2||m1;col=vec3(0.,0.,1.);}//b
    m1 = inudisk(30.*z);					if(m1){m2 = m2||m1;col=vec3(1.,1.,0.);}//origin
