#define STEPS 30.0

float hash(float x) {return fract(sin(x)*3145.23);}

vec2 frag_to_uv(vec2 frag) {
  vec2 uv = frag / iResolution.xy - 0.5;
  uv.x /= iResolution.y / iResolution.x;
  return uv;
}

float abs_circle_inv(vec2 p) {
    float circle = abs(length(p) - 0.1);
    return 1./circle;
}

float map(vec2 p) {
  float f = 0.;
  float I = 4.;
  for (float i=0.;i<I;++i) {
    f += abs_circle_inv(p + vec2(0.4 * hash(2.*i) * sin(t*hash(i)),
                                 0.3 * hash(2.*i+.5) * cos(t*1.4*hash(i+.5))));
  }

  f += abs_circle_inv(p - frag_to_uv(iMouse.xy));
  return I/f;
}

float pixel(vec2 uv) {
  float c = 0.;
  float I = STEPS;
  for(float i=0.; i<I; ++i) {
    float div = 5.;
    vec2 uv2 = uv;
    uv2 -= uv * 0.1 * i / I;
    float m = map(uv2);
    m = abs(mod(m, 0.05));
    float x = 0.037;
    c += smoothstep(x, x+2./iResolution.y, m);
  }
  return 2.*c / I;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = frag_to_uv(fragCoord);
  fragColor = vec4(vec3(sqrt(pixel(uv))),1.);
