// Base code by Christopher Wallis
// https://www.shadertoy.com/view/tdffzn


OrbLightDescription GetLight(int lightIndex)
{
    float theta = iTime * 0.7 + float(lightIndex) * PI * 2.0 / float(NUM_LIGHTS);
    float radius = 25.5;

    OrbLightDescription orbLight;
    orbLight.Position = vec3(radius * cos(theta), 5.0 + sin(theta * 2.0) * 2.5, radius * sin(theta));
    orbLight.LightColor = GetLightColor(lightIndex) * LIGHT_INTENSITY;
    orbLight.Radius = 1.0f;

    return orbLight;
}

Material GetMaterial(int materialID, vec3 position)
{
    Material materials[NUM_MATERIALS];
	materials[CHECKER_FLOOR_MATERIAL_ID] = NormalMaterial(vec3(0.6, 0.6, 0.7), 0);
    materials[DEBUG_MATERIAL_ID] = NormalMaterial(vec3(0.6, 0.6, 0.7), 0);

    for (int lightIndex = 0; lightIndex < NUM_LIGHTS; lightIndex++) {
        materials[LAMP_MATERIAL_ID + lightIndex] = NormalMaterial(GetLight(lightIndex).LightColor, MATERIAL_IS_LIGHT_SOURCE);
    }

    Material mat;
    if(materialID < int(NUM_MATERIALS))
    {
        mat = materials[materialID];
    }
    else
    {
        // Should never get hit
   		return materials[0];
    }

    if(materialID == CHECKER_FLOOR_MATERIAL_ID)
    {
        vec2 uv = position.xz / 13.0;
        uv = vec2(uv.x < 0.0 ? abs(uv.x) + 1.0 : uv.x, uv.y < 0.0 ? abs(uv.y) + 1.0 : uv.y);
        if((int(uv.x) % 2 == 0 && int(uv.y) % 2 == 0) || (int(uv.x) % 2 == 1 && int(uv.y) % 2 == 1))
        {
            mat.albedo = vec3(1, 1, 1) * 0.7;
        }
    }

    return mat;    
}

// https://www.scratchapixel.com/lessons/3d-basic-rendering/minimal-ray-tracer-rendering-simple-shapes/ray-plane-and-ray-disk-intersection
float PlaneIntersection(vec3 rayOrigin, vec3 rayDirection, vec3 planeOrigin, vec3 planeNormal, out vec3 normal) 
{ 
    float t = -1.0f;
    normal = planeNormal;
    float denom = dot(-planeNormal, rayDirection); 
    if (denom > EPSILON) { 
        vec3 rayToPlane = planeOrigin - rayOrigin; 
        return dot(rayToPlane, -planeNormal) / denom; 
    } 

    return t; 
} 


float SphereIntersection(
    in vec3 rayOrigin, 
    in vec3 rayDirection, 
    in vec3 sphereCenter, 
    in float sphereRadius, 
    out vec3 normal)
{
      vec3 eMinusC = rayOrigin - sphereCenter;
      float dDotD = dot(rayDirection, rayDirection);

      float discriminant = dot(rayDirection, (eMinusC)) * dot(rayDirection, (eMinusC))
         - dDotD * (dot(eMinusC, eMinusC) - sphereRadius * sphereRadius);

      // If the ray doesn't intersect
      if (discriminant < 0.0) 
         return -1.0;

      float firstIntersect = (dot(-rayDirection, eMinusC) - sqrt(discriminant))
             / dDotD;

      float t = firstIntersect;

      // If the ray is inside the sphere
      if (firstIntersect < EPSILON) {
         t = (dot(-rayDirection, eMinusC) + sqrt(discriminant))
             / dDotD;
      }

      normal = normalize(rayOrigin + rayDirection * t - sphereCenter);
      return t;
}


void UpdateIfIntersected(
    inout float t,
    in float intersectionT, 
    in vec3 intersectionNormal,
    in int intersectionMaterialID,
    out vec3 normal,
    out int materialID
	)
{    
    if(intersectionT > EPSILON && intersectionT < t)
    {
		normal = intersectionNormal;
        materialID = intersectionMaterialID;
        t = intersectionT;
    }
}


float IntersectOpaqueScene(in vec3 rayOrigin, in vec3 rayDirection, out int materialID, out vec3 normal)
{
    float intersectionT = LARGE_NUMBER;
    vec3 intersectionNormal = vec3(0, 0, 0);

    float t = LARGE_NUMBER;
    normal = vec3(0, 0, 0);
    materialID = INVALID_MATERIAL_ID;

    for(int lightIndex = 0; lightIndex < NUM_LIGHTS; lightIndex++)
    {
        UpdateIfIntersected(
            t,
            SphereIntersection(rayOrigin, rayDirection, GetLight(lightIndex).Position, GetLight(lightIndex).Radius, intersectionNormal),
            intersectionNormal,
            LAMP_MATERIAL_ID + lightIndex,
            normal,
            materialID);
    }


    UpdateIfIntersected(
        t,
        PlaneIntersection(rayOrigin, rayDirection, vec3(0, 0, 0), vec3(0, 1, 0), intersectionNormal),
        intersectionNormal,
        CHECKER_FLOOR_MATERIAL_ID,
        normal,
        materialID);


    return t;
}


float SdVolume(vec3 p) {
    float t = iTime;
    vec4 center = vec4(0, 7, 0, 11);
    vec4 wisp = vec4(13., 8.*sin(t) + 16., 2., 6.5);
    float offset = 8.*sin(t*.75);
    vec4 roam = vec4(offset, 6.*sin(t*1.5)+14., offset, 9.);

    float d = SdSphere(p, center.xyz, center.a);
    float dt = SdSphere(p, wisp.xyz, wisp.w);
    d = SdSmoothUnion(d, dt, BLEND_STRENGTH);
    float dr = SdSphere(p, roam.xyz, roam.w);
    d = SdSmoothUnion(d, dr, BLEND_STRENGTH);

    vec3 fbmCoord = (p + 2. * vec3(iTime, 0., iTime)) / NOISE;
    d += NOISE_HEIGHT * fbm(fbmCoord);

    dt = SdPlane(p + vec3(0., .1, 0.));
    d = SdSmoothUnion(d, dt, BLEND_STRENGTH * GROUND_STICK);

    return d;
}

float IntersectVolumetric(in vec3 rayOrigin, in vec3 rayDirection, in float maxDist, out int materialID, out vec3 normal) {
    materialID = INVALID_MATERIAL_ID;

    float dO = 0.;
    vec3 p = vec3(0.);
    for (int i=0; i<MAX_STEPS; i++) {
        p = rayOrigin + dO*rayDirection;
        float ds = SdVolume(p);
        dO += ds;
        if (ds < SURFACE_DIST || dO > maxDist) break;
    }

    return dO;
}

float VolumeLightVisibility(in vec3 rO, in vec3 rDir, in float maxT, in int numSteps, in float marchSize) {
	float t = 0.;
    float lightVis = 1.;
    for(int i=0;i<numSteps;i++) {
    	t += marchSize;
        if (t > maxT) break;

        vec3 p = rO + t*rDir;
        if (SdVolume(p) < 0.) {
            lightVis *= BeerLambert(VOLUMETRIC_ABSORPTION, marchSize);
        }
    }
    return lightVis;
}

vec3 Diffuse(in vec3 normal, in vec3 lightVec, in vec3 diffuse)
{
    float nDotL = dot(normal, lightVec);
    return clamp(nDotL * diffuse, 0.0, 1.0);
}

void CalculateLighting(vec3 position, vec3 normal, vec3 reflectionDir, Material material, inout vec3 color)
{
    for(int lightIndex = 0; lightIndex < NUM_LIGHTS; lightIndex++)
    {
        vec3 lightDir = (GetLight(lightIndex).Position - position);
        float lightDist = length(lightDir);
        lightDir = normalize(lightDir);

        // Manually tuned light falloff for what looked best
        vec3 lightColor = GetLight(lightIndex).LightColor
            * GetLightAttenuation(lightDist);

        #if CAST_SHADOW_ON_OPAQUE
        if (!IsColorInsignificant(lightColor)) {
        	lightColor *= VolumeLightVisibility(position, lightDir, lightDist, MAX_SHADOWMARCH_STEPS, .6);
        }
        #endif

        color += lightColor * pow(max(dot(reflectionDir, lightDir), 0.0), 4.0);
        color += lightColor * Diffuse(normal, lightDir, material.albedo);

    }	
    color += AMBIENT_LIGHT * material.albedo;
}

float FogDensity(vec3 p, float sdfValue) {
	float sdfMultiplier = sdfValue < 0.
        ? min(abs(sdfValue), 1.)
        : 0.;
    float density = abs(fbm(p/6.)+.5);
    return sdfMultiplier * density;
}

vec3 Render( in vec3 rayOrigin, in vec3 rayDir)
{    
    vec3 vColor = vec3(0.);
    vec3 oColor = vec3(0.);
    float fDepth = SCENE_MAX_T;
    float oVisibility = 1.;
    const float marchSize = .6;

    vec3 normal, vnormal;
    int vmaterialId = INVALID_MATERIAL_ID;
    int materialId = INVALID_MATERIAL_ID;

    float oDepth = IntersectOpaqueScene(rayOrigin, rayDir, materialId, normal);

    //we hit the ground or light, do lighting after volumetric
    if (materialId != INVALID_MATERIAL_ID) {
        fDepth = oDepth;
    }

    float vDepth = IntersectVolumetric(rayOrigin, rayDir, fDepth, vmaterialId, vnormal);

    if (vDepth > 0.) {
        //raymarch through volume
        for (int i=0;i<MAX_VOLUME_STEPS;i++) {
        	vDepth += marchSize;

            if (vDepth > oDepth)
                break;

            vec3 p = rayOrigin + rayDir * vDepth;
            float sdfValue = SdVolume(p);
            bool inVolume = sdfValue < 0.;
            if (inVolume) {
            	float prevOVisibility = oVisibility;
                //reduce visibility by volume density and absorption
                oVisibility *= BeerLambert(VOLUMETRIC_ABSORPTION * FogDensity(p, sdfValue), marchSize);
                //stop if results are negligable
                if (oVisibility < MIN_OPACITY) break;
                float marchAbsorption = prevOVisibility - oVisibility;

                //raymarch each light for its contribution at p
                for(int lightIndex=0;lightIndex<NUM_LIGHTS;lightIndex++) {
                	vec3 lightDir = GetLight(lightIndex).Position - p;
                    float lightDist = length(lightDir);
                    lightDir = normalize(lightDir);
                    vec3 lightCol = GetLight(lightIndex).LightColor * GetLightAttenuation(lightDist);
                    //raymarch to light only if value is noticable
                    if (!IsColorInsignificant(lightCol)) {
                        lightCol *= VolumeLightVisibility(p, lightDir, lightDist, MAX_LIGHTMARCH_STEPS, marchSize*1.4);
                    }

                    vColor += marchAbsorption * VOLUMETRIC_ALBEDO * lightCol;
                }
                //Add ambient volume color
                vColor += marchAbsorption * VOLUMETRIC_ALBEDO * AMBIENT_LIGHT;
            }
        }
    }

    //we hit opaque obj
    if( materialId != INVALID_MATERIAL_ID )
    {
        vec3 position = rayOrigin + oDepth*rayDir;
        Material material = GetMaterial(materialId, position);
		if(IsLightSource(material))
        {
            oColor = min(material.albedo, vec3(1.0));
        }       
        else {
        	vec3 reflectionDir = reflect( rayDir, normal);
        	CalculateLighting(position, normal, reflectionDir, material, oColor);
        }
    }
    return min(vColor, 1.) + oVisibility * oColor; 
}

float GetCameraPositionYOffset()
{
    return 250.0 * (iMouse.y / iResolution.y);
}

float GetRotationFactor()
{
    if(iMouse.x <= 0.0)
    {
        // Default value when shader is initially loaded up
        return 0.65f;
    }

    return iMouse.x / iResolution.x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    float aspectRatio = iResolution.x /  iResolution.y; 
    float lensWidth = Camera.LensHeight * aspectRatio;

    vec3 CameraPosition = Camera.Position + GetCameraPositionYOffset();

    vec3 NonNormalizedCameraView = Camera.LookAt - CameraPosition;
    float ViewLength = length(NonNormalizedCameraView);
    vec3 CameraView = NonNormalizedCameraView / ViewLength;

    vec3 lensPoint = CameraPosition;

    // Pivot the camera around the look at point
    {
        float rotationFactor = GetRotationFactor();
        mat3 viewMatrix = GetViewMatrix(rotationFactor);
        CameraView = CameraView * viewMatrix;
        lensPoint = Camera.LookAt - CameraView * ViewLength;
    }

    // Technically this could be calculated offline but I like 
    // being able to iterate quickly
    vec3 CameraRight = cross(CameraView, vec3(0, 1, 0));    
    vec3 CameraUp = cross(CameraRight, CameraView);

    vec3 focalPoint = lensPoint - Camera.FocalDistance * CameraView;
    lensPoint += CameraRight * (uv.x * 2.0 - 1.0) * lensWidth / 2.0;
    lensPoint += CameraUp * (uv.y * 2.0 - 1.0) * Camera.LensHeight / 2.0;

    vec3 rayOrigin = focalPoint;
    vec3 rayDirection = normalize(lensPoint - focalPoint);

    vec3 color = Render(rayOrigin, rayDirection);

    #if USE_BLUE_NOISE
    if (Luminance(color) > NOISE_THRESHOLD) {
        float noise = (texture(iChannel0, uv).r -.5) * NOISE_JITTER;
        color += noise;
    }
    #endif

    color = LinearToSRGB(color);
    fragColor=vec4( color, 1.0 );
}

