#define PI 3.14159
#define LARGE_NUMBER 1e20
#define EPSILON 0.0001
#define SCENE_MAX_T 900.0

//features
#define CAST_SHADOW_ON_OPAQUE 1
#define USE_BLUE_NOISE 0

//lighting
#define NUM_LIGHTS 3
#define LIGHT_ATTENUATION 1.65
#define LIGHT_INTENSITY 15.
#define AMBIENT_LIGHT vec3(.028, .016, .016)
#define VOLUMETRIC_ALBEDO vec3(.9)
#define VOLUMETRIC_ABSORPTION .25
#define MIN_OPACITY .05
#define NOISE_JITTER .02
#define NOISE_THRESHOLD .03

//volume shape
#define BLEND_STRENGTH 1.75
#define GROUND_STICK 13.
#define NUM_OCTAVES 4
#define NOISE 3.
#define NOISE_HEIGHT 5.

//raymarch
#define MAX_STEPS 40
#define MAX_VOLUME_STEPS 40
#define MAX_SHADOWMARCH_STEPS 25
#define MAX_LIGHTMARCH_STEPS 25
#define SURFACE_DIST .03

//materials
#define INVALID_MATERIAL_ID int(-1)
#define CHECKER_FLOOR_MATERIAL_ID 0
#define LAMP_MATERIAL_ID 2
#define DEBUG_MATERIAL_ID 1
#define NUM_MATERIALS (LAMP_MATERIAL_ID + NUM_LIGHTS + 1)
#define MATERIAL_IS_LIGHT_SOURCE 0x1

struct Material
{
    vec3 albedo;
    vec3 emissive;
    int flags;
};

struct CameraDescription
{
    vec3 Position;
    vec3 LookAt;    

    float LensHeight;
    float FocalDistance;
};

struct OrbLightDescription
{
    vec3 Position;
    float Radius;
    vec3 LightColor;
};

CameraDescription Camera = CameraDescription(
    vec3(0, 70, 165),
    vec3(0, 5, 0),
    2.0,
    7.0
);

vec3 GetLightColor(int lightIndex) {
    switch(lightIndex % NUM_LIGHTS)
    {
        case 0: return vec3(1., 0., 1.);
        case 1: return vec3(0., 1., 0.);
        case 2: return vec3(1.0, .5, 0.02);
        case 3: return vec3(0., 0., 1.);
    }
    return vec3(1., 0., 0.);
}

float GetLightAttenuation(float distanceToLight)
{
    return 1. / pow(distanceToLight, LIGHT_ATTENUATION);
}

Material NormalMaterial(vec3 albedo, int flags)
{
    return Material(albedo, vec3(0), flags);
}

bool IsLightSource(in Material m)
{
    return (m.flags & MATERIAL_IS_LIGHT_SOURCE) != 0;
}

float BeerLambert(float absorptionCoefficient, float distanceTraveled)
{
    return exp(-absorptionCoefficient * distanceTraveled);
}

float Luminance(vec3 color)
{
    return (color.r * 0.3) + (color.g * 0.59) + (color.b * 0.11);
}

bool IsColorInsignificant(vec3 color)
{
    const float minValue = 0.009;
    return Luminance(color) < minValue;
}

vec3 LessThan(vec3 f, float value)
{
    return vec3(
        (f.x < value) ? 1.0f : 0.0f,
        (f.y < value) ? 1.0f : 0.0f,
        (f.z < value) ? 1.0f : 0.0f);
}

vec3 LinearToSRGB(vec3 rgb)
{
    rgb = clamp(rgb, 0.0f, 1.0f);

    return mix(
        pow(rgb, vec3(1.0f / 2.4f)) * 1.055f - 0.055f,
        rgb * 12.92f,
        LessThan(rgb, 0.0031308f)
    );
}

// --------------------------------------------//
//               Noise Functions
// --------------------------------------------//
// Taken from Inigo Quilez's Rainforest ShaderToy:
// https://www.shadertoy.com/view/4ttSWf
float hash1( float n )
{
    return fract( n*17.0*fract( n*0.3183099 ) );
}

// Taken from Inigo Quilez's Rainforest ShaderToy:
// https://www.shadertoy.com/view/4ttSWf
float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 w = fract(x);

    vec3 u = w*w*w*(w*(w*6.0-15.0)+10.0);

    float n = p.x + 317.0*p.y + 157.0*p.z;

    float a = hash1(n+0.0);
    float b = hash1(n+1.0);
    float c = hash1(n+317.0);
    float d = hash1(n+318.0);
    float e = hash1(n+157.0);
	float f = hash1(n+158.0);
    float g = hash1(n+474.0);
    float h = hash1(n+475.0);

    float k0 =   a;
    float k1 =   b - a;
    float k2 =   c - a;
    float k3 =   e - a;
    float k4 =   a - b - c + d;
    float k5 =   a - c - e + g;
    float k6 =   a - b - e + f;
    float k7 = - a + b + c - d + e - f - g + h;

    return -1.0+2.0*(k0 + k1*u.x + k2*u.y + k3*u.z + k4*u.x*u.y + k5*u.y*u.z + k6*u.z*u.x + k7*u.x*u.y*u.z);
}

const mat3 m3  = mat3( 0.00,  0.80,  0.60,
                      -0.80,  0.36, -0.48,
                      -0.60, -0.48,  0.64 );

float fbm( in vec3 x )
{
    float f = 2.0;
    float s = 0.5;
    float a = 0.0;
    float b = 0.5;
    for( int i=0; i<4; i++ )
    {
        float n = noise(x);
        a += b*n;
        b *= s;
        x = f*m3*x;
    }
	return a;
}

float SdSmoothUnion( float d1, float d2, float k) {
    float h = max(k-abs(d1-d2),0.0);
    return min(d1, d2) - h*h*0.25/k;
}

float SdPlane( vec3 p ) {
    return p.y;
}

float SdSphere( vec3 p, vec3 origin, float s ) {
  p = p - origin;
  return length(p)-s;
}

mat3 GetViewMatrix(float xRotationFactor)
{ 
   float xRotation = ((1.0 - xRotationFactor) - 0.5) * PI * 0.4 + PI * 0.25;
   return mat3( cos(xRotation), 0.0, sin(xRotation),
                0.0,           1.0, 0.0,    
                -sin(xRotation),0.0, cos(xRotation));
}

