bool isInside_doubleSlitWall(
	vec2 iResolution,  // grr - Shadertoy doesn't make this available as a global var for things in the common code
	vec2 fragCoord
) {
	if ((iResolution.x*0.5-0.0 < fragCoord.x) && (fragCoord.x < iResolution.x*0.5+1.0)
		&& (false
				// 2 holes - 4 pixels wide and 4 pixels apart
			|| (iResolution.y*0.0+0.0 < fragCoord.y) && (fragCoord.y < iResolution.y*0.5-9.0)
			|| (iResolution.y*0.5-3.0 < fragCoord.y) && (fragCoord.y < iResolution.y*0.5+3.0)
			|| (iResolution.y*0.5+9.0 < fragCoord.y) && (fragCoord.y < iResolution.y*1.0-0.0) // */

