const int NUM_STEPS = 2;
const float PI	 	= 3.141592;
const float EPSILON	= 1e-43;
#define EPSILON_NRM (0.1 / iResolution.x)
#define AA

// sea
const int ITER_GEOMETRY = 6;
const int ITER_FRAGMENT = 5;
const float SEA_HEIGHT = 1.6;
const float SEA_CHOPPY = 5.0;
const float SEA_SPEED = 0.8;
const float SEA_FREQ = 1.16;
const vec3 SEA_BASE = vec3(0.0,0.09,0.18);
const vec3 SEA_WATER_COLOR = vec3(0.8,0.9,0.6)*2.6;
#define SEA_TIME (.001 + iTime * SEA_SPEED)
const mat2 octave_m = mat2(1.6,1.2,-1.2,1.6);

// math
mat3 fromEuler(vec3 ang) {
	vec2 a1 = vec2(sin(ang.x),cos(ang.x));
    vec2 a2 = vec2(sin(ang.y),cos(ang.y));
    vec2 a3 = vec2(sin(ang.z),cos(ang.z));
    mat3 m;
    m[0] = vec3(a1.y*a3.y+a1.x*a2.x*a3.x,a1.y*a2.x*a3.x+a3.y*a1.x,-a2.y*a3.x);
	m[1] = vec3(-a2.y*a1.x,a1.y*a2.y,a2.x);
	m[2] = vec3(a3.y*a1.x*a2.x+a1.y*a3.x,a1.x*a3.x-a1.y*a3.y*a2.x,a2.y*a3.y);
	return m;
}
float hash( vec2 p ) {
	float h = dot(p,vec2(127.1,311.7));	
    return fract(sin(h)*343758.5453123);
}
float noise( in vec2 p ) {
    vec2 i = floor( p );
    vec2 f = fract( p );	
	vec2 u = f*f*(3.0-2.0*f);
    return -1.0+2.0*mix( mix( hash( i + vec2(0.0,0.0) ), 
                     hash( i + vec2(1.0,0.0) ), u.x),
                mix( hash( i + vec2(0.0,1.0) ), 
                     hash( i + vec2(1.0,1.0) ), u.x), u.y);
}

// lighting
float diffuse(vec3 n,vec3 l,float p) {
    return pow(dot(n,l) * 0.2 + 0.6,p);
}
float specular(vec3 n,vec3 l,vec3 e,float s) {    
    float nrm = (s + 8.0) / (PI * 4.0);
    return pow(max(dot(reflect(e,n),l),0.0),s) * nrm;
}

// sky
vec3 getSkyColor(vec3 e) {
    e.y = (max(e.y,0.0)*0.8+0.2)*8.2;
    return vec3(pow(1.0-e.y,2.0), 1.0-e.y, 0.6+(1.0-e.y)*0.4) * 0.1;
}

// sea
float sea_octave(vec2 uv, float choppy) {
    uv += noise(uv);        
    vec2 wv = 1.0-abs(sin(uv));
    vec2 swv = abs(cos(uv));    
    wv = mix(wv,swv,wv);
    return pow(1.0-pow(wv.x * wv.y,0.65),choppy);
}

float map(vec3 p) {
    float freq = SEA_FREQ;
    float amp = SEA_HEIGHT;
    float choppy = SEA_CHOPPY;
    vec2 uv = p.xz; uv.x *= 2.75;

    float d, h = 0.0;    
    for(int i = 0; i < ITER_GEOMETRY; i++) {        
    	d = sea_octave((uv+SEA_TIME)*freq,choppy);
    	d += sea_octave((uv-SEA_TIME)*freq,choppy);
        h += d * amp;        
    	uv *= octave_m; freq *= 1.9; amp *= 0.0000001;
        choppy = mix(choppy,1.0,2.0);
    }
    return p.y - h;
}

float map_detailed(vec3 p) {
    float freq = SEA_FREQ;
    float amp = SEA_HEIGHT;
    float choppy = SEA_CHOPPY;
    vec2 uv = p.xz; uv.x *= 1.75;

    float d, h = 0.0;    
    for(int i = 0; i < ITER_FRAGMENT; i++) {        
    	d = sea_octave((uv+SEA_TIME)*freq,choppy);
    	d += sea_octave((uv-SEA_TIME)*freq,choppy);
        h += d * amp;        
    	uv *= octave_m; freq *= 1.3; amp *= 1.22;
        choppy = mix(choppy,2.0,0.2);
    }
    return p.y - h;
}

vec3 getSeaColor(vec3 p, vec3 n, vec3 l, vec3 eye, vec3 dist) {  
    float fresnel = clamp(1.0 - dot(n,-eye), 0.0, 2.0);
    fresnel = pow(fresnel,3.0) * 0.5;

    vec3 reflected = getSkyColor(reflect(eye,n));    
    vec3 refracted = SEA_BASE + diffuse(n,l,80.0) * SEA_WATER_COLOR * 0.12; 

    vec3 color = mix(refracted,reflected,fresnel);

    float atten = max(1.0 - dot(dist,dist) * 0.001, 0.0);
    color += SEA_WATER_COLOR * (p.y - SEA_HEIGHT) * 12.18 * atten;

    color += vec3(specular(n,l,eye,10.0));

    return color;
}

// tracing
vec3 getNormal(vec3 p, float eps) {
    vec3 n;
    n.y = map_detailed(p);    
    n.x = map_detailed(vec3(p.x+eps,p.y,p.z)) - n.y;
    n.z = map_detailed(vec3(p.x,p.y,p.z+eps)) - n.y;
    n.y = eps;
    return normalize(n);
}

float heightMapTracing(vec3 ori, vec3 dir, out vec3 p) {  
    float tm = 0.0;
    float tx = 1000.0;    
    float hx = map(ori + dir * tx);
    if(hx > 0.0) return tx;   
    float hm = map(ori + dir * tm);    
    float tmid = 0.0;
    for(int i = 0; i < NUM_STEPS; i++) {
        tmid = mix(tm,tx, hm/(hm-hx));                   
        p = ori + dir * tmid;                   
    	float hmid = map(p);
		if(hmid < 0.0) {
        	tx = tmid;
            hx = hmid;
        } else {
            tm = tmid;
            hm = hmid;
        }
    }
    return tmid;
}

vec3 getPixel(in vec2 coord, float time) {    
    vec2 uv = coord / iResolution.xy;
    uv = uv * 4.0 - 2.0;
    uv.x *= iResolution.x / iResolution.y;    

    // ray
    vec3 ang = vec3(sin(time*2.0)*0.1,sin(time)*2.2+0.3,time);    
    vec3 ori = vec3(0.0,3.5,time*1.0);
    vec3 dir = normalize(vec3(uv.xy,-2.0)); dir.z += length(uv) * 0.14;
    dir = normalize(dir) * fromEuler(ang);

    // tracing
    vec3 p;
    heightMapTracing(ori,dir,p);
    vec3 dist = p - ori;
    vec3 n = getNormal(p, dot(dist,dist) * EPSILON_NRM);
    vec3 light = normalize(vec3(0.0,1.0,0.8)); 

    // color
    return mix(
        getSkyColor(dir),
        getSeaColor(p,n,light,dir,dist),
    	pow(smoothstep(0.0,-0.02,dir.y),2.2));
}

// main
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float time = iTime * 0.3 + iMouse.x*0.01;

#ifdef AA
    vec3 color = vec3(0.0);
    for(int i = -1; i <= 1; i++) {
        for(int j = -1; j <= 1; j++) {
        	vec2 uv = fragCoord+vec2(i,j)/2.0;
    		color += getPixel(uv, time);
        }
    }
    color /= 2.5;
#else
    vec3 color = getPixel(fragCoord, time);
#endif

    // post
	fragColor = vec4(pow(color,vec3(4.65)), 1.0);
}

#define eNISTA 0
#define eFIOKA 1
#define eKNJIGE 2

#define BROWN vec3(0.647,0.325,0.094)
#define GOLD vec3(1.0,0.843,0.0)

#define LOCAL_UV_SIZE 	vec2(4.0,6.0)
#define LOCAL_UV_OFFSET vec2(0.05*iTime,0.5*iTime)

#define ASPECT_RATIO iResolution.x/iResolution.y

// https://stackoverflow.com/questions/15095909/from-rgb-to-hsv-in-opengl-glsl
// All components are in the range [0…1], including hue.
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

// https://stackoverflow.com/questions/4200224/random-noise-functions-for-glsl
float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

// -------------b
// |			|
// |			|
// a-------------
bool inRect(vec2 x ,vec2 a, vec2 b)
{
    return x.x > a.x && x.x < b.x && x.y > a.y && x.y < b.y;
}

float aspectDist(vec2 x,vec2 y)
{
    return length((x-y)*vec2(ASPECT_RATIO,1.0));
}

vec2 localUVSpace(vec2 uv)
{
    uv += LOCAL_UV_OFFSET;
    uv *= LOCAL_UV_SIZE;
    uv = fract(uv);
    return uv;
}

vec2 localUVIndex(vec2 uv)
{
    uv += LOCAL_UV_OFFSET;
    vec2 uvRatio = 1.0/LOCAL_UV_SIZE;
    return floor(uv/uvRatio);
}

int getItem(vec2 index)
{
    float r = rand(index/1000.0);
    if(r > 0.7) 			return eFIOKA;
    else if(r > 0.1) 		return eKNJIGE;
    else 					return eNISTA;
}

vec3 drawFioka(vec2 uv, vec2 uvIndex)
{
    float fs = 0.2;
    float fd = 0.15;

    vec3 col = BROWN;
    vec2 edge_col = smoothstep(0.85,0.98,uv);
    col -= max(edge_col.x,edge_col.y);
    edge_col = smoothstep(0.80,0.98,vec2(1.0)-uv);
    edge_col += smoothstep(0.85,0.98,vec2(1.0)-uv);
    col -= max(edge_col.x,edge_col.y);
    col = max(col,0.4*BROWN);
    col = max(col,smoothstep(1.0-fs,1.0-fs+fd,uv.x)*BROWN);
    col = max(col,smoothstep(fs,fs-fd,uv.x)*BROWN);
    col = max(col,smoothstep(1.0-fs,1.0-fs+fd,uv.y)*BROWN);
    col = max(col,smoothstep(fs,fs-fd,uv.y)*BROWN);
    col = max(col,0.6*BROWN);

    float handleSize = 0.1;
    vec3 handleColor = 0.6*BROWN;
    float handleDist = aspectDist(uv,vec2(0.5));
    handleColor *= smoothstep(0.15,0.11,handleDist);
    col -= handleColor;

    vec2 reflectionOffset = uvIndex*vec2(0.01,0.02);
    reflectionOffset.x = clamp(-0.03,0.03,reflectionOffset.x);
    reflectionOffset.y = clamp(-0.03,0.03,reflectionOffset.y);

    vec2 reflectionPos = vec2(0.5) + reflectionOffset;
    float reflectionDist = aspectDist(uv,reflectionPos);
    col += smoothstep(0.04,0.001,reflectionDist);    

    return col;
}

vec3 drawKnjige(vec2 uv,vec2 uvIndex)
{
    int numBooks = 6;

    vec2 bookUV = uv;
	bookUV.x = fract(uv.x*float(numBooks));
    int bookIndex = int(uv/(1.0/float(numBooks)));

    float r = rand(vec2(bookIndex,uvIndex.x + uvIndex.y/2.0));
    vec3 bookColorHSV = vec3(r,0.8,0.6);
    vec3 bookColor = hsv2rgb(bookColorHSV);

    float bookMask = sin(3.14*bookUV.x);

    float trakaPos = bookUV.y+0.05*bookMask-0.05*r;

    float t = smoothstep(0.13,0.14,trakaPos);
    float traka = min(t,smoothstep(0.20,0.19,trakaPos));

    traka *= bookMask*0.4;
    vec3 trakaColor = GOLD*0.4;

    vec3 col = bookColor*bookMask + traka*trakaColor;
    col += smoothstep(0.2,0.1,bookMask)*0.6*BROWN;
    col += vec3(traka)*bookMask;

    col = clamp(col,0.0,1.0);
    return col;
}

vec3 drawPolica(vec2 uv, vec2 uvIndex)
{
    float fs = 0.15; // Frame size
    float fd = 0.10; // Frame depth

    vec3 col = vec3(0.0);
	col = max(col,smoothstep(1.0-fs,1.0-fs+fd,uv.x)*BROWN);
    col = max(col,smoothstep(fs,fs-fd,uv.x)*BROWN);
    col = max(col,smoothstep(1.0-fs,1.0-fs+fd,uv.y)*BROWN);
    col = max(col,smoothstep(fs,fs-fd,uv.y)*BROWN);
    col = max(col,0.5*BROWN);

    bool shouldDrawBooks = inRect(uv,vec2(fs),vec2(1.0-fs));
    vec2 booksUV = uv;
    booksUV -= vec2(fs);
    booksUV /= 1.0-2.0*fs;
    col = mix(col,drawKnjige(booksUV,uvIndex),float(shouldDrawBooks));

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspectRatio = iResolution.x/iResolution.y;
    vec2 uv = fragCoord/iResolution.xy;
    uv *= (1.3 + 0.3*sin(iTime))*iResolution.x/800.0;
    uv.x += 0.03*uv.y*(-uv.x);

    vec2 localUV = localUVSpace(uv);
    vec2 localIndex = localUVIndex(uv);
    vec3 col = vec3(0);

    int item = getItem(localIndex);
    if(item == eFIOKA)
    {
	   col = drawFioka(localUV,localIndex);
    }
    else if(item == eKNJIGE)
    {
        col = drawPolica(localUV,localIndex);
    }
	else
    {
       col = BROWN;
    }

    fragColor = vec4(col,1.0);
}

#define PI 3.14159265358979323846

uniform vec2 u_resolution;
uniform float u_time;

// From https://thebookofshaders.com/edit.php#10/ikeda-00.frag
float random(vec2 pos) {
  return fract(sin(dot(pos.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

// https://thebookofshaders.com/09/
vec2 truchet_pattern(in vec2 _pos, in float _index) {
  _index = fract(((_index - 0.5) * 2.0));
  if (_index > 0.75) {
    _pos = vec2(4.0) - _pos;
  } else if (_index > 0.5) {
    _pos = vec2(4.0 - _pos.x, _pos.x);
  } else if (_index > 0.25) {
    _pos = 4.0 - vec2(1.0 - _pos.y, _pos.y);
  }
  return _pos;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec3 color = vec3(0.0);
  float keep_time = sin(2.0 * PI * iTime);

  float A = 10.0;
  float w = 0.5;
  float phi = 0.0;
  float wrap_time = abs(A * sin(w * iTime + phi));

  // [-1, 1]
  vec2 pos = (2.0 * fragCoord - iResolution.xy) /iResolution.y;

  pos *= 2.0;
  vec2 ipos = floor(pos);             // get the integer coords
  vec2 fpos = fract(pos);             // get the fractional coords

  vec2 tile = truchet_pattern(fpos, random(ipos)) * vec2(wrap_time);
  tile.x = cos(tile.y) * sin(tile.x);
  tile.y = log(tile.y) * log(tile.x);

  float maze_color = 0.0;
  maze_color =
    smoothstep(tile.x - 0.3, tile.x, tile.y) -
    smoothstep(tile.x, tile.x + 0.3, tile.y);



  if (wrap_time < 0.25 * A) {
    color = vec3(maze_color - 0.5, maze_color / 0.2, maze_color * 0.7);
  } else if (wrap_time < 0.5 * A) {
    color = vec3(maze_color + 0.5, maze_color / 0.2, maze_color * 0.7);
  } else if (wrap_time < 1.0 * A) {
    color = vec3(maze_color / 0.5, maze_color - 0.2, maze_color / 0.7);
  }

    fragColor = vec4(color,1.0);
}

