	/*
		 if (fract(pos.y * cols * 0.3) < 0.5) {
		 t *= -1.0;
		 }
	 */

	freq -= random(exp(pos.y * pos.y * pos.y));

	float offset_value = 0.025;
	color = vec3(
			random_series(pos.x, freq * 65.0, t + offset_value),
			random_series(pos.x, freq * 65.0, t),
