// From http://thebookofshaders.com/edit.php?log=200809173941
vec3 hsb2rgb(in vec3 c) {
	vec3 abs_rgb = abs(mod(c.x * 6.0 + vec3(0.0, 4.0, 2.0), 6.0) - 3.0) - 1.0;
	vec3 rgb = clamp(abs_rgb, 0.0, 1.0);
	rgb = rgb * rgb * (3.0 - 2.0 * rgb);
	return c.z * mix(vec3(1.0), rgb, c.y);
}

// Inspiration from http://thebookofshaders.com/edit.php?log=200809183022
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	// [-1, 1]
	vec2 pos = (2.0 * fragCoord - iResolution.xy) /iResolution.y;
	vec3 color = vec3(0.0);

	float center = 0.0;
	float radius = 1.0;

	radius = abs(sin(iTime * 1.0)) * radius;

	float pct = distance(pos, vec2(center));
	pct /= distance(pos,vec2(0.0)) + distance(pos,vec2(0.6));
	float threshold_flag = step(pct, radius);

	if (threshold_flag == 1.0){
		vec3 tmp = vec3(pct * threshold_flag);
		color = hsb2rgb(tmp * 26.0);
	} else {
		pct = pow(distance(pos,vec2(0.4)),distance(pos,vec2(0.6)));
		threshold_flag = step(pct, 15.0);
		vec3 tmp = vec3(pct * threshold_flag);
		color = hsb2rgb(tmp * 26.0);
	}

	fragColor = vec4(color, 1.0);
}

