// Inspiration from http://thebookofshaders.com/edit.php?log=200809173941
vec3 twisted_hsb2rgb(in vec3 color) {
	float color_multiplier = mod(iTime, 10.0) / 10.0 + 5.0;
	vec3 c = vec3(5.0, 8.0, 6.0) * color_multiplier;
	vec3 abs_rgb = abs(mod(color.x * 15.0 + c, 8.0) - 1.0) - 1.0;
	vec3 rgb = clamp(abs_rgb, 0.0, 1.0);
	rgb = rgb * rgb * (3.0 - 2.0 * rgb);
	return c.z * mix(vec3(1.0), rgb, c.y);
}

