void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// [0, 1]
	vec2 pos = (2.0 * fragCoord - iResolution.xy) /iResolution.y;

	pos *= iResolution.x / iResolution.y;
	vec3 color = vec3(0.0);
	float d = 0.0;
	float rate = 1.0;
	vec2 dim_bounds = vec2(1.0, 400.0);
	float test = random(dim_bounds);
	float dim = 1.0;
	float switch_inc = 3.0 * PI;


	// Distance field
	float current_time = abs(sin(iTime * rate));
	if (mod(iTime, switch_inc) < PI) {
		d = length(abs(pos) - 0.3) * current_time;
	} else if (mod(iTime, switch_inc) > PI && mod(iTime, switch_inc) < 2.0 * PI) {
		d = length( min(abs(pos)- 0.5, 0.2) ) * current_time;
	} else {
		d = length(max(abs(pos) - 0.0001, 0.3)) * current_time;
