	color = twisted_hsb2rgb(vec3(smoothstep(0.3, 0.4, d) * smoothstep(0.6, 0.5, d)) );

	// Output to screen
	fragColor = vec4(color,1.0);
}

#define PI 3.14159265359

// Inspiration from http://thebookofshaders.com/edit.php?log=200809173941
vec3 twisted_hsb2rgb(in vec3 color) {
  float change_rate = 2.0;
  float color_multiplier = abs(sin(iTime * change_rate)) + 1.0;
  vec3 c = vec3(9.0, 5.0, 2.0) * color_multiplier;
  vec3 rgb_abs = abs(mod(color.x * 5.0 + c, 5.0) - 5.0) - 1.0;
  vec3 rgb = clamp(rgb_abs, 0.0, 1.0);
  rgb = rgb * rgb * (3.0 - 2.0 * rgb);
  return color.z * mix(vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  // [-1, 1]
  vec2 position = (2.0 * fragCoord - iResolution.xy) /iResolution.y;

  vec3 color = vec3(0.0);

  float center = 0.0;
  float scale = 1.0;    

  // Polar coordinates
  float r = length(position) * scale;
  float theta = atan(position.y, position.x);

  float w0 = 500.0;
  float w1 = 10.0;
  float m = -200.0;
  float vary_radius = 0.5;

  float f = smoothstep(-0.5, 1.0, cos(theta * w0)) * m + vary_radius;

  float thickness = 0.3;
  color = twisted_hsb2rgb(vec3(1.0 - smoothstep(f, f + thickness, r)));

  // Output to screen
  fragColor = vec4(color, 1.0);
}

// 'Sanctum'
//
// Thanks to Evvvvil, Flopine, Nusan, BigWings, Iq and a bunch of others
// for sharing their knowledge!

#define MIN_DIST .0015

#define MISS_ID   .5
#define FLOOR_ID  1.5
#define WALL_ID   2.5

float hash(vec2 p) {
    return fract(sin(dot(p, vec2(123.45, 875.43))) * 5432.3);
}

vec2 cappedMod(vec2 p, float c, vec2 rr) {
    return p - c * clamp(round(p / c), -rr, rr);
}

mat2 rot(float a) {
    float c = cos(a),
          s = sin(a);
    return mat2(c, s, -s, c);
}

float sdBox(vec3 p, vec3 b) {
    vec3 q = abs(p) - b;
    return length(max(q, 0.)) + min(max(q.x, max(q.y, q.z)), 0.);
}

float sdBox(vec2 p, vec2 b) {
    vec2 q = abs(p) - b;
    return length(max(q, 0.)) + min(max(q.x, q.y), 0.);
}

float sdCylinder(vec3 p, float h, float r) {
  vec2 d = abs(vec2(length(p.xz), p.y)) - vec2(h,r);
  return min(max(d.x, d.y), 0.) + length(max(d, 0.));
}

float sdFlower(vec2 xy, float r, float a, float peaks) {
    return length(xy) - r + a * sin(atan(xy.y * 2.34 / xy.x) * peaks);
}

vec3 getRayDir(vec3 ro, vec3 lookAt, vec2 uv) {
    vec3 forward = normalize(lookAt - ro),
         right = normalize(cross(vec3(0, 1, 0), forward));
    vec3 up = cross(forward, right);
    return normalize(forward + right * uv.x + up * uv.y);
}

vec2 min2(vec2 a, vec2 b) {
    return a.x < b.x ? a : b;
}

float sdArch(vec3 p, const float plinthDepth) {
    // Main wall.
    vec3 s = vec3(2, 3, .4);
    const float gapWidth = .5;
    float holeOffset = (s.x + gapWidth) / 2.;
    vec3 pp = p;
    pp.x = abs(pp.x);
    pp.y -= s.y;
    float d = sdBox(pp, s);

    // Arch holes.
    d = max(d, -length(pp.xy - vec2(0, 1.5)) + gapWidth);
    d = max(d, -length(pp.xy - vec2(holeOffset, 1)) + gapWidth);

    // Arch gaps.
    pp.y += s.y;
    d = max(d, -sdBox(pp.xy, vec2(gapWidth, s.y + 1.5)));
    d = max(d, -sdBox(pp.xy - vec2(holeOffset, 0), vec2(gapWidth, s.y + 1.)));

    // Support decoration.
    d = min(d, sdBox(pp - vec3(s.x / 3. - .035, s.y + .75, 0.), vec3(.15, .05, s.z + .05)) - .05);

    // Short wall.
    pp.z = abs(pp.z) + .7;
    pp.y -= .6;
    d = min(d, sdCylinder(pp.yxz, 1., s.x - gapWidth / 2.));

    // Plinth.
    d = min(d, sdBox(p - vec3(0, .2, 0), vec3(s.x - gapWidth / 2., .1, s.z + plinthDepth)) - .1);

    // Backing wall.
    pp = p;
    s *= vec3(1.18, 1, .25);
    pp -= vec3(0, s.y, 3);
    d = min(d, sdBox(pp, s));

    // Backing roof.
    float hole = .6,
          bar = .03;
    pp.y -= 1.;
    pp.xy = cappedMod(pp.xy, hole + bar * 2., vec2(1));
    d = max(d, -sdBox(pp, vec3(hole / 2., hole / 2., .5)));

    return d;
}

vec2 map(vec3 p) {
    float surface = texture(iChannel0, (p.xy + p.yz + p.xz) * .1).r * .01,

    // 'Collapse'.
          collapse = sdFlower(p.xy - vec2(1, 11), 5., .2, 8.);
    collapse = max(collapse, 10. - p.z);
    collapse = min(collapse, sdFlower(p.xy - vec2(.9, 10), 3., .2, 4.));

    // 'Fold space' to duplicate the arches.
    for (int i = 0; i < 9; i++) {
        p.xz *= rot(-.1);
        p.x -= 1.04;
        p.x = abs(p.x);
    }

    // Top arches.
    float d = sdArch(p - vec3(0, 5.7, 10), 0.);
    d = max(d, -collapse);

    // Bottom arches.
    d = min(d, sdArch(p - vec3(0, 0, 10), 1.));
    d -= surface;

    return min2(vec2(abs(p.y), FLOOR_ID), vec2(d, WALL_ID));
}

vec3 calcNormal(vec3 p) {
    vec2 e = vec2(1., -1.) * .5773 * .0001;
    return normalize(e.xyy * map(p + e.xyy).x +
					 e.yyx * map(p + e.yyx).x +
					 e.yxy * map(p + e.yxy).x +
					 e.xxx * map(p + e.xxx).x);
}

float calcAO(vec3 p, vec3 n, float d) {
    return clamp(map(p + n * d).x / d, 0., 1.);
}

float calcShadow(vec3 p, vec3 lightPos) {
    vec3 rd = normalize(lightPos - p);

    float res = 1.,
          t = .1;
    for(float i = 0.; i < 30.; i++)
    {
        float h = map(p + rd * t).x;
        if (h < .03)
            return 0.; // Hit an object - Full shadow.

        res = min(res, 8. * h / t);
        t += h;

        if (t > 20.)
            break; // Marched far enough - Stop.
    }

    return res;
}

/**********************************************************************************/

vec3 vignette(vec3 col, vec2 fragCoord) {
    vec2 q = fragCoord.xy / iResolution.xy;
    col *= .5 + .5 * pow(16. * q.x * q.y * (1. - q.x) * (1. - q.y), .4);
    return col;
}

vec3 sunPos = vec3(10, 8, 20),
     sunCol = vec3(2, 1.9, 1.5);

vec3 getMaterial(vec3 p, vec3 rd, float id) {
    if (id == MISS_ID)
        return vec3(0);

    vec3 sunDir = normalize(sunPos - p),
         n = calcNormal(p);

    vec3 mat;
    if (id == FLOOR_ID) {
        mat = vec3(texture(iChannel0, p.xz * .01).r);
    } else if (id == WALL_ID) {
        mat = texture(iChannel0, (n.xy + n.yz) * .05).rgb;
    }

    // Diffuse color.
    float diff = max(0., dot(sunDir, n)),

    // Fake ambient occlusion.
          occ = min(1., .2 + calcAO(p, n, 1.) * calcAO(p, n, .4));

    // Shadows.
    float sha = calcShadow(p, sunPos);

    // Primary light (Sun).
    vec3 lig = diff * sha * sunCol;

    // Seconary light (Sky);
    lig += max(0., .5 + .5 * n.y) * vec3(.7, .8, 1) * .2 * occ;

    // Third light - Ambient.
    lig += max(0., dot(sunDir * vec3(-1., 0., -1.), n)) * sunCol * .02 * occ;

    return mat * lig;
}

void march(vec3 ro, vec3 rd, out vec3 p, out vec2 h) {
    float d = .01;
    for (float steps = 0.; steps < 96.; steps++) {
        p = ro + rd * d;
        h = map(p);

        if (abs(h.x) < MIN_DIST) return; // We've hit a surface - Stop.
        d += h.x * .9; // No hit, so keep marching.
    }

    h.y = MISS_ID;
}

void mainImage(out vec4 fragColor, vec2 fragCoord)
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;

    // Camera.
    float ft = fract(iTime / 8.),
          phase = mod(floor(iTime / 8.), 3.);

    vec3 ro,
         lookAt = vec3(0, 6, 12);

    if (phase == 0.) {
        ro = vec3(5. - ft, 1.5, -5.);
        lookAt.x -= ft;
        sunPos.x *= mix(.82, 1.62, ft);
        sunPos.y *= mix(2.375, .90, ft);
    } else if (phase == 1.) {
        ro = vec3(-5. + ft, 1.5, 2.);
        lookAt = vec3(0, 3, 12);
        sunPos.x *= mix(.02, 1.99, ft);
        sunPos.y *= mix(.375, .90, ft);
    } else if (phase == 2.) {
        ro = vec3(mix(-5., -.0625, ft), 4.7, 5.);
        sunPos.y *= 1.625;
    }

    vec3 rd = getRayDir(ro, lookAt, uv);

    // Raymarch.
    vec2 h = vec2(1e7, MISS_ID);
    vec3 p;
	march(ro, rd, p, h);

    // Materials and lighting.
    vec3 col = getMaterial(p, rd, h.y);

    // Volumetric lighting ('God rays')
    const float maxSteps = 35.;
    float dp = distance(ro, p);
    vec3 beamStep = rd * dp / maxSteps;
    float illum = 0.;
    float jit = hash(uv) * .6;
    for (float i = 0.; i < maxSteps; i++) {
        vec3 sp = ro + beamStep * (i + jit); // Jitter so smooth sampling point. (Thanks BigWings!)
        illum += smoothstep(0., 1., calcShadow(sp, sunPos));
    }

    illum /= maxSteps;
    col = mix(col, sunCol, pow(illum, .7));

    // Fog.
    col = mix(vec3(1), col, vec3(exp(-pow(dp / 50., 3.) * 5.)));

    // Output to screen.
    col = vignette(pow(col, vec3(.4545)), fragCoord);
    fragColor = vec4(col, 1);
}

//#define SHOWMOUSE
//#define SHOWDITHER
#define DITHERING
#define DOWNSCALE
#define PI 3.14

#define GRID_UV_ZOOM 10.0
#define GRID_NOISE_ZOOM 2.0
#define GRID_NUM vec2(8.0)
#define BLUR_SPREAD 32.0
#define VIGNETTE

// dithering : colour precision
#define COL_LEVELS 4.0

float sampleDither (vec2 uv, vec2 resolution)
{
    vec2 screencoord = (uv * resolution);
    return texture(iChannel1, fract(screencoord / vec2(8.0))).r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 screenuv = fragCoord/iResolution.xy;
    vec3 col = vec3(0.0);

    // downscale the screen
    #ifdef DOWNSCALE
    	vec2 downscaleResolution = floor(iResolution.xy * 0.25);
    	vec2 uvScale = downscaleResolution; // vec2(downscaleFactor) / iResolution.xy;
    	vec2 uv = floor(screenuv * uvScale) / uvScale;

    	#ifdef DITHERING
    		float screenDither = sampleDither(uv, downscaleResolution);
   		#endif
   	#else
    	vec2 uv = screenuv;
    	float screenDither = 0.5;
    #endif

    // get screen texture
    col = texture(iChannel0, uv).rgb;

    // calculate blur
    vec3 colblur = vec3(0.0);

    vec2 PIXEL_SZ = 1.0 / iResolution.xy;
    const float HALF_ITERATION = 4.0;
    const float ITERATION_WEIGHT = 0.5 / HALF_ITERATION;
    const vec2 BLUR_OFF = vec2(BLUR_SPREAD, 0.0);
    for (float i=-HALF_ITERATION; i<HALF_ITERATION; i+=1.0)
    {
        float iterationIndex = i + screenDither;
        vec2 uvoff = PIXEL_SZ * BLUR_OFF * iterationIndex;
        vec3 screencolh = texture(iChannel0, uv + uvoff.xy).rgb;
        vec3 screencolv = texture(iChannel0, uv + uvoff.yx).rgb;

        colblur += (screencolh + screencolv) * ITERATION_WEIGHT * 0.5;
    }

    // apply blur as glow
    const vec2 BLUR_SHARPNESS = vec2(2.0, 4.0);
    const float BLUR_STRENGTH = 0.85;
    colblur.r = pow(1.0 - pow(1.0 - colblur.r, BLUR_SHARPNESS.x), BLUR_SHARPNESS.y);
    colblur.g = pow(1.0 - pow(1.0 - colblur.g, BLUR_SHARPNESS.x), BLUR_SHARPNESS.y);
    colblur.b = pow(1.0 - pow(1.0 - colblur.b, BLUR_SHARPNESS.x), BLUR_SHARPNESS.y);
    col += colblur * BLUR_STRENGTH;
    col = clamp(col, 0.0, 1.0);

    // Limit colours
    #ifdef DITHERING
        const float COL_LEVELS_INV = 1.0 / COL_LEVELS;
        vec3 colDiv = (floor(col * COL_LEVELS) * COL_LEVELS_INV);
        vec3 colFrac = fract(col * COL_LEVELS);
        col = colDiv + floor(max(screenDither - colFrac, 0.0) + 0.5) * COL_LEVELS_INV;
        col = clamp(col, 0.0, 1.0);
    #endif

    // Apply vignette
    #ifdef VIGNETTE
    	const vec3 vignettetint = vec3(0.2, 0.25, 0.4);
    	float centerDist = length(0.5 - uv);

    	col *= mix(vec3(1.0), vignettetint, smoothstep(0.5, 0.75, centerDist));
    #endif

    // output to screen
    fragColor = vec4(col,1.0);

    #ifdef SHOWDITHER
    	fragColor = vec4(vec3(screenDither),1.0);
    #endif
}

