                       	sin(layerAngle), cos(layerAngle));
        layerUV *= 1.0 / uvAspectFactor;

        layerUV *= (1.0 + iterationZoomStep - layerZoom);
        layerUV += 0.5;
        layerUV *= uvAspectFactor * GRID_UV_ZOOM;
        layerUV += vec2(layerTime + cos(layerTime * PI * 0.15 + PI) * 4.0, layerTime + sin(layerTime * 0.5 * PI));
        layerUV += layerGridOff;

        // visualize grid
        float layerAlpha = mix(1.0, 1.0 - iterationZoom, max(layerInterp + iterationZoomShadeOff - 1.0, 0.0) / iterationsInv); // (1.0 - (min(layerShadeInterp, 0.0) / iterationsInv));
        float layerShadeInterp = layerInterpZoom; // clamp((1.0 - layerInterp) - iterationZoomShadeOff, 0.0, 1.0);
