float hash3(vec3 p)
{
    p *= 60.0;
    p = 50.0*fract( p*0.3183099 + vec3(0.71,0.113, 0.563));
    return fract(p.x*p.y*(p.x+p.y) + p.x*p.z*(p.x+p.z) + p.y*p.z*(p.y+p.z));
}

float sdBox( vec3 p, vec3 b)
{
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float opRep( in vec3 p, in vec3 c, in vec3 d)
{
    vec3 q = mod(p+0.5*c,c)-0.5*c;
    return sdBox( q, d );
}

float scene(vec3 p)
{
    float retVal =  opRep (p, vec3 (2.0), vec3 (1.0));
    float retVal1 = max (retVal, -p.x + 1.0);
    float retVal2 = max (retVal, p.x + 1.0);
    float retVal3 = max (retVal, -p.y + 1.0);
    float retVal4 = max (retVal, p.y + 1.0);
    return min (min (retVal3, retVal4), min (retVal1, retVal2));
}

vec3 sceneNorm (vec3 p)
{
    float gradD = 0.01;
    float gradX = scene (p + vec3 ( gradD, 0.0, 0.0)) - scene (p - vec3 ( gradD, 0.0, 0.0));
    float gradY = scene (p + vec3 ( 0.0, gradD, 0.0)) - scene (p - vec3 ( 0.0, gradD, 0.0));
    float gradZ = scene (p + vec3 ( 0.0, 0.0, gradD)) - scene (p - vec3 ( 0.0, 0.0, gradD));

    return normalize (vec3 (gradX, gradY, gradZ));
}

vec4 fetchTexture (vec2 uvCoord, vec2 textureRes)
{
    vec2 fetchCoord = uvCoord * textureRes;
    vec2 fetchFract = fract (fetchCoord);
    vec4 fetch00 = texelFetch (iChannel0, ivec2 (fetchCoord.xy), 0);
    vec4 fetch10 = texelFetch (iChannel0, ivec2 (fetchCoord.xy) + ivec2 (1, 0), 0);
    vec4 fetch01 = texelFetch (iChannel0, ivec2 (fetchCoord.xy) + ivec2 (0, 1), 0);
    vec4 fetch11 = texelFetch (iChannel0, ivec2 (fetchCoord.xy) + ivec2 (1, 1), 0);

    return mix (mix (fetch00, fetch10, fetchFract.x), mix (fetch01, fetch11, fetchFract.x), fetchFract.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy) * 2.0 - 1.0;
    vec2 textureRes = min (vec2 (800.0, 450.0), iChannelResolution[0].xy);
    uv.y *= iResolution.y/iResolution.x;

    vec3 eye = vec3 (0.0, 0.0, -iTime);
    vec3 look = normalize (vec3 (0.0, 0.0, -1.0));

    vec3 side = normalize (cross (vec3 (cos(iTime * 0.1), sin(iTime * 0.1), 0.0), look));
    vec3 up = cross (side, look);
    if ( dot (up, vec3 (cos(iTime * 0.1), sin(iTime * 0.1), 0.0)) < 0.0 ) up = -up;

    vec3 sampleDir = normalize (look + side * uv.x + up * uv.y);
    vec3 samplePt = eye + sampleDir;
    vec3 colorAccum = vec3 (0.0);

    samplePt = eye + sampleDir;

    const float drawDistSq = 10000.0;

    for (float jj = 0.0; jj != 60.0; jj += 1.0)
    {
        vec3 samplePtToEye = samplePt - eye;
        float curDist = scene (samplePt);
        if (abs(curDist) < 0.01 )
        {
	        vec3 sceneNorm = abs (sceneNorm (samplePt));
            vec3 colorFetch = vec3 (0.0);
            if ( sceneNorm.x > 0.75 )
            	colorFetch = fetchTexture (fract (samplePt.yz * 0.15), textureRes).rgb;
            else if ( sceneNorm.y > 0.75 )
            	colorFetch = fetchTexture (fract (samplePt.xz * 0.15), textureRes).rgb;
            else
            	colorFetch = fetchTexture (fract (samplePt.xy * 0.15), textureRes).rgb;
            colorFetch /= max (length (samplePtToEye) * 0.1, 1.0);
            colorAccum += colorFetch;
            samplePt += sampleDir * 5.0;
        }
        samplePt += curDist*sampleDir;
        if ( dot (samplePtToEye,samplePtToEye) > drawDistSq )
            break ;
    }

    fragColor.rgb = colorAccum;
}

