void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    bool timeToTurn = (iFrame % 40 == 0);
    vec2 iRes = min (vec2 (800.0, 450.0), iChannelResolution[0].xy);
    if ( fragCoord.x >= iRes.x || fragCoord.y >= iRes.y ) discard;
    vec2 uv = fragCoord/iRes.xy;
    vec2 pixelSize = 1.0/iRes.xy;
    if ( iFrame == 0 )
    {
        float pixelRad = length (pixelSize) * 0.5;
        fragColor = vec4 (0.0);
        for (int i = 0; i != 600; i++)
        {
            vec2 pixelRand = hash (float(i));
	    	if ( length ( uv - pixelRand ) < pixelRad )
            {
                fragColor = vec4 (hash2( pixelRand ), 1.0, 0.0);
            }
		}
    }
    else
    {
        vec4 neighFetch;
        vec2 uv_minus_x = fragCoord.xy - vec2 (1.0, 0.0);
        if ( uv_minus_x.x < 0.0 ) uv_minus_x.x = iRes.x - 1.0;
        neighFetch = texelFetch (iChannel0, ivec2 (uv_minus_x), 0);
        if ( neighFetch.b == 0.95 && neighFetch.g < 0.25 )
        {
            if ( timeToTurn )
            {
                float changeOfDir = hash2 (uv + vec2 (iTime * 5.0)).x;
                neighFetch.g = 0.5 + changeOfDir * 0.5;
            }            
            fragColor = vec4 (neighFetch.rg, 1.0, 0.0);
            return ;
        }
        vec2 uv_plus_x = fragCoord.xy + vec2 (1.0, 0.0);
        if ( uv_plus_x.x > iRes.x - 1.0 ) uv_plus_x.x = 0.0;
        neighFetch = texelFetch (iChannel0, ivec2 (uv_plus_x), 0);
        if ( neighFetch.b == 0.95 && neighFetch.g >= 0.25 && neighFetch.g < 0.5 )
        {
            if ( timeToTurn )
            {
                float changeOfDir = hash2 (uv + vec2 (iTime * 5.0)).x;
                neighFetch.g = 0.5 + changeOfDir * 0.5;
            }
            fragColor = vec4 (neighFetch.rg, 1.0, 0.0);
            return ;
        }
        vec2 uv_minus_y = fragCoord.xy - vec2 (0.0, 1.0);
        if ( uv_minus_y.y < 0.0 ) uv_minus_y.y = iRes.y - 1.0;
        neighFetch = texelFetch (iChannel0, ivec2 (uv_minus_y), 0);
        if ( neighFetch.b == 0.95 && neighFetch.g >= 0.5 && neighFetch.g < 0.75 )
        {
            if ( timeToTurn )
            {
                float changeOfDir = hash2 (uv + vec2 (iTime * 5.0)).x;
                neighFetch.g = changeOfDir * 0.5;
            }
            fragColor = vec4 (neighFetch.rg, 1.0, 0.0);
            return ;
        }
        vec2 uv_plus_y = fragCoord.xy + vec2 (0.0, 1.0);
        if ( uv_plus_y.y > iRes.y - 1.0 ) uv_plus_y.y = 0.0;
        neighFetch = texelFetch (iChannel0, ivec2 (uv_plus_y), 0);
        if ( neighFetch.b == 0.95 && neighFetch.g >= 0.75 )
        {
            if ( timeToTurn )
            {
                float changeOfDir = hash2 (uv + vec2 (iTime * 5.0)).x;
                neighFetch.g = changeOfDir * 0.5;
            }
            fragColor = vec4 (neighFetch.rg, 1.0, 0.0);
            return ;
        }

        vec4 prevFetch = texelFetch (iChannel0, ivec2 (fragCoord.xy), 0);
        prevFetch.b *= 0.95;
        if ( prevFetch.b < 0.05 ) prevFetch = vec4 (0.0);
        fragColor = prevFetch;
    }
}

