vec4 readImage(ivec2 uv, ivec2 screenRes)
{
    if ( uv.x < 0 ) uv.x = screenRes.x - 1;
    else if ( uv.x > screenRes.x - 1) uv.x = 0;

    if ( uv.y < 0 ) uv.y = screenRes.y - 1;
    else if ( uv.y > screenRes.y - 1) uv.y = 0;

    vec4 mapRead = texelFetch (iChannel0, uv, 0);

    if ( mapRead.x < 0.5 )
    {
        return vec4 (0.0, mapRead.b * 0.5, mapRead.b, 1.0);
    }
    else
    {
        return vec4 (mapRead.b, mapRead.b * 0.5, mapRead.b, 1.0);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 iRes = min (ivec2 (800, 450), ivec2 (iChannelResolution[0].xy));
    if ( int(fragCoord.x) >= iRes.x || int(fragCoord.y) >= iRes.y ) discard;
    vec4 centRead = readImage (ivec2 (fragCoord), iRes);
    vec4 totalRead = vec4 (0.0);
    for (int i = -2; i != 3; i++)
        for (int j = -2; j != 3; j++)
            totalRead += readImage (ivec2 (fragCoord) + ivec2 (i, j), iRes);
    fragColor = centRead + totalRead * 0.13;
}

