float tCur, dstFar, eWid;
bool isBg, doFg;
const float pi = 3.1415927;

float ObjDf (vec3 p)
{
  float dMin;
  dMin = dstFar;
  if (isBg) {
    dMin = min (dMin, PrCapsDf ((abs (p) - sLen.wyz).yzx, eWid, sLen.x));
    dMin = min (dMin, PrCapsDf ((abs (p) - sLen.xwz).xzy, eWid, sLen.y));
    dMin = min (dMin, PrCapsDf ((abs (p) - sLen.xyw), eWid, sLen.z));
  } else {
    dMin = min (dMin, PrCylDf ((p - (sLen.xwz - 10. * vuDir)).xzy, eWid, sLen.y - eWid));
    dMin = min (dMin, PrCylDf ((p - (sLen.xyw * vec3 (1., -1., 1.) - 10. * vuDir)), eWid, sLen.z - eWid));
  }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0002 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0001, -0.0001);
  for (int j = 0; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 roo, col, vn;
  vec2 s;
  float dstObj, dstObjF, nDotL;
  sLen = vec4 (vec3 (3., 2., 4.), 0.);
  eWid = 0.3;
  isBg = true;
  roo = ro;
  col = vec3 (0.61, 0.6, 0.6);
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    col4 = (Minv3 (max (abs (ro) - sLen.xyz + eWid, 0.)) > 0.) ?
       vec4 (0.9, 0.9, 1., 0.3) : vec4 (0., 0.9, 0.6, 0.2);
    if (doFg) {
      isBg = false;
      ro = roo;
      dstObjF = ObjRay (ro, rd);
      if (dstObjF < dstObj) {
        ro += dstObjF * rd;
        vn = ObjNf (ro);
        col4 = vec4 (0., 0.9, 0.6, 0.2);
