#ifndef saturate
#define saturate(X) clamp(X, 0.0, 1.0)
#endif

// Hack to prevent loop unrolling.
#define ZERO float(min(0, iFrame))
#define UZERO uint(min(0, iFrame))

// Various noise functions
//
// Simplex noise from https://github.com/ashima/webgl-noise
// Copyright © 2011 Ashima Arts
//
// Worley noise from https://github.com/Erkaman/glsl-worley
// Copyright © Stefan Gustavson
//
// Value noise with gradients from https://www.shadertoy.com/view/XdXBRH
// Copyright © 2017 Inigo Quilez
//
// All noise functions are licensed under the MIT License
//

vec2 mod289(vec2 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec3 mod289(vec3 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec4 mod289(vec4 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec3 permute(vec3 x) { return mod289(((x * 34.0) + 1.0) * x); }
vec4 permute(vec4 x) { return mod289(((x * 34.0) + 1.0) * x); }
float hash(float n) { return fract(sin(n) * 753.5453123); }
vec2 hash(vec2 x) {
  const vec2 k = vec2(0.3183099, 0.3678794);
  x = x * k + k.yx;
  return -1.0 + 2.0 * fract(16.0 * k * fract(x.x * x.y * (x.x + x.y)));
}
vec2 hash22(vec2 p) {
  p = mod(p, 5.0);
  float n = sin(dot(p, vec2(41.0, 289.0)));
  return fract(vec2(8.0, 1.0) * 262144.0 * n);
}

vec4 taylorInvSqrt(vec4 r) { return 1.79284291400159 - 0.85373472095314 * r; }

float snoise(vec2 v) {
  const vec4 C = vec4(0.211324865405187, 0.366025403784439, -0.577350269189626,
                      0.024390243902439);
  vec2 i = floor(v + dot(v, C.yy));
  vec2 x0 = v - i + dot(i, C.xx);
  vec2 i1;
  i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;
  i = mod289(i);
  vec3 p =
      permute(permute(i.y + vec3(0.0, i1.y, 1.0)) + i.x + vec3(0.0, i1.x, 1.0));
  vec3 m = max(
      0.5 - vec3(dot(x0, x0), dot(x12.xy, x12.xy), dot(x12.zw, x12.zw)), 0.0);
  m = m * m;
  m = m * m;
  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;
  m *= 1.79284291400159 - 0.85373472095314 * (a0 * a0 + h * h);
  vec3 g;
  g.x = a0.x * x0.x + h.x * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}

float snoise(vec3 v) {
  const vec2 C = vec2(1.0 / 6.0, 1.0 / 3.0);
  const vec4 D = vec4(0.0, 0.5, 1.0, 2.0);

  // First corner
  vec3 i = floor(v + dot(v, C.yyy));
  vec3 x0 = v - i + dot(i, C.xxx);

  // Other corners
  vec3 g = step(x0.yzx, x0.xyz);
  vec3 l = 1.0 - g;
  vec3 i1 = min(g.xyz, l.zxy);
  vec3 i2 = max(g.xyz, l.zxy);

  vec3 x1 = x0 - i1 + C.xxx;
  vec3 x2 = x0 - i2 + C.yyy; // 2.0*C.x = 1/3 = C.y
  vec3 x3 = x0 - D.yyy;      // -1.0+3.0*C.x = -0.5 = -D.y

  // Permutations
  i = mod289(i);
  vec4 p = permute(permute(permute(i.z + vec4(0.0, i1.z, i2.z, 1.0)) + i.y +
                           vec4(0.0, i1.y, i2.y, 1.0)) +
                   i.x + vec4(0.0, i1.x, i2.x, 1.0));

  // Gradients: 7x7 points over a square, mapped onto an octahedron.
  // The ring size 17*17 = 289 is close to a multiple of 49 (49*6 = 294)
  float n_ = 0.142857142857; // 1.0/7.0
  vec3 ns = n_ * D.wyz - D.xzx;

  vec4 j = p - 49.0 * floor(p * ns.z * ns.z);

  vec4 x_ = floor(j * ns.z);
  vec4 y_ = floor(j - 7.0 * x_);

  vec4 x = x_ * ns.x + ns.yyyy;
  vec4 y = y_ * ns.x + ns.yyyy;
  vec4 h = 1.0 - abs(x) - abs(y);

  vec4 b0 = vec4(x.xy, y.xy);
  vec4 b1 = vec4(x.zw, y.zw);

  vec4 s0 = floor(b0) * 2.0 + 1.0;
  vec4 s1 = floor(b1) * 2.0 + 1.0;
  vec4 sh = -step(h, vec4(0.0));

  vec4 a0 = b0.xzyw + s0.xzyw * sh.xxyy;
  vec4 a1 = b1.xzyw + s1.xzyw * sh.zzww;

  vec3 p0 = vec3(a0.xy, h.x);
  vec3 p1 = vec3(a0.zw, h.y);
  vec3 p2 = vec3(a1.xy, h.z);
  vec3 p3 = vec3(a1.zw, h.w);

  // Normalise gradients
  vec4 norm =
      taylorInvSqrt(vec4(dot(p0, p0), dot(p1, p1), dot(p2, p2), dot(p3, p3)));
  p0 *= norm.x;
  p1 *= norm.y;
  p2 *= norm.z;
  p3 *= norm.w;

  // Mix final noise value
  vec4 m =
      max(0.6 - vec4(dot(x0, x0), dot(x1, x1), dot(x2, x2), dot(x3, x3)), 0.0);
  m = m * m;
  return 42.0 *
         dot(m * m, vec4(dot(p0, x0), dot(p1, x1), dot(p2, x2), dot(p3, x3)));
}

// 2D value noise with derivatives
// https://iquilezles.org/articles/gradientnoise
vec3 noised(vec2 p) {
  vec2 i = floor(p);
  vec2 f = fract(p);

  vec2 u = f * f * f * (f * (f * 6.0 - 15.0) + 10.0);
  vec2 du = 30.0 * f * f * (f * (f - 2.0) + 1.0);

  vec2 ga = hash(i + vec2(0.0, 0.0));
  vec2 gb = hash(i + vec2(1.0, 0.0));
  vec2 gc = hash(i + vec2(0.0, 1.0));
  vec2 gd = hash(i + vec2(1.0, 1.0));

  float va = dot(ga, f - vec2(0.0, 0.0));
  float vb = dot(gb, f - vec2(1.0, 0.0));
  float vc = dot(gc, f - vec2(0.0, 1.0));
  float vd = dot(gd, f - vec2(1.0, 1.0));

  return vec3(va + u.x * (vb - va) + u.y * (vc - va) +
                  u.x * u.y * (va - vb - vc + vd), // value
              ga + u.x * (gb - ga) + u.y * (gc - ga) +
                  u.x * u.y * (ga - gb - gc + gd) + // derivatives
                  du * (u.yx * (va - vb - vc + vd) + vec2(vb, vc) - va));
}

float fbm(vec2 p, uint octaves) {
  float a = 1.0;
  float n = 0.0;
  for (uint i = 0u; i < octaves; i++) {
    n += snoise(p) * a;
    p *= 2.0;
    a *= 0.5;
  }

  return n;
}

float fbm(vec3 p, uint octaves) {
  float a = 1.0;
  float n = 0.0;
  for (uint i = 0u; i < octaves; i++) {
    n += snoise(p) * a;
    p *= 2.0;
    a *= 0.5;
  }

  return n;
}

float worley(vec2 P) {
  const float K = 0.142857142857;
  const float K2 = 0.0714285714285;
  const float jitter = 0.8;
  vec2 Pi = mod289(floor(P));
  vec2 Pf = fract(P);
  vec4 Pfx = Pf.x + vec4(-0.5, -1.5, -0.5, -1.5);
  vec4 Pfy = Pf.y + vec4(-0.5, -0.5, -1.5, -1.5);
  vec4 p = permute(Pi.x + vec4(0.0, 1.0, 0.0, 1.0));
  p = permute(p + Pi.y + vec4(0.0, 0.0, 1.0, 1.0));
  vec4 ox = mod(p, 7.0) * K + K2;
  vec4 oy = mod(floor(p * K), 7.0) * K + K2;
  vec4 dx = Pfx + jitter * ox;
  vec4 dy = Pfy + jitter * oy;
  vec4 d = dx * dx + dy * dy;
  d.xy = (d.x < d.y) ? d.xy : d.yx;
  d.xz = (d.x < d.z) ? d.xz : d.zx;
  d.xw = (d.x < d.w) ? d.xw : d.wx;
  d.y = min(d.y, d.z);
  d.y = min(d.y, d.w);
  return sqrt(d.xy).x;
}

vec3 worley_dist(vec3 x, vec3 y, vec3 z) { return x * x + y * y + z * z; }

float worley(vec3 P) {
  const float jitter = 0.8;
  float K = 0.142857142857;     // 1/7
  float Ko = 0.428571428571;    // 1/2-K/2
  float K2 = 0.020408163265306; // 1/(7*7)
  float Kz = 0.166666666667;    // 1/6
  float Kzo = 0.416666666667;   // 1/2-1/6*2

  vec3 Pi = mod(floor(P), 289.0);
  vec3 Pf = fract(P) - 0.5;

  vec3 Pfx = Pf.x + vec3(1.0, 0.0, -1.0);
  vec3 Pfy = Pf.y + vec3(1.0, 0.0, -1.0);
  vec3 Pfz = Pf.z + vec3(1.0, 0.0, -1.0);

  vec3 p = permute(Pi.x + vec3(-1.0, 0.0, 1.0));
  vec3 p1 = permute(p + Pi.y - 1.0);
  vec3 p2 = permute(p + Pi.y);
  vec3 p3 = permute(p + Pi.y + 1.0);

  vec3 p11 = permute(p1 + Pi.z - 1.0);
  vec3 p12 = permute(p1 + Pi.z);
  vec3 p13 = permute(p1 + Pi.z + 1.0);

  vec3 p21 = permute(p2 + Pi.z - 1.0);
  vec3 p22 = permute(p2 + Pi.z);
  vec3 p23 = permute(p2 + Pi.z + 1.0);

  vec3 p31 = permute(p3 + Pi.z - 1.0);
  vec3 p32 = permute(p3 + Pi.z);
  vec3 p33 = permute(p3 + Pi.z + 1.0);

  vec3 ox11 = fract(p11 * K) - Ko;
  vec3 oy11 = mod(floor(p11 * K), 7.0) * K - Ko;
  vec3 oz11 = floor(p11 * K2) * Kz - Kzo; // p11 < 289 guaranteed

  vec3 ox12 = fract(p12 * K) - Ko;
  vec3 oy12 = mod(floor(p12 * K), 7.0) * K - Ko;
  vec3 oz12 = floor(p12 * K2) * Kz - Kzo;

  vec3 ox13 = fract(p13 * K) - Ko;
  vec3 oy13 = mod(floor(p13 * K), 7.0) * K - Ko;
  vec3 oz13 = floor(p13 * K2) * Kz - Kzo;

  vec3 ox21 = fract(p21 * K) - Ko;
  vec3 oy21 = mod(floor(p21 * K), 7.0) * K - Ko;
  vec3 oz21 = floor(p21 * K2) * Kz - Kzo;

  vec3 ox22 = fract(p22 * K) - Ko;
  vec3 oy22 = mod(floor(p22 * K), 7.0) * K - Ko;
  vec3 oz22 = floor(p22 * K2) * Kz - Kzo;

  vec3 ox23 = fract(p23 * K) - Ko;
  vec3 oy23 = mod(floor(p23 * K), 7.0) * K - Ko;
  vec3 oz23 = floor(p23 * K2) * Kz - Kzo;

  vec3 ox31 = fract(p31 * K) - Ko;
  vec3 oy31 = mod(floor(p31 * K), 7.0) * K - Ko;
  vec3 oz31 = floor(p31 * K2) * Kz - Kzo;

  vec3 ox32 = fract(p32 * K) - Ko;
  vec3 oy32 = mod(floor(p32 * K), 7.0) * K - Ko;
  vec3 oz32 = floor(p32 * K2) * Kz - Kzo;

  vec3 ox33 = fract(p33 * K) - Ko;
  vec3 oy33 = mod(floor(p33 * K), 7.0) * K - Ko;
  vec3 oz33 = floor(p33 * K2) * Kz - Kzo;

  vec3 dx11 = Pfx + jitter * ox11;
  vec3 dy11 = Pfy.x + jitter * oy11;
  vec3 dz11 = Pfz.x + jitter * oz11;

  vec3 dx12 = Pfx + jitter * ox12;
  vec3 dy12 = Pfy.x + jitter * oy12;
  vec3 dz12 = Pfz.y + jitter * oz12;

  vec3 dx13 = Pfx + jitter * ox13;
  vec3 dy13 = Pfy.x + jitter * oy13;
  vec3 dz13 = Pfz.z + jitter * oz13;

  vec3 dx21 = Pfx + jitter * ox21;
  vec3 dy21 = Pfy.y + jitter * oy21;
  vec3 dz21 = Pfz.x + jitter * oz21;

  vec3 dx22 = Pfx + jitter * ox22;
  vec3 dy22 = Pfy.y + jitter * oy22;
  vec3 dz22 = Pfz.y + jitter * oz22;

  vec3 dx23 = Pfx + jitter * ox23;
  vec3 dy23 = Pfy.y + jitter * oy23;
  vec3 dz23 = Pfz.z + jitter * oz23;

  vec3 dx31 = Pfx + jitter * ox31;
  vec3 dy31 = Pfy.z + jitter * oy31;
  vec3 dz31 = Pfz.x + jitter * oz31;

  vec3 dx32 = Pfx + jitter * ox32;
  vec3 dy32 = Pfy.z + jitter * oy32;
  vec3 dz32 = Pfz.y + jitter * oz32;

  vec3 dx33 = Pfx + jitter * ox33;
  vec3 dy33 = Pfy.z + jitter * oy33;
  vec3 dz33 = Pfz.z + jitter * oz33;

  vec3 d11 = worley_dist(dx11, dy11, dz11);
  vec3 d12 = worley_dist(dx12, dy12, dz12);
  vec3 d13 = worley_dist(dx13, dy13, dz13);
  vec3 d21 = worley_dist(dx21, dy21, dz21);
  vec3 d22 = worley_dist(dx22, dy22, dz22);
  vec3 d23 = worley_dist(dx23, dy23, dz23);
  vec3 d31 = worley_dist(dx31, dy31, dz31);
  vec3 d32 = worley_dist(dx32, dy32, dz32);
  vec3 d33 = worley_dist(dx33, dy33, dz33);

  vec3 d1a = min(d11, d12);
  d12 = max(d11, d12);
  d11 = min(d1a, d13); // Smallest now not in d12 or d13
  d13 = max(d1a, d13);
  d12 = min(d12, d13); // 2nd smallest now not in d13
  vec3 d2a = min(d21, d22);
  d22 = max(d21, d22);
  d21 = min(d2a, d23); // Smallest now not in d22 or d23
  d23 = max(d2a, d23);
  d22 = min(d22, d23); // 2nd smallest now not in d23
  vec3 d3a = min(d31, d32);
  d32 = max(d31, d32);
  d31 = min(d3a, d33); // Smallest now not in d32 or d33
  d33 = max(d3a, d33);
  d32 = min(d32, d33); // 2nd smallest now not in d33
  vec3 da = min(d11, d21);
  d21 = max(d11, d21);
  d11 = min(da, d31); // Smallest now in d11
  d31 = max(da, d31); // 2nd smallest now not in d31
  d11.xy = (d11.x < d11.y) ? d11.xy : d11.yx;
  d11.xz = (d11.x < d11.z) ? d11.xz : d11.zx; // d11.x now smallest
  d12 = min(d12, d21);                        // 2nd smallest now not in d21
  d12 = min(d12, d22);                        // nor in d22
  d12 = min(d12, d31);                        // nor in d31
  d12 = min(d12, d32);                        // nor in d32
  d11.yz = min(d11.yz, d12.xy);               // nor in d12.yz
  d11.y = min(d11.y, d12.z);                  // Only two more to go
  d11.y = min(d11.y, d11.z);                  // Done! (Phew!)
  return sqrt(d11.x);                         // F1
}

float fbm_worley(vec3 p, uint octaves) {
  float a = 1.0;
  float n = 0.0;
  for (uint i = 0u; i < octaves; i++) {
    n += worley(p) * a;
    p *= 2.0;
    a *= 0.5;
  }

  return n;
}

// Hybrid multi-fractal noise from Musgrave, "Procedural Fractal Terrains"
// https://www.classes.cs.uchicago.edu/archive/2015/fall/23700-1/final-project/MusgraveTerrain00.pdf
float hmf(vec2 p, uint iters) {
  const float ex[6] =
      float[6](0.803851, 0.6461764, 0.5194296, 0.4175439, 0.3356431, 0.2698071);
  float n = 1.0;
  float w = n;

  for (uint i = 0u; i < iters; i++) {
    p *= 1.95;
    w *= (1.0 - abs(snoise(p))) * ex[i];
    n += w;
  }

  return n;
}

// Ray-AABB intersection test
vec2 intersectRayBox(vec3 ro, vec3 rd, vec3 bmin, vec3 bmax) {
  vec3 tMin = (bmin - ro) / rd;
  vec3 tMax = (bmax - ro) / rd;
  vec3 t1 = min(tMin, tMax);
  vec3 t2 = max(tMin, tMax);
  float tNear = max(max(t1.x, t1.y), t1.z);
  float tFar = min(min(t2.x, t2.y), t2.z);
  return vec2(tNear, tFar);
}

// Point-AABB intersection test
float pointInsideBox(vec3 p, vec3 bmin, vec3 bmax) {
  vec3 s = step(bmin, p) - step(bmax, p);
  return s.x * s.y * s.z;
}

// Generate points on a hemisphere
// i is the index of the current point
// n is the total number of points
vec3 forwardSF(float i, float n) {
  const float PI = 3.141592653589793238;
  const float PHI = 1.618033988749894848;
  float phi = 2.0 * PI * fract(i / PHI);
  float zi = 1.0 - (2.0 * i + 1.0) / n;
  float sinTheta = sqrt(1.0 - zi * zi);
  return vec3(cos(phi) * sinTheta, sin(phi) * sinTheta, zi);
}

// Remaps value V from range L0-H0 to range LN-HN
float remap(float v, float l0, float h0, float ln, float hn) {
  return ln + (v - l0) * (hn - ln) / (h0 - l0);
}

// Unpacks a 4-component vector of floats in range [0..1] from a 32-bit uint.
// Can use unpackUnorm4x8() instead if available.
// https://www.khronos.org/registry/OpenGL-Refpages/gl4/html/unpackUnorm.xhtml
vec4 unpackUint4x8(uint x) {
  return vec4(uvec4(x, x >> 8, x >> 16, x >> 24) & 0xFFu) / 255.0;
}

// Packs a 4-component vector of floats in range [0..1] into a 32-bit uint.
// Can use packUnorm4x8() instead if available.
// https://www.khronos.org/registry/OpenGL-Refpages/gl4/html/unpackUnorm.xhtml
uint packUint4x8(vec4 v) {
  uint x = 0u;
  v = saturate(v) * 255.0;
  x |= uint(v.x);
  x |= uint(v.y) << 8;
  x |= uint(v.z) << 16;
  x |= uint(v.w) << 24;
  return x;
}

//
// Various 2D and 3D SDF modelling functions from
// https://iquilezles.org/articles/distfunctions
// https://iquilezles.org/articles/distfunctions2d
// Code and examples at https://www.shadertoy.com/view/Xds3zN
//
// The MIT License
