float sampleHandwriting(uvec2 uvp) {
  uint index = uvp.x / 8u + uvp.y * 16u;
  uint mask = handwritingSDF[index];
  return float((mask >> ((uvp.x & 0x7u) * 4u)) & 0xFu) * (1.0 / 15.0);
}

float handwritingTexture(vec2 uv) {
  const uvec2 tt = uvec2(1u, 0u);
  const vec2 size = vec2(127.0, 15.0);
  const vec2 texelSize = vec2(1.0) / vec2(128.0, 16.0);
  vec2 f = fract(uv * size);
  uvec2 uvp = uvec2(abs(mod(uv, 1.0)) * size);

  // bilinear filtering
  float tl = sampleHandwriting(uvp);
  float tr = sampleHandwriting(uvp + tt.xy);
  float bl = sampleHandwriting(uvp + tt.yx);
  float br = sampleHandwriting(uvp + tt.xx);
  return mix(mix(tl, tr, f.x), mix(bl, br, f.x), f.y);
}

