// Generates the terrain heightmap on the first frame and caches it. The
// heightmap itself is just a combination of different noises. Note the usage of
// hybrid multi-fractal noise from Musgrave, "Procedural Fractal
// Terrains". You can get the paper from here:
// https://www.classes.cs.uchicago.edu/archive/2015/fall/23700-1/final-project/MusgraveTerrain00.pdf
//
// Also noteworthy is the slight domain distortion of the input UVs. You can
// find more info about that at https://iquilezles.org/articles/warp

// Because the alpha channel of this is otherwise unused, we use it to store
// and update some persistent state with keyboard inputs.
//

#define KEY_1 49
#define KEY_2 50
#define KEY_3 51

bool isKeyPressed(int key) {
  return texelFetch(iChannel1, ivec2(key, 1), 0).x != 0.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = fragCoord / vec2(textureSize(iChannel0, 0));

  // The code below deals with the persistent state (called global_State in the main code).
  // The state consists of 4x uint8's packed in a uint and converted to 0..1 floats on read.
  // The fields are described in the Buffer C shader.
  vec4 pixel = texelFetch(iChannel0, ivec2(fragCoord), 0);

  if (all(equal(ivec2(fragCoord), ivec2(0)))) {
    vec4 state = unpackUint4x8(floatBitsToUint(pixel.a));
    if (iFrame == 0 || state.z < 0.1 || state.z > 0.7) {
      state = vec4(0.4, 0.0, 0.4, 0.0);
    }

    state.xy = saturate(mix(state.xy, state.zw, 0.05));

    if (isKeyPressed(KEY_1)) {
      state.zw = vec2(0.15, 0.0);
    }

    if (isKeyPressed(KEY_2)) {
      state.zw = vec2(0.45, 0.1);
    }

    if (isKeyPressed(KEY_3)) {
      state.zw = vec2(0.7, 1.0);
    }

    pixel.a = uintBitsToFloat(packUint4x8(state));
    fragColor = vec4(0.0, 0.0, 0.0, pixel.a);
    return;
  }

  // Hack to get the terrain buffer to redraw when the screen is resized.
  if (dot(iResolution.xy, iResolution.xy) == pixel.a) {
    fragColor = pixel;
    return;
  }

  // Terrain generation routine
  // Several hybrid multi-fractal noises combined with some FBMs of Simplex noise
  // and some Worley noise for good measure.
  // I haven't tweaked this a lot but the initial results came out ok so eh.
  uv += 14.0;

  float h = 0.475;
  uv.x += fbm(uv * 0.5, 5u) * 0.03;
  uv.y += fbm(uv * 0.5 + vec2(117.0, 19.0), 5u) * 0.02;

  h += hmf(uv * 0.9, 5u) * 0.25;
  h *= clamp(hmf(uv * 1.1 + vec2(81.0, 63.0), 5u) - 0.5, 0.0, 1.0);
  h -= (fbm(uv * 3.0, 4u) + 0.15) * 0.1;
  h -= worley(uv * 4.0) * 0.2;
  h += (1.0 - abs(fbm(uv * 8.0, 5u))) * 0.05;
  h += fbm(uv * 48.0, 2u) * 0.01;

  h = h * pow(abs(h), 0.11);

  // free gradients, hooray
  vec2 grad = vec2(dFdx(h), dFdy(h));

  fragColor = vec4(h, grad, dot(iResolution.xy, iResolution.xy));
}

