// Here we generate the 3D texture used for the clouds. The output buffer is
// unfortunately 2D so we slice it up and pick the correct slice during
// sampling. This is not ideal as we lose on some nice free filtering. In a
// production renderer the output buffer here would be a 3D texture.

// The generation consists of combining several FBMs of Simplex (or
// Perlin) and Worley noises and is described by Haggstrom 2018, "Real-time
// rendering of volumetric clouds". You can get the paper from
// http://www.diva-portal.org/smash/get/diva2:1223894/FULLTEXT01.pdf

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 resolution = min(iResolution.xy, vec2(1024.0));

  float sliceSize = iResolution.x < 500.0 ? 32.0 : 128.0;
  float numSlices = floor(min(resolution.x, resolution.y) / sliceSize);
  float totalSlices = numSlices * numSlices;

  vec2 curSlice = floor(fragCoord / sliceSize);
  if (curSlice.x > numSlices || curSlice.y > numSlices) {
    return;
  }

  vec2 curOffset = fract(fragCoord / sliceSize) * sliceSize;
  curOffset.x -= iTime * 1.5;
  curOffset.y += sin(iTime * 0.1) * 2.0;

  float curZ = (curSlice.x + curSlice.y * numSlices) / totalSlices;

  vec3 p = vec3(curOffset, curZ * sliceSize * 0.25);
  if (iResolution.x < 500.0) {
    p *= 4.0;
  }

  float s = (fbm(p * 0.025, 4u) + 1.0);
  float w = 1.0 - fbm_worley(p * 0.025, 4u);

  float n1 = saturate((s + w) * 0.5);
  float n2 = saturate(1.0 - fbm_worley(p * 0.1, 3u) + 0.5);
  float n3 = saturate(1.0 - fbm_worley(p * 0.2, 3u) + 0.5);
  float n4 = saturate(1.0 - fbm_worley(p * 0.4, 3u) + 0.5);
  fragColor = vec4(n1, n2, n3, n4);
}

