float noise(vec3 p) //Thx to Las^Mercury
{
	vec3 i = floor(p);
	vec4 a = dot(i, vec3(1., 57., 21.)) + vec4(0., 57., 21., 78.);
	vec3 f = cos((p-i)*acos(-1.))*(-.5)+.5;
	a = mix(sin(cos(a)*a),sin(cos(1.+a)*(1.+a)), f.x);
	a.xy = mix(a.xz, a.yw, f.y);
	return mix(a.x, a.y, f.z);
}

float sphere(vec3 p, vec4 spr)
{
	return length(spr.xyz-p) - spr.w;
}

float flame(vec3 p)
{
	float d = sphere(p*vec3(0.1,.5,1.), vec4(.4,-12.,.0,13.));
	return d + (noise(p+vec3(.0,iTime*9.,.2)) + noise(p*0.)*.1)*.25*(p.y) ;
}

float scene(vec3 p)
{
	return min(400.-length(p) , abs(flame(p)) );
}

vec4 raymarch(vec3 org, vec3 dir)
{
	float d = 0.0, glow = 0.0, eps = 0.03;
	vec3  p = org;
	bool glowed = false;

	for(int i=0; i<64; i++)
	{
		d = scene(p) + eps;
		p += d * dir;
		if( d>eps )
		{
			if(flame(p) < .0)
				glowed=true;
			if(glowed)
       			glow = float(i)/64.;
		}
	}
	return vec4(p,glow);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 v = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
	v.x *= iResolution.x/iResolution.y;

	vec3 org = vec3(0., -2., 4.);
	vec3 dir = normalize(vec3(v.x*1.6, -v.y, -1.5));

	vec4 p = raymarch(org, dir);
	float glow = p.w;

	vec4 col = mix(vec4(1.,.5,.1,1.), vec4(0.1,.5,1.,1.), p.y*.02+.4);

	fragColor = mix(vec4(0.), col, pow(glow*2.,4.));
	//fragColor = mix(vec4(1.), mix(vec4(1.,.5,.1,1.),vec4(0.1,.5,1.,1.),p.y*.02+.4), pow(glow*2.,4.));

}

// Protean clouds by nimitz (twitter: @stormoid)
// https://www.shadertoy.com/view/3l23Rh
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License
// Contact the author for other licensing options

/*
	Technical details:

	The main volume noise is generated from a deformed periodic grid, which can produce
	a large range of noise-like patterns at very cheap evalutation cost. Allowing for multiple
	fetches of volume gradient computation for improved lighting.

	To further accelerate marching, since the volume is smooth, more than half the the density
	information isn't used to rendering or shading but only as an underlying volume	distance to 
	determine dynamic step size, by carefully selecting an equation	(polynomial for speed) to 
	step as a function of overall density (not necessarialy rendered) the visual results can be 
	the	same as a naive implementation with ~40% increase in rendering performance.

	Since the dynamic marching step size is even less uniform due to steps not being rendered at all
	the fog is evaluated as the difference of the fog integral at each rendered step.

*/

mat2 rot(in float a){float c = cos(a), s = sin(a);return mat2(c,s,-s,c);}
const mat3 m3 = mat3(0.33338, 0.56034, -0.71817, -0.87887, 0.32651, -0.15323, 0.15162, 0.69596, 0.61339)*1.93;
float mag2(vec2 p){return dot(p,p);}
float linstep(in float mn, in float mx, in float x){ return clamp((x - mn)/(mx - mn), 0., 1.); }
float prm1 = 0.;
vec2 bsMo = vec2(0);

vec2 disp(float t){ return vec2(sin(t*0.22)*1., cos(t*0.175)*1.)*2.; }

vec2 map(vec3 p)
{
    vec3 p2 = p;
    p2.xy -= disp(p.z).xy;
    p.xy *= rot(sin(p.z+iTime)*(0.1 + prm1*0.05) + iTime*1.01);
    float cl = mag2(p2.xy);
    float d = 0.;
    p *= .61;
    float z = 1.;
    float trk = 1.;
    float dspAmp = 0.1 + prm1*0.2;
    for(int i = 0; i < 5; i++)
    {
		p += sin(p.zxy*0.75*trk + iTime*trk*.8)*dspAmp;
        d -= abs(dot(cos(p), sin(p.yzx))*z);
        z *= 0.57;
        trk *= 1.4;
        p = p*m3;
    }
    d = abs(d + prm1*3.)+ prm1*.3 - 2.5 + bsMo.y;
    return vec2(d + cl*.2 + 0.25, cl);
}

vec4 render( in vec3 ro, in vec3 rd, float time )
{
	vec4 rez = vec4(0);
    const float ldst = 1.;
	vec3 lpos = vec3(disp(time + ldst)*0.5, time + ldst);
	float t = 1.5;
	float fogT = 0.;
	for(int i=0; i<130; i++)
	{
		if(rez.a > 0.99)break;

		vec3 pos = ro + t*rd;
        vec2 mpv = map(pos);
		float den = clamp(mpv.x-0.3,0.,1.)*3.12;
		float dn = clamp((mpv.x + 2.),0.,3.);

		vec4 col = vec4(0);
        if (mpv.x > 0.6)
        {

            col = vec4(sin(vec3(5.,0.4,0.2) + mpv.y*0.1 +sin(pos.z*0.4)*0.5 + 1.8)*0.9 + 0.5,0.08);
            col *= den*den*den;
			col.rgb *= linstep(4.,-2.5, mpv.x)*2.3;
            float dif =  clamp((den - map(pos+.8).x)/9., 0.001, 1. );
            dif += clamp((den - map(pos+.35).x)/2.5, 0.001, 1. );
            col.xyz *= den*(vec3(0.005,.045,.075) + 1.5*vec3(0.033,0.07,0.03)*dif);
        }

		float fogC = exp(t*0.2 - 2.2);
		col.rgba += vec4(0.06,0.11,0.11, 0.1)*clamp(fogC-fogT, 0., 1.);
		fogT = fogC;
		rez = rez + col*(1. - rez.a);
		t += clamp(0.5 - dn*dn*.05, 0.09, 0.3);
	}
	return clamp(rez, 0.0, 1.0);
}

float getsat(vec3 c)
{
    float mi = min(min(c.x, c.y), c.z);
    float ma = max(max(c.x, c.y), c.z);
    return (ma - mi)/(ma+ 1e-7);
}

//from my "Will it blend" shader (https://www.shadertoy.com/view/lsdGzN)
vec3 iLerp(in vec3 a, in vec3 b, in float x)
{
    vec3 ic = mix(a, b, x) + vec3(1e-6,0.,0.);
    float sd = abs(getsat(ic) - mix(getsat(a), getsat(b), x));
    vec3 dir = normalize(vec3(2.*ic.x - ic.y - ic.z, 2.*ic.y - ic.x - ic.z, 2.*ic.z - ic.y - ic.x));
    float lgt = dot(vec3(1.0), ic);
    float ff = dot(dir, normalize(ic));
    ic += 1.5*dir*sd*ff*lgt;
    return clamp(ic,0.,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
	vec2 q = fragCoord.xy/iResolution.xy;
    vec2 p = (gl_FragCoord.xy - 0.5*iResolution.xy)/iResolution.y;
    bsMo = (iMouse.xy - 0.5*iResolution.xy)/iResolution.y;

    float time = iTime*3.;
    vec3 ro = vec3(0,0,time);

    ro += vec3(sin(iTime)*0.5,sin(iTime*1.)*0.,0);

    float dspAmp = .85;
    ro.xy += disp(ro.z)*dspAmp;
    float tgtDst = 3.5;

    vec3 target = normalize(ro - vec3(disp(time + tgtDst)*dspAmp, time + tgtDst));
    ro.x -= bsMo.x*2.;
    vec3 rightdir = normalize(cross(target, vec3(0,1,0)));
    vec3 updir = normalize(cross(rightdir, target));
    rightdir = normalize(cross(updir, target));
	vec3 rd=normalize((p.x*rightdir + p.y*updir)*1. - target);
    rd.xy *= rot(-disp(time + 3.5).x*0.2 + bsMo.x);
    prm1 = smoothstep(-0.4, 0.4,sin(iTime*0.3));
	vec4 scn = render(ro, rd, time);

    vec3 col = scn.rgb;
    col = iLerp(col.bgr, col.rgb, clamp(1.-prm1,0.05,1.));

    col = pow(col, vec3(.55,0.65,0.6))*vec3(1.,.97,.9);

    col *= pow( 16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y), 0.12)*0.7+0.3; //Vign

	fragColor = vec4( col, 1.0 );
}

/**
	Gyroid Tunnel Thing
	Just some playing with the base
	of my asteroid shader 
	https://www.shadertoy.com/view/WtfyDX

	also wanted a break from hexagon truchets
*/

#define MAX_DIST 	100.
#define PI  		3.1415926
#define R 			iResolution
#define T 			iTime
#define S			smoothstep

#define r2(a)  mat2(cos(a), sin(a), -sin(a), cos(a))
#define hash(a, b) fract(sin(a*1.2664745 + b*.9560333 + 3.) * 14958.5453)

// gyroid function
float sdGry(vec3 p, float s, float t, float b) {
    p *=s;
   	float g = abs(dot(sin(p), cos(p.zxy))-b)/(s)-t;
    return g;
}

//global vars cause its just a demo
float g1,g2,g3,g4,g5;
vec3 hitPoint;
vec2 map(vec3 p) {
    vec2 res=vec2(1000.,1.);
    p.xy*=r2(T*.009);
    p +=vec3(-.05,-.2,iTime*1.4);
    // sdGry(p, thickness, scale, offset) / sdf focus 
   // p = twist(p);
	g1 =  sdGry(p, 8.,  .025,  1.05);
    g2 = sdGry(p, 24., .025, .75);
    g3 = sdGry(p, 54., .01, .25);
    g1 -= (g2 *.1);

    hitPoint =p;
    res.x = g1/1.75;
    return res;
}

vec3 get_normal(in vec3 p, in float t) {
    t *= 0.001;
	vec2 eps = vec2(t, 0.0);
	vec3 n = vec3(
	    map(p+eps.xyy).x - map(p-eps.xyy).x,
	    map(p+eps.yxy).x - map(p-eps.yxy).x,
	    map(p+eps.yyx).x - map(p-eps.yyx).x);
	return normalize(n);
}

vec2 ray_march( in vec3 ro, in vec3 rd, int x) {
    float t = 0.0001;
    float m = 0.;
    for( int i=0; i<x; i++ ) {
        vec2 d = map(ro + rd * t);
        m = d.y;
        if(d.x<.0001*t||t>MAX_DIST) break;
        t += d.x*.6;
    }
    return vec2(t,m);
}

float get_diff(in vec3 p, in vec3 lpos, in vec3 n) {
    vec3 l = normalize(lpos-p);
    float dif = clamp(dot(n,l),0. , 1.),
          shadow = ray_march(p + n * .0001 * 2., l,128).x;
    if(shadow < length(lpos-p)) dif *= .4;
    return dif;
}

// Tri-Planar blending function. Ryan Geiss
// https://developer.nvidia.com/gpugems/GPUGems3/gpugems3_ch01.html
vec3 tex3D(sampler2D t, in vec3 p, in vec3 n ){
    n = max(abs(n), 0.001);
    n /= dot(n, vec3(1));
	vec3 tx = texture(t, p.yz).xyz,
         ty = texture(t, p.zx).xyz,
         tz = texture(t, p.xy).xyz;
    return (tx*tx*n.x + ty*ty*n.y + tz*tz*n.z);
}

vec3 get_hue(float qp) {
   return (.9 + .45*cos(qp + vec3(5, 5, .5)));
}

vec3 tone1 = (.5 + .45*cos(3.72 + vec3(2, 1, .5)));
vec3 tone2 = (.5 + .45*cos(12.5 + vec3(2, 1, .5)));

vec3 get_color(vec3 p, vec3 n) {
    vec3 col = vec3(0.);
    vec3 dif = tone1 * tex3D(iChannel1,hitPoint*9.,n).g; 
	float bbd = abs(abs(g3-.005)-.0025); //b2m
    bbd = abs(bbd-.015);
    float cks1 = S(.01,.011,bbd)*.45;
    float cks2 = S(-.02,-.05,g2)*2.;

    vec3 mate = 4.5 + 1.45*cos(hitPoint.z*4.25 + vec3(4, 1, 1.5));
    col += mate*cks1+cks2;   
    return col + dif;
}

vec3 fog (in float t, in float d, in vec3 c) {
    return mix( c, tone2, 1.-exp(-d*t*t*t));
}

vec3 r( in vec3 ro, in vec3 rd, in vec2 uv) {
    vec3 c = vec3(0.);
    vec2 ray = ray_march(ro, rd, 128);

    float t = ray.x;
    float m = ray.y;
    if(t<MAX_DIST) {
		vec3 p = ro + t * rd,
             n = get_normal(p, t);
        float diff = n.x*.5+.5;
        if(ray.y == 1.){
         	c += diff * get_color(p, n);  
        }

        vec2 ref;
        vec3 rr=reflect(rd,n),
             fc=vec3(0.);

        c = fog(t,.07,c);
    } 
    return c;
}

// ACES tone mapping from HDR to LDR
// https://knarkowicz.wordpress.com/2016/01/06/aces-filmic-tone-mapping-curve/
vec3 ACESFilm(vec3 x) {
    float a = 2.51,
          b = 0.03,
          c = 2.43,
          d = 0.59,
          e = 0.14;
    return clamp((x*(a*x + b)) / (x*(c*x + d) + e), 0.0, 1.0);
}

void mainImage( out vec4 O, in vec2 F ) {
    vec2 U = (2.*F.xy-R.xy)/max(R.x,R.y);

    vec3 ro = vec3(0.,.0,-.75),
         lp = vec3(0.,.0,0.);

    vec3 cf = normalize(lp-ro),
     	 cp = vec3(0.,1.,0.),
     	 cr = normalize(cross(cp, cf)),
     	 cu = normalize(cross(cf, cr)),
     	 c = ro + cf * 1.,
     	 i = c + U.x * cr + U.y * cu,
     	 rd = i-ro;

    vec3 C = r(ro, rd, U);
    C = ACESFilm(C);
    O = vec4(pow(C, vec3(0.4545)),1.0);
}


void mainVR( out vec4 O, in vec2 F, in vec3 fragRayOri, in vec3 fragRayDir ) {
    /** normalizing center coords */
  	vec2 U = (2.*F.xy-R.xy)/
        max(R.x,R.y);

    vec3 ro = fragRayOri;
    vec3 rd = fragRayDir;

    vec3 C = r(ro, rd, U);
    C = ACESFilm(C);
    O = vec4(pow(C, vec3(0.4545)),1.0);

}

//
// APOLLONIAN NET FRACTAL
//
// shader inspiration and help
// https://www.shadertoy.com/view/llG3Dt @Gijs
// https://www.shadertoy.com/view/Xtlyzl @GregRostami / @Fabrice

#define MAX_DIST 	15.0
#define MIN_DIST 	.001
#define MAX_STEPS 	158

#define PI  		3.1415926
#define PI2 		6.2831853

#define r2(a)  mat2(cos(a), sin(a), -sin(a), cos(a))
#define kf(a,b) a*(1.2/b)
#define pfract(a) -1.+2.*fract(.5*a+.5)

// Antialiasing "module" by FabriceNeyret2 
// https://www.shadertoy.com/view/WlfyW8
// set at bottom AA = 1 is off

vec3 get_mouse( vec3 ro ) {
    float x = iMouse.xy==vec2(0) ? -1. :
    	(iMouse.y / iResolution.y * 1. - .5) * PI;
    float y = iMouse.xy==vec2(0) ? .5 :
    	-(iMouse.x / iResolution.x * 1. - .5) * PI;
    ro.zy *= r2(x);
    ro.zx *= r2(y);
    return ro;
}


vec4 orb = vec4(0.0); 

vec2 apollo (vec3 p) {
	float scale = 1.;
	orb = vec4(0.0);
	for( int i=0; i<4;i++ ) {
		p = pfract(p);
		float r2 = dot(p,p);  
        orb = min( orb, vec4(abs(p),r2) );
		p = kf(p,r2);
        scale = kf(scale,r2);
	}

    float tubes = length(mod(p.xz+.5,1.)-.5)-.025;
    tubes =   min(length(mod(p.xy+.5,1.)-.5)-.025,tubes);
    tubes =   min(length(mod(p.zy+.5,1.)-.5)-.025,tubes);
    float d = (tubes)/scale;

	return vec2(d*.65,scale);
}

vec2 map (in vec3 p) {
    p.x += .5 - iTime *.02;
    p.y+=1.25;
    vec2 d = apollo(p);
	return d;
}

//@iq calNormal using distance - fixes issues
vec3 get_normal(in vec3 pos, in float t ){
    float precis = 0.0001 * t * 0.57;
    vec2 e = vec2(1.0,-1.0)*precis;
    return normalize( e.xyy*map( pos + e.xyy ).x + 
					  e.yyx*map( pos + e.yyx ).x + 
					  e.yxy*map( pos + e.yxy ).x + 
                      e.xxx*map( pos + e.xxx ).x );
}

vec2 ray_march( in vec3 ro, in vec3 rd ) {
    float depth = 0.0;
    float m = -1.;
    for (int i = 0; i<MAX_STEPS;i++)
    {
        vec3 pos = ro + depth * rd;
        vec2 dist = map(pos);
        m = dist.y;
        if(abs(dist.x)<MIN_DIST*depth) break;
        depth += dist.x*.7;
        if(depth>MAX_DIST) break;
    } 
    return vec2(depth,m);
}

float get_diff(vec3 p, vec3 lpos, float t) {
    vec3 l = normalize(lpos-p);
    vec3 n = get_normal(p, t);
    float dif = clamp(dot(n,l),0. , 1.);

    vec2 shadow = ray_march(p + n * MIN_DIST * 2., l);
    if(shadow.x < length(p -  lpos)) {
        dif *= .1;
    }
    return dif;
}

//@iq/@shane
float get_ao(vec3 p, vec3 n){
    float r = 0., w = 1., d;
    for (float i=1.; i<5.+1.1; i++){
        d = i/5.;
        r += w*(d - map(p + n*d).x);
        w *= .5;
    }
    return 1.-clamp(r,.0,1.);
}

vec3 hsv2rgb( in vec3 c ) {
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
	return c.z * mix( vec3(6.0), rgb, c.y);
}

vec3 render( in vec3 ro, in vec3 rd, in vec2 uv) {
    vec3 color = vec3(0.);
    vec3 fadeColor = vec3(0.5);
    vec2 ray = ray_march(ro, rd);

    if(ray.x<MAX_DIST) {
        vec3 p = ro + ray.x * rd;
        vec3 n = get_normal(p, ray.x);

        vec3 lpos1 = vec3(-1.05, -.03, 1.1);
        vec3 lpos2 = vec3(.05, .01, -.5);

        vec3 tint = hsv2rgb(vec3(ray.y*.006,1.,.5));

        vec3 diff = vec3(.7)*get_diff(p, lpos1, ray.x);
             diff+= vec3(.7)*get_diff(p, lpos2, ray.x);
  		float ao = get_ao(p,n);

        color += tint * diff * ao;

    } else {
    	color = fadeColor;   
    }

    color = mix( color, fadeColor, 1.-exp(-1.0015*ray.x*ray.x*ray.x));
    return color;
}

vec3 ray( in vec3 ro, in vec3 lp, in vec2 uv ) {
    vec3 cf = normalize(lp-ro);
    vec3 cp = vec3(4.,1.,0.);
    vec3 cr = normalize(cross(cp, cf));
    vec3 cu = normalize(cross(cf, cr));
    vec3 c = ro + cf * 6.85;

    vec3 i = c + uv.x * cr + uv.y * cu;
    return i-ro; 
}

