float map(vec3 p){
    p=mod(p,2.)-1.;
    p = abs(p)-1.;
    if (p.x < p.z) p.xz = p.zx;
    if (p.y < p.z) p.yz = p.zy;
    if (p.x < p.y) p.xy = p.yx;
    float s=1.;
    for(int i=0;i++<10;)
    {
      float r2=2./clamp(dot(p,p),.1,1.);
      p=abs(p)*r2-vec3(.6,.6,1.5);
      s*=r2;
    }
    return length(p)/s;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
