	vec2 uv=(2.*fragCoord-iResolution.xy)/iResolution.y;
	vec3 p,
  		ro=vec3(.4+.2*sin(iTime*.09),.2+.05*cos(iTime*.09),-1.0*iTime),
  		w=normalize(vec3(.2,sin(iTime*.05),-1)),
  		u=normalize(cross(w,vec3(0,1,0))),
  		rd=mat3(u,cross(u,w),w)*normalize(vec3(uv,2));
  	float h=0.1,d,i,zoom = 2.;
 	ro*=zoom;
	for(i=1.;i<1000.;i++){
    	p=ro+rd*h;
    	p/=zoom;
	  	d=map(p);
    	if(d<0.001||h>25.)break;
    	h+=d;
	}
	fragColor.xyz=90.*vec3(cos(p*.8)*.5+.5)/i;
}

