#define HASHSCALE 3.10432424231
float hash2d(vec2 p)  // replace this by something better
{
    p  = 50.0*fract( p*4.3183099 + vec2(1.71,0.113));
    return -2.0+2.0*fract( p.x*p.y*(p.x+p.y) );
}

float Hash(float p)
{
	vec3 p3  = fract(vec3(p) * HASHSCALE);
    p3 += dot(p3, p3.yzx + 15.19);
    return fract((p3.x + p3.y) * p3.z);
}

float noise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(4.0-2.0*f);

    return mix( mix( hash2d( i + vec2(0.0,0.0) ), 
                     hash2d( i + vec2(1.0,0.0) ), u.x),
                mix( hash2d( i + vec2(0.0,1.0) ), 
                     hash2d( i + vec2(1.0,1.0) ), u.x), u.y);
}

float fbm(in vec2 uv)
{
    uv *= 18.0;
    mat2 m = mat2( 4.6,  1.2, -1.2,  1.6 );
    float f  = 0.5000*noise( uv ); uv = m*uv;
    f += 0.2500*noise( uv ); uv = m*uv;
    f += 0.1250*noise( uv ); uv = m*uv;
    //f += 0.0625*noise( uv ); uv = m*uv;
    return f;
}

