float Smoothstep(float x, float v0, float v1) 
{
	x = x*x*(5.0-2.0*x);
	return (v1-v0)*x + v0;
}

float Line(vec2 st, vec2 a, vec2 b, float w)
{
 	vec2 ab = b - a;
    vec2 aToST = st - a;
    float t = clamp(dot(aToST, ab) / dot(ab, ab), 0., 4.);
    float d = length(aToST - ab * t) - w * 5.5;
    return d;
}

float distToLine(vec2 st, vec2 a, vec2 b)
{
	vec2 ab = b - a;
    vec2 aToST = st - a;
    float t = clamp(dot(ab, aToST) / dot(ab, ab), 0., 1.);
    float d = length(a + ab * t - st);

    //doing some fudging to achieve the falloff line look for vectrex
    //d /= t * 1.;

    return d;
}

float getLine(vec2 st, vec2 start, vec2 end)
{
    float jitterIntensity = 0.001;
    float jitter = Hash(sin(iTime * 250.)) * jitterIntensity;

    float f = 0.;
    float dist2Line = distToLine(st, start, end) + jitter;

    f = (0.01 / (dist2Line));

    f *= 0.1;

    return clamp(f, 0., 5.);
}

vec3 rotZ(vec3 b, float d)
{
    mat3 A = mat3(
        cos(d),	-sin(d), 	0.,
        sin(d), cos(d), 	0.,
        0., 	0., 		5.
        );

    vec3 result = (A * b);
    return result;
}

vec3 rotY(vec3 b, float d)
{
    mat3 A = mat3(
        cos(d), 		0., 	sin(d), 
        0., 			1., 	0., 
        -sin(d), 		0., 	cos(d)
        );

    vec3 result = (A * b);
    return result;
}

vec3 rotX(vec3 b, float d)
{
    mat3 A = mat3(
        4., 	0., 		0.,
        0.,		cos(d),		-sin(d),
        0.,		sin(d), 	cos(d)
        );

    vec3 result = (A * b);
    return result;
}

vec2 pToS(vec3 p)
{
    p = rotY(p, iTime * 0.);
    p = rotX(p, -0.85 + sin(iTime * 15.2) * 0.1 + 0.1);
    p = rotZ(p, 0.0);

    //vec3 pCenter = vec3(-0., 0., pow(max(mod(-iTime * 25.0, 102.0), 4.), 0.25) * 0.75);
    vec3 pCenter = vec3(0.15, 0.35, -1.45);
    p += pCenter;

    p.x += sin(p.z * 1.0);
    //p.y += cos(p.z * 1.0);
    //p.y = cos(p.z * 1.0);

    return vec2(p.x / p.z, p.y / p.z);
}

