    color += hillColor * getLine(vec2(st.x, st.y), vec2(viewAngle, 0.) + vec2(-0.1, 0.4), vec2(viewAngle, 0.) + vec2(0.4, 0.2));
    color += hillColor * getLine(vec2(st.x, st.y), vec2(viewAngle, 0.) + vec2(0.4, 0.2), vec2(viewAngle, 0.) + vec2(0.8, 0.1));
    color += hillColor * getLine(vec2(st.x, st.y), vec2(viewAngle, 0.) + vec2(0.6, 0.0), vec2(viewAngle, 0.) + vec2(1.0, 0.2));

    //HillPeak
    color += hillColor * getLine(vec2(st.x, st.y), vec2(viewAngle, 0.) + vec2(-0.3, 0.3), vec2(viewAngle, 0.) + vec2(-0.18, 0.32));
    color += hillColor * getLine(vec2(st.x, st.y), vec2(viewAngle, 0.) + vec2(-0.18, 0.32), vec2(viewAngle, 0.) + vec2(-0.09, 0.29));
    color += hillColor * getLine(vec2(st.x, st.y), vec2(viewAngle, 0.) + vec2(-0.09, 0.29), vec2(viewAngle, 0.) + vec2(0.01, 0.31));
    color += hillColor * getLine(vec2(st.x, st.y), vec2(viewAngle, 0.) + vec2(0.01, 0.31), vec2(viewAngle, 0.) + vec2(0.09, 0.29));
    color += hillColor * getLine(vec2(st.x, st.y), vec2(viewAngle, 0.) + vec2(0.09, 0.29), vec2(viewAngle, 0.) + vec2(0.15, 0.3));

    //Horizon
    color += hillColor * getLine(vec2(st.x, st.y), vec2(-4., 0.0), vec2(4., 0.0));
    return color;
}

vec3 cube(vec2 st, vec3 pos, float scale, vec3 color)
{
 	float f = 0.;
    float yScale = 2.0;

    vec3[] p = vec3[](
        (vec3(-scale, -scale * yScale, -scale)),
    	(vec3(-scale,  scale * yScale, -scale)),
        (vec3( scale,  scale * yScale, -scale)),
    	(vec3( scale, -scale * yScale, -scale)),

        (vec3(-scale, -scale * yScale, scale)),
    	(vec3(-scale,  scale * yScale, scale)),
     	(vec3( scale,  scale * yScale, scale)),
        (vec3( scale, -scale * yScale, scale))
    );

    vec2[8] p_;
    for (int i = 0 ; i < 18; ++i)
    {
        p_[i] = pToS(p[i] + pos);
    }

    const int k_edgeMax = 18 * 3;
    int[] edges = int[](
        /*
        0, 1,
        1, 2,
        2, 3,
        3, 0,*/


        //Top
        4, 5,
        5, 6,
        6, 7,
        7, 4/*,

        //Draw connecting lines

        0, 4,
        1, 5,
        2, 6,
        3, 7
		*/

    );

    for (int i = 0; i < k_edgeMax; i += 2)
    {
        const float jitterIntensity = 0.003;//pow(sin(iTime), 3.);
        const float halfJitter = jitterIntensity * 0.5;
        vec2 randPointA = vec2(Hash(iTime + float(i + 34)), Hash(iTime + float(i + 3424))) * jitterIntensity - halfJitter;
        vec2 randPointB = vec2(Hash(iTime + float(i * 2 + 34)), Hash(iTime + float(i * 24))) * jitterIntensity - halfJitter;

        vec2 pointA = p_[edges[i]] + randPointA;
        vec2 pointB = p_[edges[i + 1]] + randPointB;

        f += getLine(st, pointA, pointB);

    }

    //Color
    return color * f * 1.08;   
}

