// Noise Normalization by Kristian Sivonen (ruojake)
// CC BY-SA 4.0 (https://creativecommons.org/licenses/by-sa/4.0/)

// The technique is at row 56, rest is your normal noise stuff.
// I came up with this thing via trial and error in desmos
// so it may well be that there's some named, established and
// well defined math in play that I'm simply unaware of.
// If so, feel free to educate me in the comments!

float oct;
float per;

float hash12(vec2 p)
{
	return fract(sin(dot(p, vec2(1.433, 1.512))) * 4313.441);
}

float noise(vec2 p)
{
	vec2 i = floor(p);
    vec2 f = smoothstep(0.,1.,p-i);
    const vec2 o = vec2(1.,0.);

    return mix(mix(hash12(i),hash12(i+o),f.x),
               mix(hash12(i+o.yx),hash12(i+1.),f.x),
               f.y);
}

float fbm(vec2 p)
{
    float res = 0.;
    float a = 1.;
    for(float i = 1.; i <= oct; i += 1.)
    {
    	res += noise(p) * a;
        a *= per;
        p *= 2.;
        p += 100.;
    }
    return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;

    oct = floor(fract(iTime / 48.) * 6. + 1.);
	per = fract(iTime * .125) * .9 + .05;

    float comp = (1.-per)/(1.-pow(per,oct));
    float n = fbm(uv * 10. - vec2(iTime * .1, 0.)) * comp;

	// here's the interesting bit
    if (uv.x > 0.)
    {
    	n = clamp(
            (1. + per * log(oct))
            * (n - .5) + .5,
            0., 1.);
        // clamping is kinda optional here 
        // but the value does occasionally cross beyond 0-1 range
    }

    vec3 col = vec3(n);

    if (uv.y < 0.)
    {
    	col = mix(vec3(1,0,0),vec3(0,0,1), n);
    }

    fragColor = vec4(col,1.0);
}

