#define S(a , b , t ) smoothstep( a , b , t )
#define sat(x) clamp( x, 0.0f , 1.0f )


// -------------------------------------------------
// 리맵01
// -------------------------------------------------
float remap01 ( float a , float b , float v ) {
	// 이게 단순히 비율을 구하는 것 뿐만이 아니라,
    // 로컬 좌표를 , ( 0 ~ 1)의 월드 UV로 사상시키는 의미도 담고 있다.
    return sat( ( v - a ) / ( b - a ) );
}

// -------------------------------------------------
// 리맵
// -------------------------------------------------
float remap( float a , float b , float c , float d , float value ) {
    //return (value - a) / ( b - a ) * ( d - c ) + c;
    return remap01( a , b , value ) * ( d - c ) + c;
}

// -------------------------------------------------
// within함수 ( 리맵 01 함수의 vec2 버젼이다. )
// -------------------------------------------------
vec2 within( vec2 uv , vec4 rect ) {
	// remap01과 마찬가지로, UV를 사상 시키는 의미를 담고 있다.
    return ( uv - rect.xy ) / ( rect.zw - rect.xy );   
}



// -------------------------------------------------
// 눈
// -------------------------------------------------
vec4 Eye( vec2 uv ) {
    // 이미 uv가 넘어 올때, 어떤 구역 [ ~ ] 안에서 사상되어 있는 상태이다.
    // within 함수에서 이미 계산되어 있는 상태이다.
    // 즉 자기 자신의 중점을 ( 0 , 0 )으로 생각하고 자기 자신만 계산하면 된다.

	uv -= 0.5f;
    float d = length(uv);


    vec4 irisCol = vec4( 0.3f, 0.5f, 1.0f , 1.0f );

    // 눈의 전체 동그라미
    vec4 col = mix( vec4(1.0f) , irisCol , S( 0.1f , 0.7f , d ) * 0.5f );

    // 눈동자 전체의 감싸는 동그라미
    // 뒷쪽의 sat는 그림자까지 표시해주기 위한 테크닉이다.
    col.rgb *= 1.0f - S(0.45, 0.5f , d ) * 0.5f * sat ( -uv.y - uv.x ); // 중점에서 0.5이상 떨어진 곳만 둥글게 만든다.


    // 눈동자 검은색 부분
    col.rgb = mix( col.rgb , vec3(0.0f), S(0.3f , 0.28f, d ) );

    // 눈동자 파란색 부분
    irisCol.rgb *= 1.0f + S(0.3f, 0.05f, d );
    col.rgb = mix( col.rgb , irisCol.rgb , S( 0.28f, 0.25f , d ) );


	// 눈동자 파란색 부분 안의 다시 검은 부분    
    col.rgb = mix( col.rgb ,vec3(0.0f) , S( 0.16f, 0.14f , d ) );


    // eys High Light
    float highLight = S( 0.1f , 0.09f , length( uv -vec2(-0.15f, 0.15f)));
    highLight += S( 0.07f , 0.05f , length( uv + vec2( -0.08, 0.08 )));

    col.rgb = mix( col.rgb, vec3(1.0f), highLight );
    col.a = S( 0.5f , 0.45f , d );


    return col;
}


// -------------------------------------------------
// 입
// -------------------------------------------------
vec4 Mouth( vec2 uv ) {
    uv -= 0.5f;
    vec4 col = vec4( 0.5f, 0.18f, 0.05f, 1.0f );

    uv.y *=1.5f;
    uv.y -= uv.x * uv.x * 2.0f;

    float d = length(uv );
    col.a = S( 0.5f , 0.48f, d );

    float td = length( uv - vec2(0.0f, 0.6f));

    // 이빨에 약간의 다크 셰도우를 준다.
    vec3 toothCol = vec3(1.0f) * S( 0.6f , 0.35f ,d );


    // 거리에 따른 이빨의 색깔 합성.
    col.rgb = mix( col.rgb, toothCol , S(0.4f, 0.37f ,td ) );

    // 혀
    td = length(uv + vec2(0.0f, 0.5f ));
    col.rgb = mix( col.rgb, vec3(1.0f, 0.05f, 0.05f ), S(0.5f, 0.2f,td));


    return col;
}

// -------------------------------------------------
// 머리
// -------------------------------------------------
vec4 Head( vec2 uv ) {
 	vec4 col = vec4(0.9f , 0.65f, 0.1f, 1.0f);

    float d = length(uv);

    col.a = S(0.5f , 0.49f , d );


    // 중심에서 시작해서 끝으로 갈수록 떨어지는 Fall Off
    float edgeShader = remap01( 0.35f , 0.5f , d );
    edgeShader = edgeShader * edgeShader;
    col.rgb *= 1.0f -  edgeShader * 0.5f;


    // 외곽선 테두리 ( 이건 자기가 찾아 놓은 값 대입 한 것 )
    col.rgb = mix( col.rgb, vec3(0.6f, 0.3f, 0.1f), S(0.47f ,0.48f , d) );


    // 하이라이트
    float highlight = S(0.41f, 0.405f, d );
    highlight *= remap( 0.41f , -0.1f , 0.75f , 0.0f , uv.y );
    col.rgb = mix( col.rgb, vec3(1.0f), highlight );

    // 오른쪽 볼 하이라이트
    d = length( uv - vec2(0.25f, -0.2f) );
    float cheek = S(0.2f, 0.01f, d ) * 0.4f;
    cheek *= S(0.17f, 0.16f , d );


    col.rgb = mix( col.rgb, vec3(1.0f,0.1f , 0.1f), cheek );



    return col;
}


// -------------------------------------------------
// 얼굴
// -------------------------------------------------
vec4 Smiley( vec2 uv ) {
    vec4 col = vec4(0);

    // 이게 좌우 대칭을 만들어 준다.
    uv.x = abs( uv.x );

    vec4 head = Head(uv);

    // 여기에서 눈의 uv를 리맵화 한다. 인간이 알아 보기 쉽도록
    vec4 eye = Eye( within( uv, vec4( 0.03, -0.1f , 0.35f , 0.25f) ) );
    vec4 mouth = Mouth( within( uv, vec4( -0.3f, -0.4f, 0.3f, -0.1f) ) );

    // 이거 섞어주는 순서가 중요하다.
    col = mix( col , head , head.a ); // 헤드의 알파값으로 섞는다.
    col = mix ( col , eye , eye.a );
    col = mix ( col, mouth, mouth.a );

    return col;
}


