// A Sierpinski Carpet like structure (2D Cantor). Instead of removing the central square 
// of a 3x3 subdivided square, in this shader I divide the square in 5x5 sub-squares and I 
// remove the four in the corners of the central one.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // unit square covering the whole screen
	vec2 z = fragCoord.xy / iResolution.xy;

    // make it all white
    float f = 1.0;

    // iterate	
	for( int i=0; i<4; i++ ) 
	{
        //remove subsquares (1,1), (3,1), (1,3) and (3,3)
		f *= 1.0 - step( abs(abs(z.x-(0.5+0.36475*sin(iTime+sin(iTime*3./5.))))-0.15*(1.0+sin(iTime+sin(iTime)))), 0.1 )
            *step( abs(abs(z.y-0.5+0.1*sin(iTime+sin(iTime)))-0.1*(1.0+cos(iTime+sin(iTime*7./5.)))), 0.1 );
        // scale the shole thing down by a factor of 5
		z = fract( z*5.0 );
	}

	fragColor = vec4( f, f, f, 1.0 );
}

void mainImage( out vec4 O, vec2 u )
{
    vec2 res = iResolution.xy,
          z = ( u* 2. - res) / min(res.x, res.y);

    float t = iTime;
    float scale = 2.;
    z *= scale;

    vec2 a = vec2(sin(t * 0.5), cos(t * 0.5));
    vec2 b = vec2(1., 0.);
    vec2 c = vec2(0., 1.);
    vec2 d = vec2(1., 0.);
    vec2 e = vec2(sin(t* 0.43), cos(t* 0.43));


    vec2 f = cpow(z, 100) + cmul(a, cpow(z, 4)) + cmul(b, cpow(z, 3))
                     + cmul(c, cpow(z, 2)) + cmul(d, cpow(z, 1)) + e;
    float theta = atan(f.x, f.y);
    float r = length(f);

    float k = .01 * abs(sin(fract(0.1 * t) *  PI))/abs((length(z)- 1.1- fract(0.1 * t)));
    float j = .01 * abs(sin(fract(0.1 * t) *  PI))/abs((length(z)- 1.17- fract(0.1 * t)));


    O = vec4(hsvToRgb(theta, 0.1/r, 0.3/r) + vec3(k + j), 1.);
}

