const float PI  = 3.141592653589793;

vec3 hsvToRgb(float h, float s, float v) {
    // h: -π - +π, s: 0.0 - 1.0, v: 0.0 - 1.0
    h = (h + PI) / (2.* PI) * 360.;

    float c = s; // float c = v * s;
