void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    /*// Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));*/

    ivec2 myCoords = ivec2(fragCoord);

    vec4 p = texelFetch(iChannel0, myCoords, 0);

    vec4 pixels[25];
    for(int i=0; i<25; i++){
    	//pixels[i] = gp(myCoords + offsets[i]);
    	pixels[i] = texelFetch(iChannel0, myCoords + offsets[i], 0);
    }

    // Render particles.
    vec3 color = getColor(pixels);

    // Render GUI.
    if(fragCoord.y >= iResolution.y - MENU_HEIGHT && iResolution.x - fragCoord.x < MENU_WIDTH * MENU_NUM_BUTTONS){
        int fromRight = int((iResolution.x - fragCoord.x) / MENU_WIDTH);
        if(fromRight == 0){
        	color = MENU_ERASER_COLOR;
        }else if(fromRight == 1){
        	color = SAND_COLOR;
        }else if(fromRight == 2){
        	color = WATER_COLOR;
        }else if(fromRight == 3){
        	color = GAS_COLOR;
        }else if(fromRight == 4){
        	color = FIRE_COLOR;
        }else if(fromRight == 5){
        	color = WATER_VAPOR_COLOR;
        }else if(fromRight == 6){
        	color = BOMB_COLOR;
        }else if(fromRight == 7){
        	color = TANK_COLOR;
        }else if(fromRight == 8){
        	color = EXPLOSION_COLOR;
        }else if(fromRight == 9){
        	color = BROKEN_TANK_COLOR;
        }else if(fromRight == 10){
        	color = AIRCRAFT_COLOR;
        }else if(fromRight == 11){
        	color = LAZER_COLOR;
        }else if(fromRight == 12){
        	color = MEGA_BOMB_COLOR;
        }else if(fromRight == 13){
        	color = DIRT_COLOR;
        }else if(fromRight == 14){
        	color = CONCRETE_COLOR;
        }else if(fromRight == 15){
        	color = CONCRETE_BROKEN_COLOR;
        }else if(fromRight == 16){
        	color = NUKE_COLOR;
        }else if(fromRight == 17){
        	color = HOUSE_COLOR;
        }else if(fromRight == 18){
        	color = GRASS_COLOR;
        }
    }
    bool doAutoSpawn = texelFetch(iChannel0, ivec2(AUTO_SPAWN_AIRCRAFT, 0), 0).x > 0.5;
    ivec2 topLeft = ivec2(iResolution.x*0.04, iResolution.y*0.95);
    if(length(vec2(myCoords-topLeft)) < 5.){
        if(doAutoSpawn){
        	color = mix(color, vec3(1.,0.,0.), 0.75);
        }
    }

    //
    vec2 tankPos = texelFetch(iChannel0, ivec2(TANK_POSITION, 0), 0).xy;
    if(myCoords == ivec2(tankPos)){
    	color.r = 1.;
    }
    // Crosshair.
    if(tankPos.x != -1.){
        vec2 tankDir = vec2(myCoords) - tankPos;
        float tankShootAngle = texelFetch(iChannel0, ivec2(TANK_SHOOT_ANGLE, 0), 0).x;
        vec2 aimDir = vec2(
            cos(tankShootAngle),
            sin(tankShootAngle)
        );
        float d = dot(tankDir, aimDir);
        float cosA = d/length(tankDir);
        const float minRange = 30.;
        const float maxRange = 300.;
        const float maxCosA = 0.9998;
        if(d > minRange && d < maxRange && cosA > maxCosA){
            //color.rgb = vec3(1.);
            float forwardFactor = (d-minRange)/(maxRange-minRange);
            forwardFactor *= (1.-forwardFactor);
            float sideFactor = (cosA - maxCosA)/(1.-maxCosA);
            color = mix(color, vec3(1), forwardFactor*sideFactor);
        }
    }
    //color.r = tankShootAngle*0.1;
    //gp(myCoords + offsets[i]);

    //
    if(exist(p)){
        //color += length(p.xy)*.01;
        //color = mix(color, vec3(1., 0.8, 0.5), length(p.xy)*.01);
        float f = 0.02;
        if(getClass(p) == WATER_VAPOR){
            f = 0.002;
        }
        color += vec3(1.0, 0.5, 0.125) * length(p.xy)*f;
    }


    // Output to screen
    fragColor = vec4(color,1.0);
}

